// Test script for Featherless API integration
// Run with: node test-featherless.js

import OpenAI from 'openai';

const openai = new OpenAI({
  baseURL: 'https://api.featherless.ai/v1',
  apiKey: 'rc_0d2c186ee945d2e0a15310e7630233b1b3bd5448fdf0d587ab5dc71cf5994fa3',
});

async function testFeatherlessAPI() {
  console.log('Testing Featherless API with DeepSeek R1...');

  try {
    const chatCompletions = await openai.chat.completions.create({
      model: 'huihui-ai/DeepSeek-R1-Distill-Llama-70B-abliterated',
      max_tokens: 1000,
      messages: [
        { role: 'system', content: 'You are a helpful assistant.' },
        { role: 'user', content: 'What is the fastest way to get to the airport?' }
      ],
    });

    console.log('\n✅ Success! API Response:');
    console.log('-----------------------------------');
    chatCompletions.choices.forEach(choice => {
      console.log(choice.message.content);
    });
    console.log('-----------------------------------\n');

    console.log('API Stats:');
    console.log('- Model:', chatCompletions.model);
    console.log('- Tokens used:', chatCompletions.usage?.total_tokens || 'N/A');
    console.log('- Finish reason:', chatCompletions.choices[0]?.finish_reason);

  } catch (error) {
    console.error('❌ Error testing Featherless API:');
    console.error(error.message);
    if (error.response) {
      console.error('Status:', error.response.status);
      console.error('Data:', error.response.data);
    }
  }
}

// Test tool calling capability
async function testToolCalling() {
  console.log('\n\nTesting tool calling support...');

  try {
    const response = await openai.chat.completions.create({
      model: 'huihui-ai/DeepSeek-R1-Distill-Llama-70B-abliterated',
      max_tokens: 1000,
      messages: [
        { role: 'user', content: 'What is the weather like in San Francisco?' }
      ],
      tools: [
        {
          type: 'function',
          function: {
            name: 'get_weather',
            description: 'Get the current weather in a location',
            parameters: {
              type: 'object',
              properties: {
                location: {
                  type: 'string',
                  description: 'The city and state, e.g. San Francisco, CA'
                }
              },
              required: ['location']
            }
          }
        }
      ],
      tool_choice: 'auto'
    });

    console.log('✅ Tool calling test result:');
    console.log('Tool calls:', response.choices[0]?.message?.tool_calls);

  } catch (error) {
    console.log('⚠️  Tool calling may not be supported:', error.message);
  }
}

// Run tests
testFeatherlessAPI().then(() => testToolCalling());

