   149   */
   150  private convertMessages(messages: Message[]): Anthropic.MessageParam[] {
   151    return messages
   152      .filter(m => m.role !== 'system') // System prompts handled separately
   153      .map(m => ({
   154        role: m.role as 'user' | 'assistant',
   155        content: typeof m.content === 'string'
   156          ? m.content
   157          : m.content.map(block => this.convertContentBlock(block))
   158      }));
   159  }
