   161  /**
   162   * Convert content block to Anthropic format
   163   */
   164  private convertContentBlock(block: ContentBlock): any {
   165    if (block.type === 'text') {
   166      return { type: 'text', text: block.text };
   167    } else if (block.type === 'image') {
   168      return {
   169        type: 'image',
   170        source: block.source
   171      };
   172    } else if (block.type === 'tool_use') {
   173      return {
   174        type: 'tool_use',
   175        id: block.id,
   176        name: block.name,
   177        input: block.input
   178      };
   179    } else if (block.type === 'tool_result') {
   180      return {
   181        type: 'tool_result',
   182        tool_use_id: block.tool_use_id,
   183        content: block.content,
   184        is_error: block.is_error
   185      };
