   187    return block;
   188  }
   189
   190  /**
   191   * Convert Anthropic response to our format
   192   */
   193  private convertResponse(response: Anthropic.Message): LLMResponse {
   194    return {
   195      id: response.id,
   196      model: response.model,
   197      role: 'assistant',
   198      content: response.content.map(block => this.convertContentBlock(block as any)),
   199      stop_reason: response.stop_reason as any,
   200      usage: {
   201        input_tokens: response.usage.input_tokens,
   202        output_tokens: response.usage.output_tokens,
   203        total_tokens: response.usage.input_tokens + response.usage.output_tokens
   204      }
   205    };
   206  }
   207
   208  /**
   209   * Convert Anthropic stream event to our format
   210   */
   211  private convertStreamEvent(event: any): StreamEvent {
   212    if (event.type === 'message_start') {
   213      return {
   214        type: 'message_start',
   215        message: {
   216          id: event.message.id,
   217          model: event.message.model,
   218          role: event.message.role,
   219          usage: event.message.usage
   220        }
   221      };
   222    } else if (event.type === 'content_block_start') {
   223      return {
   224        type: 'content_block_start',
   225        index: event.index,
   226        content_block: event.content_block
   227      };
   228    } else if (event.type === 'content_block_delta') {
   229      return {
   230        type: 'content_block_delta',
   231        index: event.index,
   232        delta: event.delta
   233      };
   234    } else if (event.type === 'content_block_stop') {
   235      return {
   236        type: 'content_block_stop',
   237        index: event.index
   238      };
   239    } else if (event.type === 'message_delta') {
   240      return {
   241        type: 'message_delta',
   242        delta: event.delta,
   243        usage: event.usage
   244      };
   245    } else if (event.type === 'message_stop') {
   246      return {
   247        type: 'message_stop'
   248      };
   249    } else if (event.type === 'error') {
   250      return {
   251        type: 'error',
   252        error: event.error
   253      };
   254    }
   255
   256    // Unknown event type
   257    return {
   258      type: 'error',
   259      error: {
   260        type: 'unknown_event',
   261        message: `Unknown stream event: ${event.type}`
   262      }
   263    };
   264  }
   265}
