     1/**
     2 * MCP Provider - Multi-model delegation via MCP server
     3 *
     4 * Integrates with ~/.claude/multi-model-mcp-server.js
     5 * Provides access to Featherless and GLM models
     6 */
     7
     8import { spawn } from 'child_process';
     9import type {
    10  ILLMProvider,
    11  LLMRequest,
    12  LLMResponse,
    13  StreamHandler,
    14  ProviderCapabilities,
    15  ProviderConfig,
    16  ContentBlock
    17} from '../types';
    18
    19/**
    20 * Model configuration from MCP server
    21 */
    22interface MCPModel {
    23  id: string;
    24  name: string;
    25  capabilities: string[];
    26  cost: string;
    27  speed: string;
    28  quality: string;
    29  vision: boolean;
    30}
    31
    32/**
    33 * MCP models (synced with multi-model-mcp-server.js)
    34 */
    35const MCP_MODELS: Record<string, MCPModel> = {
    36  'dolphin-3': {
    37    id: 'featherless/dphn/Dolphin-Mistral-24B-Venice-Edition',
    38    name: 'Dolphin-3 Venice',
    39    capabilities: ['coding', 'security', 'reverse-engineering', 'unrestricted'],
    40    cost: 'low',
    41    speed: 'fast',
    42    quality: 'high',
    43    vision: false
    44  },
    45  'qwen-72b': {
    46    id: 'featherless/huihui-ai/Qwen2.5-72B-Instruct-abliterated',
    47    name: 'Qwen 2.5 72B',
    48    capabilities: ['reasoning', 'coding', 'writing', 'unrestricted'],
    49    cost: 'medium',
    50    speed: 'medium',
    51    quality: 'exceptional',
    52    vision: false
    53  },
    54  'whiterabbit': {
    55    id: 'featherless/WhiteRabbitNeo/Llama-3-WhiteRabbitNeo-8B-v2.0',
    56    name: 'WhiteRabbitNeo 8B',
    57    capabilities: ['coding', 'creative', 'unrestricted'],
    58    cost: 'very-low',
    59    speed: 'very-fast',
    60    quality: 'good',
    61    vision: false
    62  },
    63  'llama-fast': {
    64    id: 'featherless/mlabonne/Meta-Llama-3.1-8B-Instruct-abliterated',
    65    name: 'Llama 3.1 8B',
    66    capabilities: ['general', 'fast-response', 'unrestricted'],
    67    cost: 'very-low',
    68    speed: 'very-fast',
    69    quality: 'good',
    70    vision: false
    71  },
    72  'llama-70b': {
    73    id: 'featherless/huihui-ai/Llama-3.3-70B-Instruct-abliterated',
    74    name: 'Llama 3.3 70B',
    75    capabilities: ['reasoning', 'coding', 'writing', 'unrestricted'],
    76    cost: 'medium',
    77    speed: 'medium',
    78    quality: 'exceptional',
    79    vision: false
    80  },
    81  'kimi-k2': {
    82    id: 'featherless/moonshotai/Kimi-K2-Instruct',
    83    name: 'Kimi K2',
    84    capabilities: ['agentic', 'coding', 'autonomous', 'reasoning', 'unrestricted'],
    85    cost: 'medium',
    86    speed: 'fast',
    87    quality: 'exceptional',
    88    vision: false
    89  },
    90  'glm-4.7': {
    91    id: 'glm/glm-4.7',
    92    name: 'GLM-4.7',
    93    capabilities: ['reasoning', 'coding', 'chinese', 'multilingual'],
    94    cost: 'low',
    95    speed: 'fast',
    96    quality: 'high',
    97    vision: false
    98  }
    99};
   100
   101/**
   102 * MCP Provider implementation
   103 */
   104export class MCPProvider implements ILLMProvider {
   105  public readonly name = 'mcp';
   106  public readonly capabilities: ProviderCapabilities = {
   107    streaming: false, // MCP server doesn't support streaming yet
   108    vision: false, // No vision models in current lineup
   109    tools: false, // No tool support yet
   110    systemPrompt: true,
   111    multiModal: false
   112  };
   113
   114  private proxyUrl: string;
   115  private defaultModel: string;
   116
   117  constructor(config: ProviderConfig) {
   118    this.proxyUrl = config.baseUrl || process.env.PROXY_URL || 'http://127.0.0.1:3000';
   119    this.defaultModel = config.defaultModel || 'qwen-72b';
   120  }
   121
   122  /**
   123   * Send a completion request via proxy
   124   */
   125  async complete(request: LLMRequest): Promise<LLMResponse> {
   126    const modelKey = request.model || this.defaultModel;
   127    const modelInfo = MCP_MODELS[modelKey];
   128
   129    if (!modelInfo) {
   130      throw new Error(`Unknown MCP model: ${modelKey}. Available: ${Object.keys(MCP_MODELS).join(', ')}`);
   131    }
   132
   133    // Build messages array
   134    const messages: any[] = [];
   135
   136    // Add system message if provided
   137    if (request.system) {
   138      messages.push({ role: 'system', content: request.system });
   139    }
   140
   141    // Add request messages
   142    for (const msg of request.messages) {
   143      messages.push({
   144        role: msg.role,
   145        content: this.flattenContent(msg.content)
   146      });
   147    }
   148
   149    // Call proxy server
   150    const response = await fetch(`${this.proxyUrl}/v1/messages`, {
   151      method: 'POST',
   152      headers: {
   153        'Content-Type': 'application/json',
   154        'anthropic-version': '2023-06-01'
   155      },
   156      body: JSON.stringify({
   157        model: modelInfo.id,
   158        messages,
   159        max_tokens: request.max_tokens || 2048,
   160        temperature: request.temperature,
   161        stream: false
   162      })
   163    });
   164
   165    if (!response.ok) {
   166      const error = await response.text();
   167      throw new Error(`MCP proxy error: ${response.status} ${error}`);
   168    }
   169
   170    const data = (await response.json()) as any;
   171
   172    if (data.error) {
   173      throw new Error(data.error.message || JSON.stringify(data.error));
   174    }
   175
   176    // Convert to our format
   177    const text = data.content?.find((b: any) => b.type === 'text')?.text || '';
   178
   179    return {
   180      id: data.id || `mcp-${Date.now()}`,
   181      model: modelKey,
   182      role: 'assistant',
   183      content: [{ type: 'text', text }],
   184      stop_reason: 'end_turn',
   185      usage: {
   186        input_tokens: data.usage?.input_tokens || 0,
   187        output_tokens: data.usage?.output_tokens || 0,
   188        total_tokens: (data.usage?.input_tokens || 0) + (data.usage?.output_tokens || 0)
   189      }
   190    };
   191  }
   192
   193  /**
   194   * Streaming not supported yet
   195   */
   196  async streamComplete(request: LLMRequest, handler: StreamHandler): Promise<LLMResponse> {
   197    throw new Error('Streaming not supported by MCP provider yet');
   198  }
   199
   200  /**
   201   * List available MCP models
   202   */
   203  async listModels(): Promise<string[]> {
   204    return Object.keys(MCP_MODELS);
   205  }
   206
   207  /**
   208   * Get model info
   209   */
   210  getModelInfo(modelKey: string): MCPModel | undefined {
   211    return MCP_MODELS[modelKey];
   212  }
   213
   214  /**
   215   * Flatten content to string (MCP proxy doesn't support multi-modal yet)
   216   */
   217  private flattenContent(content: string | ContentBlock[]): string {
   218    if (typeof content === 'string') {
   219      return content;
   220    }
   221
   222    return content
   223      .filter(block => block.type === 'text')
   224      .map(block => (block as any).text)
   225      .join('\n');
   226  }
   227}
   228
   229/**
   230 * Check if MCP proxy is available
   231 */
