{
  "$schema": "https://raw.githubusercontent.com/anthropics/claude-code/main/schemas/mcp-servers.schema.json",
  "mcpServers": {
    "runpod": {
      "command": "npx",
      "args": ["-y", "@runpod/mcp"],
      "env": {
        "RUNPOD_API_KEY": "${RUNPOD_API_KEY}"
      },
      "description": "RunPod cloud GPU management - pods, endpoints, templates, volumes"
    },
    "ghidra": {
      "command": "python3",
      "args": ["-m", "ghidra_mcp"],
      "env": {
        "GHIDRA_INSTALL_PATH": "/Applications/ghidra"
      },
      "description": "Ghidra reverse engineering - decompile, analyze binaries, rename methods"
    },
    "memory": {
      "command": "npx",
      "args": ["-y", "@modelcontextprotocol/server-memory"],
      "env": {
        "MEMORY_FILE_PATH": "${HOME}/.claude/memory/mcp-knowledge-graph.jsonl"
      },
      "description": "Official Anthropic knowledge graph memory - entities, relations, observations"
    },
    "qdrant-memory": {
      "command": "mcp-server-qdrant",
      "args": [
        "--qdrant-url", "http://localhost:6333",
        "--collection-name", "claude_memory",
        "--embedding-model", "sentence-transformers/all-MiniLM-L6-v2"
      ],
      "disabled": true,
      "description": "Qdrant vector memory - semantic search, embeddings (requires: pip install mcp-server-qdrant)"
    },
    "basic-memory": {
      "command": "basic-memory",
      "args": ["serve"],
      "disabled": true,
      "description": "Markdown-based memory with SQLite indexing (requires: pip install basic-memory)"
    },
    "firecrawl": {
      "command": "npx",
      "args": ["-y", "firecrawl-mcp"],
      "env": {
        "FIRECRAWL_API_KEY": "${FIRECRAWL_API_KEY}"
      },
      "disabled": true,
      "description": "Firecrawl web scraping - crawl, scrape, search, extract (requires: FIRECRAWL_API_KEY)"
    },
    "fetch": {
      "command": "uvx",
      "args": ["mcp-server-fetch"],
      "description": "Simple URL fetcher MCP server (requires: pip install mcp-server-fetch)"
    },
    "glm": {
      "command": "node",
      "args": ["${HOME}/.claude/glm-proxy-server.js"],
      "env": {
        "GLM_API_KEY": "9a58c7331504f3cbaef3f2f95cb375b.BrfNpV8TbeF5tCaK"
      },
      "description": "GLM (ZhipuAI) models - glm-4, glm-4-air, glm-4-airx, glm-4-flash"
    },
    "github": {
      "command": "npx",
      "args": ["-y", "@modelcontextprotocol/server-github"],
      "env": {
        "GITHUB_PERSONAL_ACCESS_TOKEN": "${GITHUB_PERSONAL_ACCESS_TOKEN}"
      },
      "disabled": true,
      "description": "GitHub integration - repos, issues, PRs, code search (requires: GITHUB_PERSONAL_ACCESS_TOKEN)"
    }
  }
}

