const { Pool } = require('pg');
const pool = new Pool({ connectionString: process.env.DATABASE_URL, ssl: { rejectUnauthorized: false } });

async function fix() {
  const client = await pool.connect();
  try {
    await client.query('BEGIN');

    // Update users table
    await client.query(\"UPDATE users SET stripe_customer_id = 'cus_beta_josh_irizarry' WHERE stripe_customer_id = 'beta_josh_irizarry'\");
    await client.query(\"UPDATE users SET stripe_customer_id = 'cus_beta_danny_isakov' WHERE stripe_customer_id = 'beta_danny_isakov'\");
    await client.query(\"UPDATE users SET stripe_customer_id = 'cus_beta_jimmy_nick' WHERE stripe_customer_id = 'beta_jimmy_nick'\");

    // Update license_keys table
    await client.query(\"UPDATE license_keys SET stripe_customer_id = 'cus_beta_josh_irizarry' WHERE stripe_customer_id = 'beta_josh_irizarry'\");
    await client.query(\"UPDATE license_keys SET stripe_customer_id = 'cus_beta_danny_isakov' WHERE stripe_customer_id = 'beta_danny_isakov'\");
    await client.query(\"UPDATE license_keys SET stripe_customer_id = 'cus_beta_jimmy_nick' WHERE stripe_customer_id = 'beta_jimmy_nick'\");

    await client.query('COMMIT');
    console.log('Updated all customer IDs to cus_ format');

    // Verify
    const users = await client.query('SELECT stripe_customer_id, email FROM users');
    console.log('Users:', users.rows);
  } catch (e) {
    await client.query('ROLLBACK');
    throw e;
  } finally {
    client.release();
    pool.end();
  }
}
fix();
"

