import { defineConfig } from 'vite';
import { resolve } from 'path';

export default defineConfig({
  root: 'public',
  base: './',
  build: {
    outDir: '../dist',
    emptyOutDir: true,
    rollupOptions: {
      input: {
        main: resolve(__dirname, 'public/index.html'),
      },
      output: {
        entryFileNames: 'index.js',
        chunkFileNames: '[name].js',
        assetFileNames: '[name].[ext]',
      },
    },
    target: 'es2020',
    minify: 'esbuild',
    sourcemap: true,
  },
  resolve: {
    alias: {
      '@': resolve(__dirname, 'src'),
      '@components': resolve(__dirname, 'src/components'),
      '@hooks': resolve(__dirname, 'src/hooks'),
      '@utils': resolve(__dirname, 'src/utils'),
      '@types': resolve(__dirname, 'src/types'),
      '@context': resolve(__dirname, 'src/context'),
      '@config': resolve(__dirname, 'src/config'),
    },
  },
  server: {
    port: 3000,
    open: false,
  },
});

// Central type exports for Splice v3

export * from './plugin';
export * from './api';
export * from './ui';
export * from './premiere';

// Plugin-specific types

export interface PluginState {
  isInitialized: boolean;
  isPremium: boolean;
  currentPlan: PricingPlan;
  selectedClips: ClipSelection[];
  aiServiceConnected: boolean;
}

export type PricingPlan = 'free' | 'pro' | 'enterprise';

export interface ClipSelection {
  id: string;
  name: string;
  startTime: number;
  endTime: number;
  trackIndex: number;
  type: 'video' | 'audio';
}

export interface PluginConfig {
  apiEndpoint: string;
  maxFreeOperations: number;
  features: FeatureFlags;
}

export interface FeatureFlags {
  aiAnalysis: boolean;
  batchProcessing: boolean;
  advancedSelection: boolean;
  cloudSync: boolean;
}

export interface UserLicense {
  userId: string;
  plan: PricingPlan;
  expiresAt: Date | null;
  operationsUsed: number;
  operationsLimit: number;
}

// API types for AI services integration

export interface AIServiceConfig {
  provider: AIProvider;
  apiKey: string;
  model?: string;
  maxTokens?: number;
}

export type AIProvider = 'openai' | 'anthropic' | 'google';

export interface AIAnalysisRequest {
  clipData: ClipAnalysisData;
  analysisType: AnalysisType;
  options?: AIAnalysisOptions;
}

export interface ClipAnalysisData {
  duration: number;
  frameRate: number;
  audioChannels: number;
  metadata: Record<string, unknown>;
}

export type AnalysisType = 'content' | 'scene-detection' | 'audio-analysis' | 'smart-cut';

export interface AIAnalysisOptions {
  detailed?: boolean;
  language?: string;
  customPrompt?: string;
}

export interface AIAnalysisResponse {
  success: boolean;
  result?: AnalysisResult;
  error?: APIError;
  usage?: APIUsage;
}

export interface AnalysisResult {
  scenes?: SceneMarker[];
  suggestions?: EditSuggestion[];
  transcription?: string;
  metadata?: Record<string, unknown>;
}

export interface SceneMarker {
  startTime: number;
  endTime: number;
  confidence: number;
  label?: string;
}

export interface EditSuggestion {
  type: 'cut' | 'trim' | 'transition' | 'effect';
  startTime: number;
  endTime: number;
  description: string;
  priority: 'low' | 'medium' | 'high';
}

export interface APIError {
  code: string;
  message: string;
  details?: Record<string, unknown>;
}

export interface APIUsage {
  tokensUsed: number;
  requestsRemaining: number;
  costEstimate?: number;
}

// UI component prop types

export interface PanelProps {
  isLoading?: boolean;
  error?: string | null;
}

export interface ButtonProps {
  variant?: 'primary' | 'secondary' | 'negative' | 'accent';
  size?: 'small' | 'medium' | 'large';
  disabled?: boolean;
  loading?: boolean;
  onClick?: () => void;
}

export interface DialogProps {
  open: boolean;
  title: string;
  onClose: () => void;
  onConfirm?: () => void;
  confirmLabel?: string;
  cancelLabel?: string;
  variant?: 'confirmation' | 'information' | 'destructive';
}

export interface SelectionListProps {
  items: SelectionItem[];
  selectedIds: string[];
  onSelectionChange: (ids: string[]) => void;
  multiSelect?: boolean;
}

export interface SelectionItem {
  id: string;
  label: string;
  description?: string;
  icon?: string;
  disabled?: boolean;
}

export interface ToastNotification {
  id: string;
  message: string;
  variant: 'info' | 'positive' | 'negative' | 'warning';
  duration?: number;
}

export interface ProgressIndicatorProps {
  value?: number;
  indeterminate?: boolean;
  label?: string;
  size?: 'small' | 'medium' | 'large';
}

// Premiere Pro API types (extends @adobe/cc-ext-uxp-types)

export interface PremiereProject {
  name: string;
  path: string;
  sequences: PremiereSequence[];
  activeSequence: PremiereSequence | null;
}

export interface PremiereSequence {
  id: string;
  name: string;
  videoTracks: PremiereTrack[];
  audioTracks: PremiereTrack[];
  duration: number;
  frameRate: FrameRate;
}

export interface PremiereTrack {
  id: string;
  name: string;
  index: number;
  clips: PremiereClip[];
  muted: boolean;
  locked: boolean;
}

export interface PremiereClip {
  id: string;
  name: string;
  startTime: number;
  endTime: number;
  duration: number;
  inPoint: number;
  outPoint: number;
  mediaPath?: string;
  isSelected: boolean;
}

export interface FrameRate {
  numerator: number;
  denominator: number;
  value: number;
}

export interface TimelineMarker {
  id: string;
  name: string;
  time: number;
  duration: number;
  color: MarkerColor;
  comments?: string;
}

export type MarkerColor = 'green' | 'red' | 'purple' | 'orange' | 'yellow' | 'white' | 'blue' | 'cyan';

export interface SelectionState {
  clips: PremiereClip[];
  markers: TimelineMarker[];
  tracks: PremiereTrack[];
}

