/* Splice v3 - Global Styles & Design Tokens */

:root {
  /* Color Tokens */
  --color-primary: #0d66d0;
  --color-primary-hover: #1473e6;
  --color-secondary: #6e6e6e;
  --color-accent: #0fbf84;
  --color-negative: #d7373f;
  --color-warning: #e68619;

  /* Background Colors */
  --bg-base: #1e1e1e;
  --bg-layer-1: #252525;
  --bg-layer-2: #2c2c2c;
  --bg-elevated: #323232;

  /* Text Colors */
  --text-primary: #ffffff;
  --text-secondary: #b3b3b3;
  --text-disabled: #6e6e6e;

  /* Border Colors */
  --border-default: #3e3e3e;
  --border-hover: #5c5c5c;
  --border-focus: var(--color-primary);

  /* Spacing */
  --space-xs: 4px;
  --space-sm: 8px;
  --space-md: 16px;
  --space-lg: 24px;
  --space-xl: 32px;

  /* Border Radius */
  --radius-sm: 4px;
  --radius-md: 8px;
  --radius-lg: 12px;

  /* Shadows */
  --shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.3);
  --shadow-md: 0 4px 8px rgba(0, 0, 0, 0.4);
  --shadow-lg: 0 8px 16px rgba(0, 0, 0, 0.5);

  /* Typography */
  --font-family: 'Adobe Clean', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  --font-size-xs: 11px;
  --font-size-sm: 12px;
  --font-size-md: 14px;
  --font-size-lg: 16px;
  --font-size-xl: 20px;
  --font-weight-normal: 400;
  --font-weight-medium: 500;
  --font-weight-bold: 700;

  /* Transitions */
  --transition-fast: 100ms ease;
  --transition-normal: 200ms ease;
  --transition-slow: 300ms ease;
}

/* Reset & Base Styles */
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html,
body {
  height: 100%;
  font-family: var(--font-family);
  font-size: var(--font-size-md);
  color: var(--text-primary);
  background-color: var(--bg-base);
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

#app {
  height: 100%;
  display: flex;
  flex-direction: column;
}

/* Spectrum Theme Overrides */
sp-theme {
  --spectrum-global-dimension-size-100: 8px;
  --spectrum-global-dimension-size-200: 16px;
  background-color: var(--bg-base);
}

/* Scrollbar Styles */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--bg-layer-1);
}

::-webkit-scrollbar-thumb {
  background: var(--border-default);
  border-radius: var(--radius-sm);
}

::-webkit-scrollbar-thumb:hover {
  background: var(--border-hover);
}

/* Utility Classes */
.hidden {
  display: none !important;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Freemium Badge Styles */
.premium-badge {
  background: linear-gradient(135deg, #ffd700, #ffaa00);
  color: #1a1a1a;
  padding: 2px 8px;
  border-radius: var(--radius-sm);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-bold);
  text-transform: uppercase;
}

.free-badge {
  background: var(--bg-layer-2);
  color: var(--text-secondary);
  padding: 2px 8px;
  border-radius: var(--radius-sm);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-medium);
}

// Splice v3 - Main Entry Point
import '@spectrum-web-components/theme/sp-theme.js';
import '@spectrum-web-components/theme/theme-dark.js';
import '@spectrum-web-components/theme/scale-medium.js';
import '@spectrum-web-components/button/sp-button.js';
import '@spectrum-web-components/action-button/sp-action-button.js';
import '@spectrum-web-components/menu/sp-menu.js';
import '@spectrum-web-components/menu/sp-menu-item.js';
import '@spectrum-web-components/picker/sp-picker.js';
import '@spectrum-web-components/textfield/sp-textfield.js';
import '@spectrum-web-components/checkbox/sp-checkbox.js';
import '@spectrum-web-components/progress-circle/sp-progress-circle.js';
import '@spectrum-web-components/toast/sp-toast.js';

import { PluginApp } from './components/panels/PluginApp';
import { initializePlugin } from './utils/api/plugin';
import { logger } from './utils/logger';

// Initialize plugin when DOM is ready
document.addEventListener('DOMContentLoaded', async () => {
  try {
    logger.info('Splice v3 initializing...');

    // Initialize plugin core
    await initializePlugin();

    // Mount the application
    const appContainer = document.getElementById('app');
    if (appContainer) {
      const app = new PluginApp();
      appContainer.appendChild(app);
      logger.info('Splice v3 mounted successfully');
    } else {
      throw new Error('App container not found');
    }
  } catch (error) {
    logger.error('Failed to initialize Splice v3:', error);
  }
});

// Logging utility for Splice v3

type LogLevel = 'debug' | 'info' | 'warn' | 'error';

interface LogEntry {
  level: LogLevel;
  message: string;
  timestamp: string;
  data?: unknown;
}

const LOG_PREFIX = '[Splice v3]';

class Logger {
  private isDevelopment: boolean;
  private logHistory: LogEntry[] = [];
  private maxHistorySize = 100;

  constructor() {
    this.isDevelopment = import.meta.env?.DEV ?? true;
  }

  private formatMessage(level: LogLevel, message: string): string {
    const timestamp = new Date().toISOString();
    return `${LOG_PREFIX} [${level.toUpperCase()}] ${timestamp}: ${message}`;
  }

  private addToHistory(entry: LogEntry): void {
    this.logHistory.push(entry);
    if (this.logHistory.length > this.maxHistorySize) {
      this.logHistory.shift();
    }
  }

  debug(message: string, data?: unknown): void {
    if (this.isDevelopment) {
      console.debug(this.formatMessage('debug', message), data ?? '');
      this.addToHistory({ level: 'debug', message, timestamp: new Date().toISOString(), data });
    }
  }

  info(message: string, data?: unknown): void {
    console.info(this.formatMessage('info', message), data ?? '');
    this.addToHistory({ level: 'info', message, timestamp: new Date().toISOString(), data });
  }

  warn(message: string, data?: unknown): void {
    console.warn(this.formatMessage('warn', message), data ?? '');
    this.addToHistory({ level: 'warn', message, timestamp: new Date().toISOString(), data });
  }

  error(message: string, error?: unknown): void {
    console.error(this.formatMessage('error', message), error ?? '');
    this.addToHistory({ level: 'error', message, timestamp: new Date().toISOString(), data: error });
  }

  getHistory(): LogEntry[] {
    return [...this.logHistory];
  }

  clearHistory(): void {
    this.logHistory = [];
  }
}

export const logger = new Logger();

// Plugin initialization and core API

import type { PluginState, PluginConfig, UserLicense } from '@/types/plugin';
import { logger } from '../logger';

const DEFAULT_CONFIG: PluginConfig = {
  apiEndpoint: 'https://api.splice.com/v3',
  maxFreeOperations: 10,
  features: {
    aiAnalysis: true,
    batchProcessing: false,
    advancedSelection: false,
    cloudSync: false,
  },
};

let pluginState: PluginState = {
  isInitialized: false,
  isPremium: false,
  currentPlan: 'free',
  selectedClips: [],
  aiServiceConnected: false,
};

let userLicense: UserLicense | null = null;

export async function initializePlugin(): Promise<void> {
  try {
    logger.info('Initializing plugin...');

    // Load saved state from local storage
    await loadPersistedState();

    // Check license status
    await checkLicenseStatus();

    pluginState.isInitialized = true;
    logger.info('Plugin initialized successfully', { plan: pluginState.currentPlan });
  } catch (error) {
    logger.error('Failed to initialize plugin', error);
    throw error;
  }
}

async function loadPersistedState(): Promise<void> {
  try {
    // UXP local storage access
    const storage = await getLocalStorage();
    const savedState = storage.getItem('splice-v3-state');
    if (savedState) {
      const parsed = JSON.parse(savedState);
      pluginState = { ...pluginState, ...parsed };
    }
  } catch (error) {
    logger.warn('Could not load persisted state', error);
  }
}

async function checkLicenseStatus(): Promise<void> {
  try {
    // In a real implementation, this would call your licensing API
    // For now, we'll use the freemium free tier
    userLicense = {
      userId: 'local-user',
      plan: 'free',
      expiresAt: null,
      operationsUsed: 0,
      operationsLimit: DEFAULT_CONFIG.maxFreeOperations,
    };

    pluginState.currentPlan = userLicense.plan;
    pluginState.isPremium = userLicense.plan !== 'free';

    // Update features based on plan
    updateFeaturesForPlan(userLicense.plan);
  } catch (error) {
    logger.warn('Could not check license status, defaulting to free tier', error);
  }
}

function updateFeaturesForPlan(plan: UserLicense['plan']): void {
  const config = getConfig();

  switch (plan) {
    case 'pro':
      config.features.batchProcessing = true;
      config.features.advancedSelection = true;
      break;
    case 'enterprise':
      config.features.batchProcessing = true;
      config.features.advancedSelection = true;
      config.features.cloudSync = true;
      break;
    default:
      // Free tier - defaults are already set
      break;
  }
}

export function getPluginState(): PluginState {
  return { ...pluginState };
}

export function getConfig(): PluginConfig {
  return { ...DEFAULT_CONFIG };
}

export function getUserLicense(): UserLicense | null {
  return userLicense ? { ...userLicense } : null;
}

export async function saveState(): Promise<void> {
  try {
    const storage = await getLocalStorage();
    storage.setItem('splice-v3-state', JSON.stringify(pluginState));
    logger.debug('State saved');
  } catch (error) {
    logger.error('Failed to save state', error);
  }
}

export function incrementOperationsUsed(): boolean {
  if (!userLicense) return false;

  if (userLicense.plan === 'free' && userLicense.operationsUsed >= userLicense.operationsLimit) {
    logger.warn('Free tier operation limit reached');
    return false;
  }

  userLicense.operationsUsed++;
  saveState();
  return true;
}

export function canPerformOperation(): boolean {
  if (!userLicense) return false;
  if (userLicense.plan !== 'free') return true;
  return userLicense.operationsUsed < userLicense.operationsLimit;
}

export function getRemainingOperations(): number {
  if (!userLicense) return 0;
  if (userLicense.plan !== 'free') return Infinity;
  return Math.max(0, userLicense.operationsLimit - userLicense.operationsUsed);
}

// UXP storage helper
async function getLocalStorage(): Promise<Storage> {
  // In UXP environment, use the localStorage API
  // This is a simplified version - real UXP plugins use the storage module
  return window.localStorage;
}

// AI Services API Integration

import type {
  AIServiceConfig,
  AIProvider,
  AIAnalysisRequest,
  AIAnalysisResponse,
  AnalysisResult,
} from '@/types/api';
import { logger } from '../logger';

let aiConfig: AIServiceConfig | null = null;

export function configureAIService(config: AIServiceConfig): void {
  aiConfig = { ...config };
  logger.info('AI service configured', { provider: config.provider });
}

export function isAIServiceConfigured(): boolean {
  return aiConfig !== null && !!aiConfig.apiKey;
}

export async function analyzeClip(request: AIAnalysisRequest): Promise<AIAnalysisResponse> {
  if (!aiConfig) {
    return {
      success: false,
      error: {
        code: 'NOT_CONFIGURED',
        message: 'AI service is not configured. Please add your API key in settings.',
      },
    };
  }

  try {
    logger.info('Starting clip analysis', { type: request.analysisType });

    const result = await callAIProvider(aiConfig, request);

    logger.info('Clip analysis completed', { success: true });
    return {
      success: true,
      result,
      usage: {
        tokensUsed: 0, // Would be populated from actual API response
        requestsRemaining: 100,
      },
    };
  } catch (error) {
    logger.error('AI analysis failed', error);
    return {
      success: false,
      error: {
        code: 'ANALYSIS_FAILED',
        message: error instanceof Error ? error.message : 'Unknown error occurred',
      },
    };
  }
}

async function callAIProvider(
  config: AIServiceConfig,
  request: AIAnalysisRequest
): Promise<AnalysisResult> {
  const endpoint = getProviderEndpoint(config.provider);
  const headers = getProviderHeaders(config);
  const body = buildRequestBody(config, request);

  const response = await fetch(endpoint, {
    method: 'POST',
    headers,
    body: JSON.stringify(body),
  });

  if (!response.ok) {
    const errorData = await response.json().catch(() => ({}));
    throw new Error(errorData.message || `API request failed: ${response.status}`);
  }

  const data = await response.json();
  return parseProviderResponse(config.provider, data, request.analysisType);
}

function getProviderEndpoint(provider: AIProvider): string {
  switch (provider) {
    case 'anthropic':
      return 'https://api.anthropic.com/v1/messages';
    case 'openai':
      return 'https://api.openai.com/v1/chat/completions';
    case 'google':
      return 'https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent';
    default:
      throw new Error(`Unsupported AI provider: ${provider}`);
  }
}

function getProviderHeaders(config: AIServiceConfig): HeadersInit {
  const headers: HeadersInit = {
    'Content-Type': 'application/json',
  };

  switch (config.provider) {
    case 'anthropic':
      headers['x-api-key'] = config.apiKey;
      headers['anthropic-version'] = '2024-01-01';
      break;
    case 'openai':
      headers['Authorization'] = `Bearer ${config.apiKey}`;
      break;
    case 'google':
      headers['x-goog-api-key'] = config.apiKey;
      break;
  }

  return headers;
}

function buildRequestBody(
  config: AIServiceConfig,
  request: AIAnalysisRequest
): Record<string, unknown> {
  const prompt = buildAnalysisPrompt(request);

  switch (config.provider) {
    case 'anthropic':
      return {
        model: config.model || 'claude-3-5-sonnet-20241022',
        max_tokens: config.maxTokens || 1024,
        messages: [{ role: 'user', content: prompt }],
      };
    case 'openai':
      return {
        model: config.model || 'gpt-4o',
        max_tokens: config.maxTokens || 1024,
        messages: [{ role: 'user', content: prompt }],
      };
    case 'google':
      return {
        contents: [{ parts: [{ text: prompt }] }],
        generationConfig: {
          maxOutputTokens: config.maxTokens || 1024,
        },
      };
    default:
      throw new Error(`Unsupported provider: ${config.provider}`);
  }
}

function buildAnalysisPrompt(request: AIAnalysisRequest): string {
  const { clipData, analysisType, options } = request;

  if (options?.customPrompt) {
    return options.customPrompt;
  }

  const baseContext = `Analyzing video clip with duration: ${clipData.duration}s, frame rate: ${clipData.frameRate}fps, audio channels: ${clipData.audioChannels}.`;

  switch (analysisType) {
    case 'scene-detection':
      return `${baseContext}\n\nAnalyze this clip and identify distinct scenes. For each scene, provide start time, end time, and a brief description. Return as JSON array.`;
    case 'audio-analysis':
      return `${baseContext}\n\nAnalyze the audio characteristics of this clip. Identify key moments, volume changes, and suggest edit points based on audio. Return as JSON.`;
    case 'smart-cut':
      return `${baseContext}\n\nSuggest optimal cut points for this clip based on pacing and content flow. Provide timestamps and reasoning. Return as JSON.`;
    case 'content':
    default:
      return `${baseContext}\n\nProvide a detailed analysis of this clip's content, themes, and suggested improvements. Return as JSON.`;
  }
}

function parseProviderResponse(
  provider: AIProvider,
  response: Record<string, unknown>,
  analysisType: string
): AnalysisResult {
  let content: string;

  switch (provider) {
    case 'anthropic':
      content = (response.content as Array<{ text: string }>)?.[0]?.text || '';
      break;
    case 'openai':
      content = (response.choices as Array<{ message: { content: string } }>)?.[0]?.message?.content || '';
      break;
    case 'google':
      content = (response.candidates as Array<{ content: { parts: Array<{ text: string }> } }>)?.[0]?.content?.parts?.[0]?.text || '';
      break;
    default:
      content = '';
  }

  try {
    // Try to parse JSON from the response
    const jsonMatch = content.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
    if (jsonMatch) {
      return JSON.parse(jsonMatch[0]);
    }
  } catch {
    logger.warn('Could not parse AI response as JSON');
  }

  // Return as metadata if not parseable
  return {
    metadata: {
      rawResponse: content,
      analysisType,
    },
  };
}

export function getAvailableProviders(): { id: AIProvider; name: string }[] {
  return [
    { id: 'anthropic', name: 'Anthropic Claude' },
    { id: 'openai', name: 'OpenAI GPT' },
    { id: 'google', name: 'Google Gemini' },
  ];
}

