// Helper utilities barrel export

export * from './formatters';
export * from './validators';

// Formatting utilities

/**
 * Format time in seconds to HH:MM:SS:FF (timecode format)
 */
export function formatTimecode(seconds: number, frameRate = 24): string {
  const totalFrames = Math.floor(seconds * frameRate);
  const frames = totalFrames % frameRate;
  const totalSeconds = Math.floor(totalFrames / frameRate);
  const secs = totalSeconds % 60;
  const mins = Math.floor(totalSeconds / 60) % 60;
  const hours = Math.floor(totalSeconds / 3600);

  return [
    hours.toString().padStart(2, '0'),
    mins.toString().padStart(2, '0'),
    secs.toString().padStart(2, '0'),
    frames.toString().padStart(2, '0'),
  ].join(':');
}

/**
 * Format duration in a human-readable format
 */
export function formatDuration(seconds: number): string {
  if (seconds < 60) {
    return `${seconds.toFixed(1)}s`;
  } else if (seconds < 3600) {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}m ${secs}s`;
  } else {
    const hours = Math.floor(seconds / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    return `${hours}h ${mins}m`;
  }
}

/**
 * Format file size in human-readable format
 */
export function formatFileSize(bytes: number): string {
  const units = ['B', 'KB', 'MB', 'GB', 'TB'];
  let unitIndex = 0;
  let size = bytes;

  while (size >= 1024 && unitIndex < units.length - 1) {
    size /= 1024;
    unitIndex++;
  }

  return `${size.toFixed(unitIndex === 0 ? 0 : 1)} ${units[unitIndex]}`;
}

/**
 * Format percentage
 */
export function formatPercentage(value: number, decimals = 0): string {
  return `${(value * 100).toFixed(decimals)}%`;
}

/**
 * Truncate text with ellipsis
 */
export function truncateText(text: string, maxLength: number): string {
  if (text.length <= maxLength) return text;
  return `${text.slice(0, maxLength - 3)}...`;
}

/**
 * Format clip name for display
 */
export function formatClipName(name: string, maxLength = 30): string {
  // Remove file extension if present
  const nameWithoutExt = name.replace(/\.[^/.]+$/, '');
  return truncateText(nameWithoutExt, maxLength);
}

/**
 * Format date for display
 */
export function formatDate(date: Date | string): string {
  const d = typeof date === 'string' ? new Date(date) : date;
  return d.toLocaleDateString(undefined, {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
  });
}

// Validation utilities

import type { AIProvider } from '@/types/api';

/**
 * Validate time range
 */
export function isValidTimeRange(startTime: number, endTime: number): boolean {
  return startTime >= 0 && endTime > startTime;
}

/**
 * Validate API key format based on provider
 */
export function isValidApiKey(apiKey: string, provider: AIProvider): boolean {
  if (!apiKey || typeof apiKey !== 'string') return false;

  const trimmed = apiKey.trim();

  switch (provider) {
    case 'anthropic':
      // Anthropic keys start with 'sk-ant-'
      return trimmed.startsWith('sk-ant-') && trimmed.length > 20;
    case 'openai':
      // OpenAI keys start with 'sk-'
      return trimmed.startsWith('sk-') && trimmed.length > 20;
    case 'google':
      // Google API keys are typically 39 characters
      return trimmed.length >= 30;
    default:
      return trimmed.length > 10;
  }
}

/**
 * Validate email format
 */
export function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

/**
 * Validate that a value is a positive number
 */
export function isPositiveNumber(value: unknown): value is number {
  return typeof value === 'number' && !isNaN(value) && value > 0;
}

/**
 * Validate frame rate
 */
export function isValidFrameRate(frameRate: number): boolean {
  const validRates = [23.976, 24, 25, 29.97, 30, 50, 59.94, 60];
  return validRates.some(rate => Math.abs(rate - frameRate) < 0.01);
}

/**
 * Validate clip selection
 */
export function isValidClipSelection(clip: {
  id: string;
  startTime: number;
  endTime: number;
}): boolean {
  return (
    typeof clip.id === 'string' &&
    clip.id.length > 0 &&
    isValidTimeRange(clip.startTime, clip.endTime)
  );
}

/**
 * Sanitize string input
 */
export function sanitizeInput(input: string): string {
  return input
    .trim()
    .replace(/[<>]/g, '') // Remove potential HTML tags
    .slice(0, 1000); // Limit length
}

/**
 * Validate URL
 */
export function isValidUrl(url: string): boolean {
  try {
    new URL(url);
    return true;
  } catch {
    return false;
  }
}

