      font-weight: var(--font-weight-bold);
      text-transform: capitalize;
    }
  `;

  @property({ type: String }) currentPlan: PricingPlan = 'free';

  @state() private selectedProvider: AIProvider = 'anthropic';
  @state() private apiKey = '';
  @state() private isSaving = false;
  @state() private saveMessage = '';

  private providers = getAvailableProviders();

  private async handleSaveApiKey(): Promise<void> {
    if (!this.apiKey.trim()) {
      this.saveMessage = 'Please enter an API key';
      return;
    }

    if (!isValidApiKey(this.apiKey, this.selectedProvider)) {
      this.saveMessage = 'Invalid API key format';
      return;
    }

    this.isSaving = true;
    this.saveMessage = '';

    try {
      configureAIService({
        provider: this.selectedProvider,
        apiKey: this.apiKey.trim(),
      });

      this.saveMessage = 'API key saved successfully!';
      logger.info('AI service configured', { provider: this.selectedProvider });

      // Clear the input for security
      this.apiKey = '';
    } catch (error) {
      this.saveMessage = 'Failed to save API key';
      logger.error('Failed to configure AI service', error);
    } finally {
      this.isSaving = false;
    }
  }

  private handleProviderChange(e: Event): void {
    const picker = e.target as HTMLSelectElement;
    this.selectedProvider = picker.value as AIProvider;
  }

  render() {
    const isConfigured = isAIServiceConfigured();

    return html`
      <div class="section">
        <div class="section-title">AI Service Configuration</div>
        <div class="setting-card">
          <div class="setting-row">
            <div>
              <span class="setting-label">Connection Status</span>
            </div>
            <div class="status-indicator ${isConfigured ? 'connected' : 'disconnected'}">
              <span class="status-dot"></span>
              ${isConfigured ? 'Connected' : 'Not Configured'}
            </div>
          </div>

          <div class="setting-row">
            <span class="setting-label">AI Provider & API Key</span>
            <span class="setting-description">
              Select your AI provider and enter your API key. Keys are stored locally.
            </span>
            <div class="form-row">
              <sp-picker
                label="Provider"
                .value=${this.selectedProvider}
                @change=${this.handleProviderChange}
              >
                ${this.providers.map(
                  p => html`
                    <sp-menu-item value=${p.id} ?selected=${this.selectedProvider === p.id}>
                      ${p.name}
                    </sp-menu-item>
                  `
                )}
              </sp-picker>
              <sp-textfield
                type="password"
                placeholder="Enter API key"
                .value=${this.apiKey}
                @input=${(e: Event) => (this.apiKey = (e.target as HTMLInputElement).value)}
              ></sp-textfield>
            </div>
            <sp-button
              variant="primary"
              ?disabled=${this.isSaving}
              @click=${this.handleSaveApiKey}
            >
              ${this.isSaving ? 'Saving...' : 'Save API Key'}
            </sp-button>
            ${this.saveMessage
              ? html`<span class="setting-description">${this.saveMessage}</span>`
              : ''}
          </div>
        </div>
      </div>

      <div class="section">
        <div class="section-title">Subscription</div>
        <div class="setting-card">
          <div class="plan-info">
            <div>
              <span class="setting-label">Current Plan</span>
              <div class="plan-name">${this.currentPlan}</div>
            </div>
            ${this.currentPlan === 'free'
              ? html`<sp-button variant="accent" size="s">Upgrade</sp-button>`
              : html`<sp-button variant="secondary" size="s">Manage</sp-button>`}
          </div>
        </div>
      </div>

      <div class="section">
        <div class="section-title">About</div>
        <div class="setting-card about-section">
          <p><strong>Splice v3</strong></p>
          <p>Version 1.0.0</p>
          <p>
            <a href="https://splice.com/help" target="_blank">Help & Support</a> |
            <a href="https://splice.com/privacy" target="_blank">Privacy Policy</a>
          </p>
        </div>
      </div>
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    'settings-panel': SettingsPanel;
  }
}

// Upgrade Prompt - Freemium conversion modal
import { LitElement, html, css } from 'lit';
import { customElement, property } from 'lit/decorators.js';
import type { PricingPlan } from '@/types/plugin';

@customElement('upgrade-prompt')
export class UpgradePrompt extends LitElement {
  static styles = css`
    :host {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      background: rgba(0, 0, 0, 0.7);
      z-index: 1000;
    }

    .modal {
      background: var(--bg-layer-1);
      border-radius: var(--radius-lg);
      padding: var(--space-lg);
      max-width: 400px;
      width: 90%;
      box-shadow: var(--shadow-lg);
    }

    .modal-header {
      text-align: center;
      margin-bottom: var(--space-lg);
    }

    .modal-title {
      font-size: var(--font-size-xl);
      font-weight: var(--font-weight-bold);
      margin-bottom: var(--space-xs);
    }

    .modal-subtitle {
      color: var(--text-secondary);
      font-size: var(--font-size-sm);
    }

    .plans {
      display: flex;
      flex-direction: column;
      gap: var(--space-md);
      margin-bottom: var(--space-lg);
    }

    .plan-card {
      background: var(--bg-layer-2);
      border: 2px solid var(--border-default);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      cursor: pointer;
      transition: all var(--transition-fast);
    }

    .plan-card:hover {
      border-color: var(--color-primary);
    }

    .plan-card.recommended {
      border-color: var(--color-accent);
      background: rgba(15, 191, 132, 0.05);
    }

    .plan-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: var(--space-sm);
    }

    .plan-name {
      font-weight: var(--font-weight-bold);
    }

    .plan-badge {
      font-size: var(--font-size-xs);
      padding: 2px 8px;
      border-radius: var(--radius-sm);
      background: var(--color-accent);
      color: white;
    }

    .plan-price {
      font-size: var(--font-size-lg);
      font-weight: var(--font-weight-bold);
      margin-bottom: var(--space-sm);
    }

    .plan-price span {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-normal);
      color: var(--text-secondary);
    }

    .plan-features {
      list-style: none;
      padding: 0;
      margin: 0;
    }

    .plan-features li {
      font-size: var(--font-size-sm);
      color: var(--text-secondary);
      padding: var(--space-xs) 0;
      display: flex;
      align-items: center;
      gap: var(--space-xs);
    }

    .plan-features li::before {
      content: '✓';
      color: var(--color-accent);
    }

    .modal-actions {
      display: flex;
      gap: var(--space-sm);
    }

    .modal-actions sp-button {
      flex: 1;
    }

    .close-button {
      position: absolute;
      top: var(--space-sm);
      right: var(--space-sm);
      background: none;
      border: none;
      color: var(--text-secondary);
      cursor: pointer;
      font-size: 20px;
      padding: var(--space-xs);
    }

    .close-button:hover {
      color: var(--text-primary);
    }
  `;

  @property({ type: String }) currentPlan: PricingPlan = 'free';

  private plans = [
    {
      id: 'pro' as PricingPlan,
      name: 'Pro',
      price: 9.99,
      period: 'month',
      recommended: true,
      features: [
        'Unlimited AI operations',
        'Batch processing',
        'Advanced selection tools',
        'Priority support',
      ],
    },
    {
      id: 'enterprise' as PricingPlan,
      name: 'Enterprise',
      price: 29.99,
      period: 'month',
      recommended: false,
      features: [
        'Everything in Pro',
        'Cloud sync & backup',
        'Team collaboration',
        'Custom AI models',
        'Dedicated support',
      ],
    },
  ];

  private handleClose(): void {
    this.dispatchEvent(new CustomEvent('close', { bubbles: true, composed: true }));
  }

  private handleUpgrade(plan: PricingPlan): void {
    // In a real implementation, this would redirect to a payment page
    // or open Adobe Exchange purchase flow
    window.open(`https://splice.com/upgrade?plan=${plan}`, '_blank');
  }

  render() {
    return html`
      <div class="modal">
        <button class="close-button" @click=${this.handleClose}>×</button>

        <div class="modal-header">
          <div class="modal-title">Upgrade to Pro</div>
          <div class="modal-subtitle">Unlock the full power of Splice v3</div>
        </div>

        <div class="plans">
          ${this.plans.map(
            plan => html`
              <div
                class="plan-card ${plan.recommended ? 'recommended' : ''}"
                @click=${() => this.handleUpgrade(plan.id)}
              >
                <div class="plan-header">
                  <span class="plan-name">${plan.name}</span>
                  ${plan.recommended
                    ? html`<span class="plan-badge">Most Popular</span>`
                    : ''}
                </div>
                <div class="plan-price">
                  $${plan.price} <span>/ ${plan.period}</span>
                </div>
                <ul class="plan-features">
                  ${plan.features.map(feature => html`<li>${feature}</li>`)}
                </ul>
              </div>
            `
          )}
        </div>

        <div class="modal-actions">
          <sp-button variant="secondary" @click=${this.handleClose}>
            Maybe Later
          </sp-button>
          <sp-button variant="accent" @click=${() => this.handleUpgrade('pro')}>
            Get Pro
          </sp-button>
        </div>
      </div>
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    'upgrade-prompt': UpgradePrompt;
  }
}

