// Main Plugin Application Component
import { LitElement, html, css } from 'lit';
import { customElement, state } from 'lit/decorators.js';
import type { ClipSelection, PricingPlan } from '@/types/plugin';
import { getPluginState, getUserLicense, getRemainingOperations } from '@/utils/api/plugin';
import { getSelectedClips } from '@/utils/api/premiere';
import { isAIServiceConfigured } from '@/utils/api/ai-service';
import { logger } from '@/utils/logger';

import '../common/Header';
import '../common/SelectionPanel';
import '../common/AIToolsPanel';
import '../common/SettingsPanel';
import '../common/UpgradePrompt';

type TabId = 'selection' | 'ai-tools' | 'settings';

@customElement('plugin-app')
export class PluginApp extends LitElement {
  static styles = css`
    :host {
      display: flex;
      flex-direction: column;
      height: 100%;
      background-color: var(--bg-base);
      color: var(--text-primary);
    }

    .main-content {
      flex: 1;
      overflow-y: auto;
      padding: var(--space-md);
    }

    .tab-content {
      display: none;
    }

    .tab-content.active {
      display: block;
    }

    .footer {
      padding: var(--space-sm) var(--space-md);
      border-top: 1px solid var(--border-default);
      display: flex;
      justify-content: space-between;
      align-items: center;
      font-size: var(--font-size-xs);
      color: var(--text-secondary);
    }

    .operations-count {
      display: flex;
      align-items: center;
      gap: var(--space-xs);
    }

    .operations-count.low {
      color: var(--color-warning);
    }

    .operations-count.depleted {
      color: var(--color-negative);
    }
  `;

  @state() private activeTab: TabId = 'selection';
  @state() private selectedClips: ClipSelection[] = [];
  @state() private currentPlan: PricingPlan = 'free';
  @state() private remainingOperations = 0;
  @state() private isLoading = false;
  @state() private showUpgradePrompt = false;

  connectedCallback(): void {
    super.connectedCallback();
    this.initializeState();
  }

  private async initializeState(): Promise<void> {
    try {
      const state = getPluginState();
      const license = getUserLicense();

      this.currentPlan = state.currentPlan;
      this.remainingOperations = getRemainingOperations();

      // Get initial selection
      await this.refreshSelection();
    } catch (error) {
      logger.error('Failed to initialize app state', error);
    }
  }

  private async refreshSelection(): Promise<void> {
    this.isLoading = true;
    try {
      this.selectedClips = await getSelectedClips();
    } finally {
      this.isLoading = false;
    }
  }

  private handleTabChange(tab: TabId): void {
    this.activeTab = tab;
  }

  private handleUpgradeClick(): void {
    this.showUpgradePrompt = true;
  }

  private handleUpgradeClose(): void {
    this.showUpgradePrompt = false;
  }

  private getOperationsClass(): string {
    if (this.remainingOperations === 0) return 'operations-count depleted';
    if (this.remainingOperations <= 3) return 'operations-count low';
    return 'operations-count';
  }

  render() {
    return html`
      <app-header
        .activeTab=${this.activeTab}
        .currentPlan=${this.currentPlan}
        @tab-change=${(e: CustomEvent) => this.handleTabChange(e.detail)}
        @upgrade-click=${this.handleUpgradeClick}
      ></app-header>

      <div class="main-content">
        <div class="tab-content ${this.activeTab === 'selection' ? 'active' : ''}">
          <selection-panel
            .selectedClips=${this.selectedClips}
            .isLoading=${this.isLoading}
            @refresh=${this.refreshSelection}
          ></selection-panel>
        </div>

        <div class="tab-content ${this.activeTab === 'ai-tools' ? 'active' : ''}">
          <ai-tools-panel
            .selectedClips=${this.selectedClips}
            .remainingOperations=${this.remainingOperations}
            .isPremium=${this.currentPlan !== 'free'}
            @operation-used=${() => this.remainingOperations--}
            @upgrade-needed=${this.handleUpgradeClick}
          ></ai-tools-panel>
        </div>

        <div class="tab-content ${this.activeTab === 'settings' ? 'active' : ''}">
          <settings-panel
            .currentPlan=${this.currentPlan}
          ></settings-panel>
        </div>
      </div>

      <div class="footer">
        <span>Splice v3</span>
        ${this.currentPlan === 'free'
          ? html`
              <span class=${this.getOperationsClass()}>
                ${this.remainingOperations} operations remaining
              </span>
            `
          : html`<span class="premium-badge">PRO</span>`}
      </div>

      ${this.showUpgradePrompt
        ? html`
            <upgrade-prompt
              .currentPlan=${this.currentPlan}
              @close=${this.handleUpgradeClose}
            ></upgrade-prompt>
          `
        : ''}
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    'plugin-app': PluginApp;
  }
}

// Header component with navigation tabs
import { LitElement, html, css } from 'lit';
import { customElement, property } from 'lit/decorators.js';
import type { PricingPlan } from '@/types/plugin';

type TabId = 'selection' | 'ai-tools' | 'settings';

@customElement('app-header')
export class Header extends LitElement {
  static styles = css`
    :host {
      display: block;
      border-bottom: 1px solid var(--border-default);
    }

    .header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: var(--space-sm) var(--space-md);
    }

    .title {
      font-size: var(--font-size-lg);
      font-weight: var(--font-weight-bold);
      display: flex;
      align-items: center;
      gap: var(--space-sm);
    }

    .plan-badge {
      font-size: var(--font-size-xs);
      padding: 2px 6px;
      border-radius: var(--radius-sm);
      text-transform: uppercase;
    }

    .plan-badge.free {
      background: var(--bg-layer-2);
      color: var(--text-secondary);
    }

    .plan-badge.pro {
      background: linear-gradient(135deg, #ffd700, #ffaa00);
      color: #1a1a1a;
    }

    .plan-badge.enterprise {
      background: linear-gradient(135deg, #8b5cf6, #6366f1);
      color: white;
    }

    .tabs {
      display: flex;
      gap: var(--space-xs);
      padding: 0 var(--space-md);
      padding-bottom: var(--space-sm);
    }

    .tab {
      padding: var(--space-xs) var(--space-sm);
      border: none;
      background: transparent;
      color: var(--text-secondary);
      cursor: pointer;
      font-size: var(--font-size-sm);
      border-radius: var(--radius-sm);
      transition: all var(--transition-fast);
    }

    .tab:hover {
      background: var(--bg-layer-1);
      color: var(--text-primary);
    }

    .tab.active {
      background: var(--color-primary);
      color: white;
    }

    .upgrade-btn {
      font-size: var(--font-size-xs);
    }
  `;

  @property({ type: String }) activeTab: TabId = 'selection';
  @property({ type: String }) currentPlan: PricingPlan = 'free';

  private tabs: { id: TabId; label: string }[] = [
    { id: 'selection', label: 'Selection' },
    { id: 'ai-tools', label: 'AI Tools' },
    { id: 'settings', label: 'Settings' },
  ];

  private handleTabClick(tabId: TabId): void {
    this.dispatchEvent(
      new CustomEvent('tab-change', {
        detail: tabId,
        bubbles: true,
        composed: true,
      })
    );
  }

  private handleUpgradeClick(): void {
    this.dispatchEvent(
      new CustomEvent('upgrade-click', {
        bubbles: true,
        composed: true,
      })
    );
  }

  render() {
    return html`
      <div class="header">
        <div class="title">
          Splice
          <span class="plan-badge ${this.currentPlan}">${this.currentPlan}</span>
        </div>
        ${this.currentPlan === 'free'
          ? html`
              <sp-button
                class="upgrade-btn"
                variant="accent"
                size="s"
                @click=${this.handleUpgradeClick}
              >
                Upgrade
              </sp-button>
            `
          : ''}
      </div>
      <div class="tabs">
        ${this.tabs.map(
          tab => html`
            <button
              class="tab ${this.activeTab === tab.id ? 'active' : ''}"
              @click=${() => this.handleTabClick(tab.id)}
            >
              ${tab.label}
            </button>
          `
        )}
      </div>
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    'app-header': Header;
  }
}

// Selection Panel - Core selection tools for Premiere Pro
import { LitElement, html, css } from 'lit';
import { customElement, property, state } from 'lit/decorators.js';
import type { ClipSelection } from '@/types/plugin';
import { selectClipsByTimeRange, selectAllClipsInTrack } from '@/utils/api/premiere';
import { formatTimecode, formatDuration } from '@/utils/helpers/formatters';
import { logger } from '@/utils/logger';

@customElement('selection-panel')
export class SelectionPanel extends LitElement {
  static styles = css`
    :host {
      display: block;
    }

    .section {
      margin-bottom: var(--space-lg);
    }

    .section-title {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-medium);
      color: var(--text-secondary);
      margin-bottom: var(--space-sm);
      text-transform: uppercase;
      letter-spacing: 0.5px;
    }

    .selection-summary {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      margin-bottom: var(--space-md);
    }

    .summary-stat {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: var(--space-xs) 0;
    }

    .summary-stat:not(:last-child) {
      border-bottom: 1px solid var(--border-default);
    }

    .stat-label {
      color: var(--text-secondary);
      font-size: var(--font-size-sm);
    }

    .stat-value {
      font-weight: var(--font-weight-medium);
    }

    .clip-list {
      max-height: 200px;
      overflow-y: auto;
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
    }

    .clip-item {
      display: flex;
      align-items: center;
      padding: var(--space-sm) var(--space-md);
      gap: var(--space-sm);
      border-bottom: 1px solid var(--border-default);
    }

    .clip-item:last-child {
      border-bottom: none;
    }

    .clip-icon {
      width: 16px;
      height: 16px;
      border-radius: 2px;
    }

    .clip-icon.video {
      background: var(--color-primary);
    }

    .clip-icon.audio {
      background: var(--color-accent);
    }

    .clip-info {
      flex: 1;
      min-width: 0;
    }

    .clip-name {
      font-size: var(--font-size-sm);
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }

    .clip-time {
      font-size: var(--font-size-xs);
      color: var(--text-secondary);
    }

    .empty-state {
      text-align: center;
      padding: var(--space-xl);
      color: var(--text-secondary);
    }

    .empty-state-icon {
      font-size: 48px;
      margin-bottom: var(--space-md);
      opacity: 0.5;
    }

    .quick-actions {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: var(--space-sm);
    }

    .time-range-form {
      display: flex;
      flex-direction: column;
      gap: var(--space-sm);
    }

    .time-inputs {
      display: flex;
      gap: var(--space-sm);
    }

    .time-inputs sp-textfield {
      flex: 1;
    }
  `;

  @property({ type: Array }) selectedClips: ClipSelection[] = [];
  @property({ type: Boolean }) isLoading = false;

  @state() private startTime = '';
  @state() private endTime = '';
  @state() private selectedTrack = -1;

  private handleRefresh(): void {
    this.dispatchEvent(new CustomEvent('refresh', { bubbles: true, composed: true }));
  }

  private async handleTimeRangeSelect(): Promise<void> {
    if (!this.startTime || !this.endTime) return;

    try {
      const start = parseFloat(this.startTime);
      const end = parseFloat(this.endTime);

      if (isNaN(start) || isNaN(end) || end <= start) {
        logger.warn('Invalid time range');
        return;
      }

      await selectClipsByTimeRange(start, end);
      this.handleRefresh();
    } catch (error) {
      logger.error('Failed to select by time range', error);
    }
  }

  private async handleSelectAllInTrack(trackIndex: number, type: 'video' | 'audio'): Promise<void> {
    try {
      await selectAllClipsInTrack(trackIndex, type);
      this.handleRefresh();
    } catch (error) {
      logger.error('Failed to select all in track', error);
    }
  }

  private getTotalDuration(): number {
    return this.selectedClips.reduce((total, clip) => total + (clip.endTime - clip.startTime), 0);
  }

  private getVideoClipsCount(): number {
    return this.selectedClips.filter(c => c.type === 'video').length;
  }

  private getAudioClipsCount(): number {
    return this.selectedClips.filter(c => c.type === 'audio').length;
  }

  render() {
    if (this.isLoading) {
      return html`
        <div class="empty-state">
          <sp-progress-circle indeterminate size="l"></sp-progress-circle>
          <p>Loading selection...</p>
        </div>
      `;
    }

    return html`
      <div class="section">
