// AI Tools Panel - AI-powered analysis and editing suggestions
import { LitElement, html, css } from 'lit';
import { customElement, property, state } from 'lit/decorators.js';
import type { ClipSelection } from '@/types/plugin';
import type { AnalysisType, AIAnalysisResponse } from '@/types/api';
import { analyzeClip, isAIServiceConfigured } from '@/utils/api/ai-service';
import { canPerformOperation, incrementOperationsUsed } from '@/utils/api/plugin';
import { logger } from '@/utils/logger';

@customElement('ai-tools-panel')
export class AIToolsPanel extends LitElement {
  static styles = css`
    :host {
      display: block;
    }

    .section {
      margin-bottom: var(--space-lg);
    }

    .section-title {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-medium);
      color: var(--text-secondary);
      margin-bottom: var(--space-sm);
      text-transform: uppercase;
      letter-spacing: 0.5px;
    }

    .ai-tools-grid {
      display: grid;
      gap: var(--space-sm);
    }

    .ai-tool-card {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      cursor: pointer;
      transition: all var(--transition-fast);
      border: 1px solid transparent;
    }

    .ai-tool-card:hover {
      background: var(--bg-layer-2);
      border-color: var(--border-hover);
    }

    .ai-tool-card.disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    .ai-tool-card.disabled:hover {
      background: var(--bg-layer-1);
      border-color: transparent;
    }

    .tool-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: var(--space-xs);
    }

    .tool-name {
      font-weight: var(--font-weight-medium);
    }

    .tool-badge {
      font-size: var(--font-size-xs);
      padding: 2px 6px;
      border-radius: var(--radius-sm);
      background: var(--color-accent);
      color: white;
    }

    .tool-badge.premium {
      background: linear-gradient(135deg, #ffd700, #ffaa00);
      color: #1a1a1a;
    }

    .tool-description {
      font-size: var(--font-size-sm);
      color: var(--text-secondary);
    }

    .warning-banner {
      background: var(--bg-layer-1);
      border-left: 3px solid var(--color-warning);
      padding: var(--space-md);
      border-radius: var(--radius-sm);
      margin-bottom: var(--space-md);
    }

    .warning-banner h4 {
      margin: 0 0 var(--space-xs) 0;
      color: var(--color-warning);
      font-size: var(--font-size-sm);
    }

    .warning-banner p {
      margin: 0;
      font-size: var(--font-size-sm);
      color: var(--text-secondary);
    }

    .results-section {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
    }

    .result-item {
      padding: var(--space-sm) 0;
      border-bottom: 1px solid var(--border-default);
    }

    .result-item:last-child {
      border-bottom: none;
    }

    .loading-state {
      display: flex;
      flex-direction: column;
      align-items: center;
      padding: var(--space-xl);
      gap: var(--space-md);
    }
  `;

  @property({ type: Array }) selectedClips: ClipSelection[] = [];
  @property({ type: Number }) remainingOperations = 0;
  @property({ type: Boolean }) isPremium = false;

  @state() private isAnalyzing = false;
  @state() private analysisResult: AIAnalysisResponse | null = null;

  private aiTools = [
    {
      id: 'scene-detection' as AnalysisType,
      name: 'Scene Detection',
      description: 'Automatically detect scene changes and suggest cut points',
      premium: false,
    },
    {
      id: 'smart-cut' as AnalysisType,
      name: 'Smart Cut Suggestions',
      description: 'AI-powered suggestions for optimal edit points',
      premium: false,
    },
    {
      id: 'audio-analysis' as AnalysisType,
      name: 'Audio Analysis',
      description: 'Analyze audio for beats, silence, and speech patterns',
      premium: true,
    },
    {
      id: 'content' as AnalysisType,
      name: 'Content Analysis',
      description: 'Deep analysis of clip content and themes',
      premium: true,
    },
  ];

  private async handleToolClick(toolId: AnalysisType, isPremiumTool: boolean): Promise<void> {
    // Check if service is configured
    if (!isAIServiceConfigured()) {
      logger.warn('AI service not configured');
      return;
    }

    // Check if clips are selected
    if (this.selectedClips.length === 0) {
      logger.warn('No clips selected for analysis');
      return;
    }

    // Check premium requirement
    if (isPremiumTool && !this.isPremium) {
      this.dispatchEvent(new CustomEvent('upgrade-needed', { bubbles: true, composed: true }));
      return;
    }

    // Check operation limit for free tier
    if (!this.isPremium && !canPerformOperation()) {
      this.dispatchEvent(new CustomEvent('upgrade-needed', { bubbles: true, composed: true }));
      return;
    }

    this.isAnalyzing = true;
    this.analysisResult = null;

    try {
      // Use first selected clip for analysis
      const clip = this.selectedClips[0];

      const result = await analyzeClip({
        clipData: {
          duration: clip.endTime - clip.startTime,
          frameRate: 24, // Would come from actual project settings
          audioChannels: 2,
          metadata: { name: clip.name },
        },
        analysisType: toolId,
      });

      this.analysisResult = result;

      // Decrement operations for free tier
      if (!this.isPremium && result.success) {
        incrementOperationsUsed();
        this.dispatchEvent(new CustomEvent('operation-used', { bubbles: true, composed: true }));
      }
    } catch (error) {
      logger.error('Analysis failed', error);
    } finally {
      this.isAnalyzing = false;
    }
  }

  private canUseTool(isPremiumTool: boolean): boolean {
    if (!isAIServiceConfigured()) return false;
    if (this.selectedClips.length === 0) return false;
    if (isPremiumTool && !this.isPremium) return false;
    if (!this.isPremium && !canPerformOperation()) return false;
    return true;
  }

  render() {
    if (!isAIServiceConfigured()) {
      return html`
        <div class="warning-banner">
          <h4>⚠️ AI Service Not Configured</h4>
          <p>Please add your API key in Settings to use AI-powered features.</p>
        </div>
      `;
    }

    if (this.isAnalyzing) {
      return html`
        <div class="loading-state">
          <sp-progress-circle indeterminate size="l"></sp-progress-circle>
          <p>Analyzing clip...</p>
        </div>
      `;
    }

    return html`
      ${this.selectedClips.length === 0
        ? html`
            <div class="warning-banner">
              <h4>📹 No Clips Selected</h4>
              <p>Select clips in the Selection tab or Premiere Pro timeline to use AI tools.</p>
            </div>
          `
        : ''}

      <div class="section">
        <div class="section-title">AI-Powered Tools</div>
        <div class="ai-tools-grid">
          ${this.aiTools.map(
            tool => html`
              <div
                class="ai-tool-card ${this.canUseTool(tool.premium) ? '' : 'disabled'}"
                @click=${() => this.canUseTool(tool.premium) && this.handleToolClick(tool.id, tool.premium)}
              >
                <div class="tool-header">
                  <span class="tool-name">${tool.name}</span>
                  ${tool.premium
                    ? html`<span class="tool-badge premium">PRO</span>`
                    : html`<span class="tool-badge">AI</span>`}
                </div>
                <div class="tool-description">${tool.description}</div>
              </div>
            `
          )}
        </div>
      </div>

      ${this.analysisResult
        ? html`
            <div class="section">
              <div class="section-title">Analysis Results</div>
              <div class="results-section">
                ${this.analysisResult.success
                  ? html`
                      <pre>${JSON.stringify(this.analysisResult.result, null, 2)}</pre>
                    `
                  : html`
                      <p style="color: var(--color-negative)">
                        Error: ${this.analysisResult.error?.message}
                      </p>
                    `}
              </div>
            </div>
          `
        : ''}
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    'ai-tools-panel': AIToolsPanel;
  }
}

// Settings Panel - Plugin configuration and API setup
import { LitElement, html, css } from 'lit';
import { customElement, property, state } from 'lit/decorators.js';
import type { PricingPlan } from '@/types/plugin';
import type { AIProvider } from '@/types/api';
import { configureAIService, getAvailableProviders, isAIServiceConfigured } from '@/utils/api/ai-service';
import { isValidApiKey } from '@/utils/helpers/validators';
import { logger } from '@/utils/logger';

@customElement('settings-panel')
export class SettingsPanel extends LitElement {
  static styles = css`
    :host {
      display: block;
    }

    .section {
      margin-bottom: var(--space-lg);
    }

    .section-title {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-medium);
      color: var(--text-secondary);
      margin-bottom: var(--space-sm);
      text-transform: uppercase;
      letter-spacing: 0.5px;
    }

    .setting-card {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      margin-bottom: var(--space-sm);
    }

    .setting-row {
      display: flex;
      flex-direction: column;
      gap: var(--space-sm);
      margin-bottom: var(--space-md);
    }

    .setting-row:last-child {
      margin-bottom: 0;
    }

    .setting-label {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-medium);
    }

    .setting-description {
      font-size: var(--font-size-xs);
      color: var(--text-secondary);
    }

    .status-indicator {
      display: inline-flex;
      align-items: center;
      gap: var(--space-xs);
      font-size: var(--font-size-sm);
      padding: var(--space-xs) var(--space-sm);
      border-radius: var(--radius-sm);
    }

    .status-indicator.connected {
      background: rgba(15, 191, 132, 0.1);
      color: var(--color-accent);
    }

    .status-indicator.disconnected {
      background: rgba(215, 55, 63, 0.1);
      color: var(--color-negative);
    }

    .status-dot {
      width: 8px;
      height: 8px;
      border-radius: 50%;
    }

    .status-indicator.connected .status-dot {
      background: var(--color-accent);
    }

    .status-indicator.disconnected .status-dot {
      background: var(--color-negative);
    }

    .form-row {
      display: flex;
      gap: var(--space-sm);
    }

    .form-row sp-picker {
      width: 140px;
    }

    .form-row sp-textfield {
      flex: 1;
    }

    .about-section {
      text-align: center;
      padding: var(--space-md);
      color: var(--text-secondary);
      font-size: var(--font-size-sm);
    }

    .about-section a {
      color: var(--color-primary);
      text-decoration: none;
    }

    .about-section a:hover {
      text-decoration: underline;
    }

    .plan-info {
      display: flex;
      align-items: center;
      justify-content: space-between;
    }

    .plan-name {
      font-weight: var(--font-weight-bold);
      text-transform: capitalize;
    }
  `;

  @property({ type: String }) currentPlan: PricingPlan = 'free';

  @state() private selectedProvider: AIProvider = 'anthropic';
  @state() private apiKey = '';
  @state() private isSaving = false;
  @state() private saveMessage = '';

  private providers = getAvailableProviders();

  private async handleSaveApiKey(): Promise<void> {
    if (!this.apiKey.trim()) {
      this.saveMessage = 'Please enter an API key';
      return;
    }

    if (!isValidApiKey(this.apiKey, this.selectedProvider)) {
      this.saveMessage = 'Invalid API key format';
      return;
    }

    this.isSaving = true;
    this.saveMessage = '';

    try {
      configureAIService({
        provider: this.selectedProvider,
        apiKey: this.apiKey.trim(),
      });

      this.saveMessage = 'API key saved successfully!';
      logger.info('AI service configured', { provider: this.selectedProvider });

      // Clear the input for security
      this.apiKey = '';
    } catch (error) {
      this.saveMessage = 'Failed to save API key';
      logger.error('Failed to configure AI service', error);
    } finally {
      this.isSaving = false;
    }
  }

  private handleProviderChange(e: Event): void {
    const picker = e.target as HTMLSelectElement;
    this.selectedProvider = picker.value as AIProvider;
  }

  render() {
    const isConfigured = isAIServiceConfigured();

    return html`
      <div class="section">
        <div class="section-title">AI Service Configuration</div>
        <div class="setting-card">
          <div class="setting-row">
            <div>
              <span class="setting-label">Connection Status</span>
            </div>
            <div class="status-indicator ${isConfigured ? 'connected' : 'disconnected'}">
              <span class="status-dot"></span>
              ${isConfigured ? 'Connected' : 'Not Configured'}
            </div>
          </div>

          <div class="setting-row">
            <span class="setting-label">AI Provider & API Key</span>
            <span class="setting-description">
              Select your AI provider and enter your API key. Keys are stored locally.
            </span>
            <div class="form-row">
              <sp-picker
                label="Provider"
                .value=${this.selectedProvider}
                @change=${this.handleProviderChange}
              >
                ${this.providers.map(
                  p => html`
                    <sp-menu-item value=${p.id} ?selected=${this.selectedProvider === p.id}>
                      ${p.name}
                    </sp-menu-item>
                  `
                )}
              </sp-picker>
              <sp-textfield
                type="password"
                placeholder="Enter API key"
                .value=${this.apiKey}
                @input=${(e: Event) => (this.apiKey = (e.target as HTMLInputElement).value)}
              ></sp-textfield>
            </div>
            <sp-button
              variant="primary"
              ?disabled=${this.isSaving}
              @click=${this.handleSaveApiKey}
            >
              ${this.isSaving ? 'Saving...' : 'Save API Key'}
            </sp-button>
            ${this.saveMessage
              ? html`<span class="setting-description">${this.saveMessage}</span>`
              : ''}
          </div>
        </div>
      </div>

      <div class="section">
        <div class="section-title">Subscription</div>
        <div class="setting-card">
          <div class="plan-info">
            <div>
              <span class="setting-label">Current Plan</span>
              <div class="plan-name">${this.currentPlan}</div>
            </div>
            ${this.currentPlan === 'free'
              ? html`<sp-button variant="accent" size="s">Upgrade</sp-button>`
              : html`<sp-button variant="secondary" size="s">Manage</sp-button>`}
          </div>
        </div>
      </div>

      <div class="section">
        <div class="section-title">About</div>
        <div class="setting-card about-section">
          <p><strong>Splice v3</strong></p>
          <p>Version 1.0.0</p>
          <p>
            <a href="https://splice.com/help" target="_blank">Help & Support</a> |
            <a href="https://splice.com/privacy" target="_blank">Privacy Policy</a>
          </p>
        </div>
      </div>
    `;
  }
}

