import { describe, it, expect } from 'vitest';
import {
  formatTimecode,
  formatDuration,
  formatFileSize,
  formatPercentage,
  truncateText,
  formatClipName,
} from '@/utils/helpers/formatters';

describe('formatters', () => {
  describe('formatTimecode', () => {
    it('should format 0 seconds correctly', () => {
      expect(formatTimecode(0, 24)).toBe('00:00:00:00');
    });

    it('should format seconds with frames correctly', () => {
      expect(formatTimecode(1.5, 24)).toBe('00:00:01:12');
    });

    it('should format minutes correctly', () => {
      expect(formatTimecode(90, 24)).toBe('00:01:30:00');
    });

    it('should format hours correctly', () => {
      expect(formatTimecode(3661, 24)).toBe('01:01:01:00');
    });
  });

  describe('formatDuration', () => {
    it('should format seconds', () => {
      expect(formatDuration(45)).toBe('45.0s');
    });

    it('should format minutes and seconds', () => {
      expect(formatDuration(125)).toBe('2m 5s');
    });

    it('should format hours and minutes', () => {
      expect(formatDuration(3720)).toBe('1h 2m');
    });
  });

  describe('formatFileSize', () => {
    it('should format bytes', () => {
      expect(formatFileSize(500)).toBe('500 B');
    });

    it('should format kilobytes', () => {
      expect(formatFileSize(1536)).toBe('1.5 KB');
    });

    it('should format megabytes', () => {
      expect(formatFileSize(1048576)).toBe('1.0 MB');
    });

    it('should format gigabytes', () => {
      expect(formatFileSize(1073741824)).toBe('1.0 GB');
    });
  });

  describe('formatPercentage', () => {
    it('should format without decimals by default', () => {
      expect(formatPercentage(0.5)).toBe('50%');
    });

    it('should format with specified decimals', () => {
      expect(formatPercentage(0.333, 2)).toBe('33.30%');
    });
  });

  describe('truncateText', () => {
    it('should not truncate short text', () => {
      expect(truncateText('Hello', 10)).toBe('Hello');
    });

    it('should truncate long text with ellipsis', () => {
      expect(truncateText('Hello World', 8)).toBe('Hello...');
    });
  });

  describe('formatClipName', () => {
    it('should remove file extension', () => {
      expect(formatClipName('video.mp4')).toBe('video');
    });

    it('should truncate long names', () => {
      expect(formatClipName('this_is_a_very_long_clip_name.mov', 15)).toBe('this_is_a_ve...');
    });
  });
});

import { describe, it, expect } from 'vitest';
import {
  isValidTimeRange,
  isValidApiKey,
  isValidEmail,
  isPositiveNumber,
  isValidFrameRate,
  isValidClipSelection,
  sanitizeInput,
  isValidUrl,
} from '@/utils/helpers/validators';

describe('validators', () => {
  describe('isValidTimeRange', () => {
    it('should return true for valid range', () => {
      expect(isValidTimeRange(0, 10)).toBe(true);
    });

    it('should return false for negative start', () => {
      expect(isValidTimeRange(-1, 10)).toBe(false);
    });

    it('should return false when end <= start', () => {
      expect(isValidTimeRange(10, 5)).toBe(false);
      expect(isValidTimeRange(10, 10)).toBe(false);
    });
  });

  describe('isValidApiKey', () => {
    it('should validate Anthropic keys', () => {
      expect(isValidApiKey('sk-ant-api03-xxxxxxxxxxxx', 'anthropic')).toBe(true);
      expect(isValidApiKey('invalid', 'anthropic')).toBe(false);
    });

    it('should validate OpenAI keys', () => {
      expect(isValidApiKey('sk-xxxxxxxxxxxxxxxxxxxx', 'openai')).toBe(true);
      expect(isValidApiKey('invalid', 'openai')).toBe(false);
    });

    it('should validate Google keys', () => {
      expect(isValidApiKey('AIzaSyxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx', 'google')).toBe(true);
      expect(isValidApiKey('short', 'google')).toBe(false);
    });
  });

  describe('isValidEmail', () => {
    it('should return true for valid emails', () => {
      expect(isValidEmail('test@example.com')).toBe(true);
      expect(isValidEmail('user.name@domain.co.uk')).toBe(true);
    });

    it('should return false for invalid emails', () => {
      expect(isValidEmail('invalid')).toBe(false);
      expect(isValidEmail('missing@domain')).toBe(false);
      expect(isValidEmail('@nodomain.com')).toBe(false);
    });
  });

  describe('isPositiveNumber', () => {
    it('should return true for positive numbers', () => {
      expect(isPositiveNumber(1)).toBe(true);
      expect(isPositiveNumber(0.5)).toBe(true);
    });

    it('should return false for non-positive values', () => {
      expect(isPositiveNumber(0)).toBe(false);
      expect(isPositiveNumber(-1)).toBe(false);
      expect(isPositiveNumber(NaN)).toBe(false);
      expect(isPositiveNumber('5')).toBe(false);
    });
  });

  describe('isValidFrameRate', () => {
    it('should return true for common frame rates', () => {
      expect(isValidFrameRate(24)).toBe(true);
      expect(isValidFrameRate(29.97)).toBe(true);
      expect(isValidFrameRate(60)).toBe(true);
    });

    it('should return false for unusual frame rates', () => {
      expect(isValidFrameRate(15)).toBe(false);
      expect(isValidFrameRate(100)).toBe(false);
    });
  });

  describe('isValidClipSelection', () => {
    it('should return true for valid clip', () => {
      expect(isValidClipSelection({ id: 'clip-1', startTime: 0, endTime: 10 })).toBe(true);
    });

    it('should return false for invalid clips', () => {
      expect(isValidClipSelection({ id: '', startTime: 0, endTime: 10 })).toBe(false);
      expect(isValidClipSelection({ id: 'clip-1', startTime: 10, endTime: 5 })).toBe(false);
    });
  });

  describe('sanitizeInput', () => {
    it('should trim whitespace', () => {
      expect(sanitizeInput('  hello  ')).toBe('hello');
    });

    it('should remove HTML tags', () => {
      expect(sanitizeInput('<script>alert(1)</script>')).toBe('scriptalert(1)/script');
    });

    it('should limit length', () => {
      const longInput = 'a'.repeat(2000);
      expect(sanitizeInput(longInput).length).toBe(1000);
    });
  });

  describe('isValidUrl', () => {
    it('should return true for valid URLs', () => {
      expect(isValidUrl('https://example.com')).toBe(true);
      expect(isValidUrl('http://localhost:3000')).toBe(true);
    });

    it('should return false for invalid URLs', () => {
      expect(isValidUrl('not-a-url')).toBe(false);
      expect(isValidUrl('')).toBe(false);
    });
  });
});

import { test, expect } from '@playwright/test';

test.describe('Splice v3 Plugin', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
  });

  test('should load the plugin application', async ({ page }) => {
    // Check that the main app container exists
    await expect(page.locator('#app')).toBeVisible();
  });

  test('should display the header with app title', async ({ page }) => {
    // Wait for custom elements to be defined
    await page.waitForSelector('plugin-app');

    // Check for the Splice title in shadow DOM
    const header = page.locator('app-header');
    await expect(header).toBeVisible();
  });

  test('should show tabs for navigation', async ({ page }) => {
    await page.waitForSelector('plugin-app');

    // Look for tab buttons
    const selectionTab = page.getByRole('button', { name: /selection/i });
    const aiToolsTab = page.getByRole('button', { name: /ai tools/i });
    const settingsTab = page.getByRole('button', { name: /settings/i });

    await expect(selectionTab).toBeVisible();
    await expect(aiToolsTab).toBeVisible();
    await expect(settingsTab).toBeVisible();
  });

  test('should switch tabs when clicked', async ({ page }) => {
    await page.waitForSelector('plugin-app');

    // Click on AI Tools tab
    const aiToolsTab = page.getByRole('button', { name: /ai tools/i });
    await aiToolsTab.click();

    // Verify the tab is now active
    await expect(aiToolsTab).toHaveClass(/active/);
  });

  test('should show empty state when no clips selected', async ({ page }) => {
    await page.waitForSelector('plugin-app');

    // Check for empty state message
    const emptyState = page.getByText(/no clips selected/i);
    await expect(emptyState).toBeVisible();
  });

  test('should display settings panel with AI configuration', async ({ page }) => {
    await page.waitForSelector('plugin-app');

    // Navigate to settings
    const settingsTab = page.getByRole('button', { name: /settings/i });
    await settingsTab.click();

    // Check for AI configuration section
    const aiConfigSection = page.getByText(/ai service configuration/i);
    await expect(aiConfigSection).toBeVisible();
  });
});

