{
  "id": "com.adobe.ppro.samples",
  "name": "PremierePro UXP plugin Sample Project",
  "shortname": "3psample",
  "version": "1.0.0",
  "main": "index.html",
  "host": {
      "app": "premierepro",
      "minVersion": "25.1.0"
  },
  "manifestVersion": 5,
  "requiredPermissions": {
    "localFileSystem": "request",
    "clipboard": "readAndWrite"
  },
  "entrypoints": [
    {
      "id": "samplepanel",
      "type": "panel",
      "minimumSize": {
        "width": 430,
        "height": 500
      },
      "maximumSize": {
        "width": 2000,
        "height": 2000
      },
      "preferredDockedSize": {
        "width": 230,
        "height": 300
      },
      "preferredFloatingSize": {
        "width": 400,
        "height": 300
      },
      "label": {
        "default": "PremierePro UXP plugin Sample Project"
      },
      "icons": [
        {
          "width": 23,
          "height": 23,
          "path": "icons/dark.png",
          "scale": [1, 2],
          "theme": ["darkest", "dark", "medium"]
        },
        {
          "width": 23,
          "height": 23,
          "path": "icons/light.png",
          "scale": [1, 2],
          "theme": ["lightest", "light"]
        }
      ]
    }
  ],
  "icons": [
    {
      "width": 48,
      "height": 48,
      "path": "icons/plugin-icon.png",
      "scale": [1, 2],
      "theme": ["darkest", "dark", "medium", "lightest", "light", "all"],
      "species": ["pluginList"]
    }
  ]
}
