     1â†’/**
     2â†’ * Product Recommendation Engine
     3â†’ * Maps specific user flaws to high-ticket affiliate products ("Prescriptions")
     4â†’ */
     5â†’
     6â†’import { Flaw, ProductRecommendation } from '@/types/results';
     7â†’import { getProductById } from '@/lib/product_db';
     8â†’
     9â†’// Vision data from Claude Vision analysis
    10â†’interface ProductVisionData {
    11â†’    skin?: { clarity?: number };
    12â†’    hair?: { hairline_nw?: number };
    13â†’    teeth?: { status?: string; alignment?: string };
    14â†’}
    15â†’
    16â†’export function generateProductRecommendations(flaws: Flaw[], vision?: ProductVisionData): ProductRecommendation[] {
    17â†’    const recommendations: ProductRecommendation[] = [];
    18â†’    const addedProductIds = new Set<string>();
    19â†’
    20â†’    // Helper to add recommendation
    21â†’    const addRec = (productId: string, flaw: Flaw | null, message: string, urgency: 'high' | 'medium' | 'low', targetMetric?: string) => {
    22â†’        if (addedProductIds.has(productId)) return;
    23â†’
    24â†’        const product = getProductById(productId);
    25â†’        if (!product) return;
    26â†’
    27â†’        recommendations.push({
    28â†’            product,
    29â†’            state: 'flaw',
    30â†’            targetMetric: targetMetric || (flaw?.responsibleRatios[0]?.ratioId) || 'general',
    31â†’            message: message,
    32â†’            urgency,
    33â†’            matchedMetrics: flaw ? flaw.responsibleRatios.map(r => r.ratioId) : []
    34â†’        });
    35â†’        addedProductIds.add(productId);
    36â†’    };
    37â†’
    38â†’    // 1. Jawline / Masseter Issues -> Jawliner
    39â†’    const jawFlaw = flaws.find(f =>
    40â†’        f.categoryName.includes('Jaw') ||
    41â†’        f.flawName.toLowerCase().includes('jaw') ||
    42â†’        f.flawName.toLowerCase().includes('gonial')
    43â†’    );
    44â†’
    45â†’    if (jawFlaw) {
    46â†’        addRec(
    47â†’            'jawliner_medium',
    48â†’            jawFlaw,
    49â†’            "Your jawline definition is lacking masseter volume. Clinical hypertrophy training is recommended.",
    50â†’            'high'
    51â†’        );
    52â†’    }
    53â†’
    54â†’    // 2. Puffiness / Midface -> Gua Sha / Ice Roller
    55â†’    const puffyFlaw = flaws.find(f =>
    56â†’        f.categoryName.includes('Midface') ||
    57â†’        f.flawName.toLowerCase().includes('cheek') ||
    58â†’        f.flawName.toLowerCase().includes('bloat')
    59â†’    );
    60â†’
    61â†’    if (puffyFlaw) {
    62â†’        addRec(
    63â†’            'gua_sha_set',
    64â†’            puffyFlaw,
    65â†’            "Facial puffiness is masking your bone structure. Lymphatic drainage is required.",
    66â†’            'medium'
    67â†’        );
    68â†’    }
    69â†’
    70â†’    // 3. Skin Texture / Aging / Acne -> Red Light Mask
    71â†’    // Use vision data for skin clarity
    72â†’    const skinClarity = vision?.skin?.clarity;
    73â†’    const hasSkinIssues = skinClarity !== undefined && skinClarity < 7;
    74â†’    const skinFlaw = flaws.find(f =>
    75â†’        f.categoryName.includes('Skin') ||
    76â†’        f.flawName.toLowerCase().includes('texture') ||
    77â†’        f.flawName.toLowerCase().includes('wrinkle')
    78â†’    );
    79â†’
    80â†’    if (hasSkinIssues || skinFlaw) {
    81â†’        const severity = (skinClarity !== undefined && skinClarity < 4) ? 'high' : 'medium';
    82â†’        const msg = hasSkinIssues
    83â†’            ? `Vision analysis detected skin clarity issues (${skinClarity}/10). LED therapy accelerates skin repair.`
    84â†’            : "Skin texture irregularities detected. Clinical LED therapy prevents collagen degradation.";
    85â†’        addRec(
    86â†’            'red_light_mask',
    87â†’            skinFlaw || null,
    88â†’            msg,
    89â†’            severity,
    90â†’            'Skin Clarity'
    91â†’        );
    92â†’    }
    93â†’
    94â†’    // 4. Hair / Forehead -> Derma Roller
    95â†’    // Use vision data for hairline
    96â†’    const hairlineNW = vision?.hair?.hairline_nw;
    97â†’    const hairFlaw = flaws.find(f =>
    98â†’        f.categoryName.includes('Hair') ||
    99â†’        f.categoryName.includes('Upper Third') ||
   100â†’        f.flawName.toLowerCase().includes('forehead')
   101â†’    );
   102â†’
   103â†’    const hasHairlineRecession = hairlineNW !== undefined && hairlineNW >= 2;
   104â†’    if (hasHairlineRecession || hairFlaw) {
   105â†’        const msg = hasHairlineRecession
   106â†’            ? `Hairline recession (NW${hairlineNW}) detected. Microneedling combined with clinical topicals is advised.`
   107â†’            : "Upper third proportions risk being compromised by hairline density. Collagen induction therapy advised.";
   108â†’        addRec(
   109â†’            'derma_roller_15',
   110â†’            hairFlaw || null,
   111â†’            msg,
   112â†’            'high',
   113â†’            'Hairline'
   114â†’        );
   115â†’    }
   116â†’
   117â†’    // 5. Dental -> Whitening Strips (New)
   118â†’    const teethStatus = vision?.teeth?.status;
   119â†’    const teethAlignment = vision?.teeth?.alignment;
   120â†’    if (teethStatus === 'yellow' || teethAlignment === 'crowded') {
   121â†’        addRec(
   122â†’            'teeth_whitening_kit', // Assuming this exists in product_db or will be added
   123â†’            null,
   124â†’            `Dental irregularities detected (${teethStatus}). Clinical grade whitening strips will improve smile harmony.`,
   125â†’            'medium',
   126â†’            'Dental Harmony'
   127â†’        );
   128â†’    }
   129â†’
   130â†’    return recommendations;
   131â†’}
