@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Pure black background */
  --background: #000000;
  --background-secondary: #0a0a0a;
  --background-tertiary: #141414;

  /* Text colors */
  --foreground: #ffffff;
  --foreground-muted: #e5e5e5;
  --foreground-dim: #a3a3a3;

  /* Neon blue/cyan accent */
  --accent: #00f3ff;
  --accent-hover: #67f9ff;
  --accent-light: #99f6ff;
  --accent-glow: rgba(0, 243, 255, 0.4);
  --accent-glow-strong: rgba(0, 243, 255, 0.6);

  /* Neon cyan accent (alternative) */
  --accent-alt: #00f3ff;
  --accent-alt-light: #67f9ff;

  /* Primary neon blue for buttons */
  --accent-primary: #00d4e6;
  --accent-primary-hover: #00f3ff;

  /* Borders and dividers */
  --border: #30363d;
  --border-light: #3d444d;
  --border-accent: rgba(0, 243, 255, 0.3);

  /* Status colors */
  --success: #22c55e;
  --warning: #eab308;
  --error: #ef4444;
}

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

html,
body {
  max-width: 100vw;
  overflow-x: hidden;
}

body {
  color: var(--foreground-muted);
  background: var(--background);
  font-family: var(--font-geist-sans), -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Global background - pure black */

/* Headings use pure white */
h1, h2, h3, h4, h5, h6 {
  color: var(--foreground);
}

a {
  color: var(--accent);
  text-decoration: none;
  transition: color 0.2s ease;
}

a:hover {
  color: var(--accent-light);
  text-shadow: 0 0 8px var(--accent-glow);
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--background-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--border-light);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--foreground-dim);
}

/* Selection styling */
::selection {
  background: var(--accent);
  color: var(--background);
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  /* Glow effect utilities */
  .glow {
    box-shadow: 0 0 20px var(--accent-glow);
  }

  .glow-sm {
    box-shadow: 0 0 10px var(--accent-glow);
  }

  .glow-lg {
    box-shadow: 0 0 40px var(--accent-glow);
  }

  .glow-xl {
    box-shadow: 0 0 60px var(--accent-glow-strong);
  }

  /* Text glow */
  .text-glow {
    text-shadow: 0 0 10px var(--accent-glow);
  }

  .text-glow-lg {
    text-shadow: 0 0 20px var(--accent-glow), 0 0 40px var(--accent-glow);
  }

  /* Gradient text */
  .gradient-text {
    background: linear-gradient(135deg, var(--foreground) 0%, var(--accent) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Neon border */
  .border-glow {
    border-color: var(--accent);
    box-shadow: 0 0 10px var(--accent-glow), inset 0 0 10px var(--accent-glow);
  }
}

@layer components {
  /* Card component */
  .card {
    background: var(--background-secondary);
    border: 1px solid var(--border);
    border-radius: 12px;
    padding: 1.5rem;
    transition: all 0.3s ease;
  }

  .card:hover {
    border-color: var(--border-accent);
  }

  /* Glass card variant */
  .card-glass {
    background: rgba(17, 17, 17, 0.8);
    backdrop-filter: blur(10px);
    border: 1px solid var(--border);
    border-radius: 12px;
    padding: 1.5rem;
  }

  /* Button base */
  .btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    border-radius: 8px;
    font-weight: 500;
    transition: all 0.2s ease;
    cursor: pointer;
    border: none;
  }

  .btn-primary {
    background: var(--accent);
    color: white;
    font-weight: 600;
  }

  .btn-primary:hover {
    background: var(--accent-hover);
    color: white;
    box-shadow: 0 0 25px var(--accent-glow-strong);
  }

  .btn-secondary {
    background: var(--background-tertiary);
    color: white;
    border: 1px solid var(--border);
  }

  .btn-secondary:hover {
    border-color: var(--accent);
    color: white;
    box-shadow: 0 0 15px var(--accent-glow);
  }

  .btn-outline {
    background: transparent;
    color: var(--accent);
    border: 1px solid var(--accent);
  }

  .btn-outline:hover {
    background: var(--accent);
    color: white;
    box-shadow: 0 0 20px var(--accent-glow);
  }

  /* Input styling */
  .input {
    background: var(--background-secondary);
    border: 1px solid var(--border);
    border-radius: 8px;
    padding: 0.75rem 1rem;
    color: var(--foreground);
    transition: all 0.2s ease;
  }

  .input:focus {
    outline: none;
    border-color: var(--accent);
    box-shadow: 0 0 10px var(--accent-glow);
  }

  .input::placeholder {
    color: var(--foreground-dim);
  }
}

"use client";

import { motion } from "framer-motion";
import { useState, useEffect } from "react";
import {
  ArrowRight,
  CloudUpload,
  Target,
  FileText,
  Gauge,
  Ruler,
  CheckCircle,
  TrendingUp,
  Pencil,
  Zap,
  Trophy,
  Check,
  X,
} from "lucide-react";
import Link from "next/link";


// Animation variants
const fadeIn = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

// Rotating text words from original
const rotatingWords = ['proportions', 'symmetry', 'harmony', 'ratios', 'measurements', 'balance'];

// ============================================================================
// NAVBAR COMPONENT
// ============================================================================
function Navbar() {
  return (
    <nav className="fixed top-0 inset-x-0 z-30 transition-all duration-300 bg-black/90 backdrop-blur-sm">
      <div className="max-w-7xl mx-auto px-6">
        <div className="flex items-center justify-between h-16">
          <Link href="/" className="flex items-center gap-2 text-sm font-semibold">
            <div className="h-5 w-5 rounded bg-blue-500/20 flex items-center justify-center">
              <span className="text-blue-400 text-xs font-bold">F</span>
            </div>
            <span>LOOKSMAXX</span>
          </Link>
          <div className="hidden md:flex items-center gap-8">
            <a href="#how-it-works" className="text-sm text-gray-300 hover:text-white transition-colors">
              How it works
            </a>
            <a href="#your-plan" className="text-sm text-gray-300 hover:text-white transition-colors">
              Your Plan
            </a>
            <a href="#pricing" className="text-sm text-gray-300 hover:text-white transition-colors">
              Pricing
            </a>
            <Link
              href="/login"
              className="text-sm font-medium bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-500 transition-colors"
            >
              Get started
            </Link>
          </div>
          <div className="md:hidden">
            <Link
              href="/login"
              className="text-sm font-medium bg-blue-600 text-white px-3 py-2 rounded-lg hover:bg-blue-500 transition-colors"
            >
              Get started
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
}

// ============================================================================
// ROTATING TEXT COMPONENT
// ============================================================================
function RotatingText() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentIndex((prev) => (prev + 1) % rotatingWords.length);
        setIsAnimating(false);
      }, 300);
    }, 2500);

    return () => clearInterval(timer);
  }, []);

  return (
    <span
      className="text-blue-400 inline-block"
      style={{
        transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
        minWidth: '280px',
        opacity: isAnimating ? 0 : 1,
        transform: isAnimating ? 'translateY(10px)' : 'translateY(0)',
      }}
    >
      {rotatingWords[currentIndex]}
    </span>
  );
}

// ============================================================================
// YOUTUBE ICON COMPONENT
// ============================================================================
function YouTubeIcon({ className }: { className?: string }) {
  return (
    <svg className={className} viewBox="0 0 24 24" fill="currentColor">
      <path d="M23.498 6.186a3.016 3.016 0 0 0-2.122-2.136C19.505 3.545 12 3.545 12 3.545s-7.505 0-9.377.505A3.017 3.017 0 0 0 .502 6.186C0 8.07 0 12 0 12s0 3.93.502 5.814a3.016 3.016 0 0 0 2.122 2.136c1.871.505 9.376.505 9.376.505s7.505 0 9.377-.505a3.015 3.015 0 0 0 2.122-2.136C24 15.93 24 12 24 12s0-3.93-.502-5.814zM9.545 15.568V8.432L15.818 12l-6.273 3.568z" />
    </svg>
  );
}

// ============================================================================
// HERO SECTION COMPONENT
// ============================================================================
function HeroSection() {
  return (
    <>
      <section className="relative overflow-hidden flex items-stretch min-h-screen bg-black">
        {/* Top Gradient Overlay */}
        <div className="pointer-events-none absolute inset-x-0 -top-16 h-48 bg-gradient-to-b from-black via-black/80 to-transparent z-0 hidden lg:block" />

        {/* Bottom Gradient Overlay */}
        <div
          className="pointer-events-none absolute inset-x-0 bottom-0 h-[20vh] z-[15] hidden lg:block"
          style={{
            background:
              "linear-gradient(to bottom, transparent 0%, rgba(0,0,0,0.3) 20%, rgba(0,0,0,0.6) 50%, rgba(0,0,0,0.9) 80%, black 100%)",
          }}
        />

        <div className="max-w-7xl mx-auto w-full px-6 flex flex-col lg:flex-row items-stretch relative z-10">
          {/* Hero Text */}
          <div className="w-full lg:w-1/2 relative z-10 flex items-center py-12 sm:py-16 md:py-24 order-2 lg:order-1">
            <div className="max-w-xl w-full mx-auto lg:max-w-none lg:mx-0 text-center lg:text-left">
              {/* Social Proof - Stars and YouTube */}
              <motion.div
                variants={fadeIn}
                initial="hidden"
                animate="visible"
                transition={{ duration: 0.5 }}
                className="inline-flex flex-wrap items-center justify-center lg:justify-start gap-2 sm:gap-3 mb-4 sm:mb-6"
              >
                {/* Star Rating */}
                <div className="flex items-center gap-1.5 sm:gap-2 bg-blue-500/10 border border-blue-500/20 rounded-full px-2.5 py-1 sm:px-3 sm:py-1.5 flex-shrink-0">
