'use client';

import { useRouter } from 'next/navigation';
import { motion } from 'framer-motion';
import { useGender, Gender } from '@/contexts/GenderContext';

export default function GenderPage() {
  const router = useRouter();
  const { gender, setGender } = useGender();

  const handleSelect = (selectedGender: Gender) => {
    setGender(selectedGender);
  };

  const handleContinue = () => {
    if (gender) {
      router.push('/ethnicity');
    }
  };

  return (
    <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4 py-12">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="text-center mb-12"
      >
        <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
          Select Your Gender
        </h1>
        <p className="text-foreground-dim text-lg">
          This helps us provide accurate facial analysis
        </p>
      </motion.div>

      {/* Gender Cards */}
      <div className="flex flex-col md:flex-row gap-6 md:gap-10 mb-12">
        {/* Male Card */}
        <motion.button
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5, delay: 0.1 }}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={() => handleSelect('male')}
          className={`
            relative w-64 h-80 md:w-72 md:h-96 rounded-2xl border-2
            flex flex-col items-center justify-center gap-6
            transition-all duration-300 cursor-pointer
            ${gender === 'male'
              ? 'border-accent bg-accent/10 shadow-[0_0_40px_rgba(0,243,255,0.4),inset_0_0_40px_rgba(0,243,255,0.1)]'
              : 'border-border bg-background-secondary hover:border-border-light'
            }
          `}
        >
          {/* Male Icon */}
          <div className={`
            text-8xl transition-all duration-300
            ${gender === 'male' ? 'text-accent drop-shadow-[0_0_20px_rgba(0,243,255,0.8)]' : 'text-foreground-dim'}
          `}>
            ♂
          </div>
          <span className={`
            text-2xl font-semibold transition-all duration-300
            ${gender === 'male' ? 'text-accent text-glow' : 'text-white'}
          `}>
            Male
          </span>

          {/* Selected indicator */}
          {gender === 'male' && (
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              className="absolute top-4 right-4 w-6 h-6 rounded-full bg-accent flex items-center justify-center"
            >
              <svg className="w-4 h-4 text-black" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
              </svg>
            </motion.div>
          )}
        </motion.button>

        {/* Female Card */}
        <motion.button
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={() => handleSelect('female')}
          className={`
            relative w-64 h-80 md:w-72 md:h-96 rounded-2xl border-2
            flex flex-col items-center justify-center gap-6
            transition-all duration-300 cursor-pointer
            ${gender === 'female'
              ? 'border-accent bg-accent/10 shadow-[0_0_40px_rgba(0,243,255,0.4),inset_0_0_40px_rgba(0,243,255,0.1)]'
              : 'border-border bg-background-secondary hover:border-border-light'
            }
          `}
        >
          {/* Female Icon */}
          <div className={`
            text-8xl transition-all duration-300
            ${gender === 'female' ? 'text-accent drop-shadow-[0_0_20px_rgba(0,243,255,0.8)]' : 'text-foreground-dim'}
          `}>
            ♀
          </div>
          <span className={`
            text-2xl font-semibold transition-all duration-300
            ${gender === 'female' ? 'text-accent text-glow' : 'text-white'}
          `}>
            Female
          </span>

          {/* Selected indicator */}
          {gender === 'female' && (
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              className="absolute top-4 right-4 w-6 h-6 rounded-full bg-accent flex items-center justify-center"
            >
              <svg className="w-4 h-4 text-black" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
              </svg>
            </motion.div>
          )}
        </motion.button>
      </div>

      {/* Continue Button */}
      <motion.button
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        onClick={handleContinue}
        disabled={!gender}
        className={`
          px-12 py-4 rounded-lg font-semibold text-lg
          transition-all duration-300
          ${gender
            ? 'bg-accent text-black hover:bg-accent-hover hover:shadow-[0_0_30px_rgba(0,243,255,0.5)] cursor-pointer'
            : 'bg-background-tertiary text-foreground-dim cursor-not-allowed'
          }
        `}
      >
        Continue
      </motion.button>
    </main>
  );
}

