'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { motion, AnimatePresence } from 'framer-motion';
import { ArrowLeft, User, UserCircle, Check, BarChart3 } from 'lucide-react';
import { useUpload } from '@/contexts/UploadContext';
import { useGender } from '@/contexts/GenderContext';
import { FrontProfileLandmarks } from '@/components/FrontProfileLandmarks';
import { SideProfileManualTool } from '@/components/SideProfileManualTool';
import { ResultsDashboard } from '@/components/ResultsDashboard';
import { LandmarkPoint } from '@/lib/landmarks';

type AnalysisStep = 'front' | 'side' | 'results';

export default function AnalysisPage() {
  const router = useRouter();
  const { frontPhoto, sidePhoto } = useUpload();
  const { gender } = useGender();
  const [currentStep, setCurrentStep] = useState<AnalysisStep>('front');
  const [frontLandmarks, setFrontLandmarks] = useState<LandmarkPoint[]>([]);
  const [sideLandmarks, setSideLandmarks] = useState<LandmarkPoint[]>([]);

  // Redirect if no photos uploaded
  if (!frontPhoto && !sidePhoto) {
    return (
      <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center"
        >
          <h1 className="text-2xl font-bold text-white mb-4">No Photos Found</h1>
          <p className="text-foreground-dim mb-8">
            Please upload your photos first to begin the analysis.
          </p>
          <button
            onClick={() => router.push('/upload')}
            className="px-6 py-3 rounded-lg bg-accent text-black font-semibold hover:bg-accent-hover transition-colors"
          >
            Go to Upload
          </button>
        </motion.div>
      </main>
    );
  }

  const handleFrontComplete = (landmarks: LandmarkPoint[]) => {
    setFrontLandmarks(landmarks);
    if (sidePhoto) {
      setCurrentStep('side');
    } else {
      setCurrentStep('results');
    }
  };

  const handleSideComplete = (landmarks: LandmarkPoint[]) => {
    setSideLandmarks(landmarks);
    setCurrentStep('results');
  };

  const handleBack = () => {
    if (currentStep === 'side') {
      setCurrentStep('front');
    } else if (currentStep === 'results') {
      setCurrentStep(sidePhoto ? 'side' : 'front');
    } else {
      router.push('/upload');
    }
  };

  const stepConfig = {
    front: {
      title: 'Front Profile Analysis',
      number: 1,
      icon: User,
    },
    side: {
      title: 'Side Profile Analysis',
      number: 2,
      icon: UserCircle,
    },
    results: {
      title: 'Analysis Results',
      number: 3,
      icon: BarChart3,
    },
  };

  const config = stepConfig[currentStep];

  return (
    <main className="min-h-screen bg-black flex flex-col items-center px-4 py-8">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-4xl mb-6"
      >
        <div className="flex items-center justify-between">
          <button
            onClick={handleBack}
            className="flex items-center gap-2 text-foreground-dim hover:text-white transition-colors"
          >
            <ArrowLeft className="w-5 h-5" />
            Back
          </button>

          {/* Step Indicators */}
          <div className="flex items-center gap-3">
            {/* Front Step */}
            <div className="flex items-center gap-2">
              <div
                className={`
                  w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                  transition-all duration-300
                  ${
                    currentStep === 'front'
                      ? 'bg-accent text-black shadow-[0_0_15px_rgba(0,243,255,0.5)]'
                      : frontLandmarks.length > 0
                        ? 'bg-green-500 text-white'
                        : 'bg-background-tertiary text-foreground-dim'
                  }
                `}
              >
                {frontLandmarks.length > 0 && currentStep !== 'front' ? (
                  <Check className="w-4 h-4" />
                ) : (
                  '1'
                )}
              </div>
              <span
                className={`text-sm hidden sm:block ${
                  currentStep === 'front' ? 'text-white' : 'text-foreground-dim'
                }`}
              >
                Front
              </span>
            </div>

            {/* Connector */}
            <div
              className={`w-8 h-0.5 ${
                frontLandmarks.length > 0 ? 'bg-accent' : 'bg-border'
              } transition-colors duration-300`}
            />

            {/* Side Step */}
            {sidePhoto && (
              <>
                <div className="flex items-center gap-2">
                  <div
                    className={`
                      w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                      transition-all duration-300
                      ${
                        currentStep === 'side'
                          ? 'bg-accent text-black shadow-[0_0_15px_rgba(0,243,255,0.5)]'
                          : sideLandmarks.length > 0
                            ? 'bg-green-500 text-white'
                            : 'bg-background-tertiary text-foreground-dim'
                      }
                    `}
                  >
                    {sideLandmarks.length > 0 && currentStep !== 'side' ? (
                      <Check className="w-4 h-4" />
                    ) : (
                      '2'
                    )}
                  </div>
                  <span
                    className={`text-sm hidden sm:block ${
                      currentStep === 'side' ? 'text-white' : 'text-foreground-dim'
                    }`}
                  >
                    Side
                  </span>
                </div>

                {/* Connector */}
                <div
                  className={`w-8 h-0.5 ${
                    sideLandmarks.length > 0 ? 'bg-accent' : 'bg-border'
                  } transition-colors duration-300`}
                />
              </>
            )}

            {/* Results Step */}
            <div className="flex items-center gap-2">
              <div
                className={`
                  w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                  transition-all duration-300
                  ${
                    currentStep === 'results'
                      ? 'bg-accent text-black shadow-[0_0_15px_rgba(0,243,255,0.5)]'
                      : 'bg-background-tertiary text-foreground-dim'
                  }
                `}
              >
                {sidePhoto ? '3' : '2'}
              </div>
              <span
                className={`text-sm hidden sm:block ${
                  currentStep === 'results' ? 'text-white' : 'text-foreground-dim'
                }`}
              >
                Results
              </span>
            </div>
          </div>

          {/* Current step badge */}
          <div className="px-3 py-1 rounded-full bg-accent/20 border border-accent/50">
            <span className="text-accent text-sm font-medium flex items-center gap-2">
              <config.icon className="w-4 h-4" />
              {config.title}
            </span>
          </div>
        </div>
      </motion.div>

      {/* Main Content */}
      <AnimatePresence mode="wait">
        {currentStep === 'front' && frontPhoto && (
          <motion.div
            key="front"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 20 }}
            className="w-full"
          >
            <FrontProfileLandmarks
              imageUrl={frontPhoto.preview}
              onLandmarksChange={setFrontLandmarks}
              onComplete={handleFrontComplete}
            />
          </motion.div>
        )}

        {currentStep === 'side' && sidePhoto && (
          <motion.div
            key="side"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 20 }}
            className="w-full"
          >
            <SideProfileManualTool
              imageUrl={sidePhoto.preview}
              onLandmarksChange={setSideLandmarks}
              onComplete={handleSideComplete}
            />
          </motion.div>
        )}

        {currentStep === 'results' && (
          <motion.div
            key="results"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 20 }}
            className="w-full max-w-5xl"
          >
            <ResultsDashboard
              frontLandmarks={frontLandmarks}
              sideLandmarks={sideLandmarks}
              gender={gender || 'male'}
            />

            {/* Debug Output */}
            <details className="mt-8">
              <summary className="text-foreground-dim text-sm cursor-pointer hover:text-white">
                View Raw Landmark Data (Debug)
              </summary>
              <pre className="mt-4 p-4 rounded-lg bg-background-secondary border border-border text-xs text-foreground-dim overflow-auto max-h-64">
                {JSON.stringify(
                  {
                    front: frontLandmarks.map((l) => ({
                      id: l.id,
                      x: l.x.toFixed(4),
                      y: l.y.toFixed(4),
                    })),
                    side: sideLandmarks.map((l) => ({
                      id: l.id,
                      x: l.x.toFixed(4),
                      y: l.y.toFixed(4),
                    })),
                  },
                  null,
                  2
                )}
              </pre>
            </details>
          </motion.div>
        )}
      </AnimatePresence>
    </main>
  );
}

input.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* FaceIQ-style design tokens */
:root {
  /* Background colors - Light theme */
  --background: #ffffff;
  --background-secondary: #f9fafb;
  --background-tertiary: #f3f4f6;

  /* Text colors */
  --foreground: #111827;
  --foreground-muted: #374151;
  --foreground-dim: #6b7280;
  --foreground-light: #9ca3af;

  /* Border colors */
  --border: #e5e7eb;
  --border-hover: #d1d5db;
  --border-focus: #9ca3af;

  /* Status colors */
  --success: #22c55e;
  --warning: #eab308;
  --error: #ef4444;

  /* Primary accent (grayscale buttons) */
  --primary: #111827;
  --primary-hover: #1f2937;
}

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

html,
body {
  max-width: 100vw;
  overflow-x: hidden;
}

body {
  color: var(--foreground);
  background: var(--background);
  font-family: var(--font-manrope), -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Scrollbar styling - subtle for light theme */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--background-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--border-hover);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--foreground-light);
}

/* Selection styling */
::selection {
  background: var(--primary);
  color: var(--background);
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

@layer components {
  /* FaceIQ Button - Primary (dark) */
  .btn-primary {
    @apply inline-flex items-center justify-center gap-2;
    @apply h-11 px-5 rounded-lg;
    @apply bg-gray-900 text-white text-sm font-medium;
    @apply transition-all;
    @apply hover:bg-gray-800;
    @apply focus:outline-none focus:ring-2 focus:ring-gray-900 focus:ring-offset-2;
    @apply disabled:bg-gray-200 disabled:text-gray-400 disabled:cursor-not-allowed;
  }

  /* FaceIQ Button - Secondary (outline) */
  .btn-secondary {
    @apply inline-flex items-center justify-center gap-2;
    @apply h-11 px-5 rounded-lg;
    @apply border border-gray-300 bg-white text-gray-700 text-sm font-medium;
    @apply shadow-sm transition-all;
    @apply hover:bg-gray-50 hover:border-gray-400;
    @apply focus:outline-none focus:ring-2 focus:ring-gray-900 focus:ring-offset-2;
    @apply disabled:bg-gray-50 disabled:text-gray-400 disabled:cursor-not-allowed;
  }

  /* FaceIQ Input */
  .input {
    @apply h-11 px-3.5 text-sm;
    @apply bg-white border border-gray-300 rounded-lg;
    @apply placeholder:text-gray-400;
    @apply focus:outline-none focus:border-gray-400;
    @apply disabled:bg-gray-50 disabled:text-gray-500 disabled:cursor-not-allowed;
    @apply transition-all;
  }

  /* FaceIQ Card */
  .card {
    @apply bg-white border border-gray-200 rounded-xl p-6;
    @apply transition-all;
    @apply hover:border-gray-300;
  }

  /* FaceIQ Divider with text */
  .divider-text {
    @apply relative;
  }

  .divider-text::before {
    @apply absolute inset-0 flex items-center;
    content: "";
  }

  .divider-text::before {
    @apply border-t border-gray-200;
  }
}

