toolUseResult.originalFile:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Pure black background */
  --background: #000000;
  --background-secondary: #0a0a0a;
  --background-tertiary: #141414;

  /* Text colors */
  --foreground: #ffffff;
  --foreground-muted: #e5e5e5;
  --foreground-dim: #a3a3a3;

  /* Neon blue/cyan accent */
  --accent: #00f3ff;
  --accent-hover: #67f9ff;
  --accent-light: #99f6ff;
  --accent-glow: rgba(0, 243, 255, 0.4);
  --accent-glow-strong: rgba(0, 243, 255, 0.6);

  /* Neon cyan accent (alternative) */
  --accent-alt: #00f3ff;
  --accent-alt-light: #67f9ff;

  /* Primary neon blue for buttons */
  --accent-primary: #00d4e6;
  --accent-primary-hover: #00f3ff;

  /* Borders and dividers */
  --border: #30363d;
  --border-light: #3d444d;
  --border-accent: rgba(0, 243, 255, 0.3);

  /* Status colors */
  --success: #22c55e;
  --warning: #eab308;
  --error: #ef4444;
}

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

html,
body {
  max-width: 100vw;
  overflow-x: hidden;
}

body {
  color: var(--foreground-muted);
  background: var(--background);
  font-family: var(--font-geist-sans), -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Global background - pure black */

/* Headings use pure white */
h1, h2, h3, h4, h5, h6 {
  color: var(--foreground);
}

a {
  color: var(--accent);
  text-decoration: none;
  transition: color 0.2s ease;
}

a:hover {
  color: var(--accent-light);
  text-shadow: 0 0 8px var(--accent-glow);
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--background-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--border-light);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--foreground-dim);
}

/* Selection styling */
::selection {
  background: var(--accent);
  color: var(--background);
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  /* Glow effect utilities */
  .glow {
    box-shadow: 0 0 20px var(--accent-glow);
  }

  .glow-sm {
    box-shadow: 0 0 10px var(--accent-glow);
  }

  .glow-lg {
    box-shadow: 0 0 40px var(--accent-glow);
  }

  .glow-xl {
    box-shadow: 0 0 60px var(--accent-glow-strong);
  }

  /* Text glow */
  .text-glow {
    text-shadow: 0 0 10px var(--accent-glow);
  }

  .text-glow-lg {
    text-shadow: 0 0 20px var(--accent-glow), 0 0 40px var(--accent-glow);
  }

  /* Gradient text */
  .gradient-text {
    background: linear-gradient(135deg, var(--foreground) 0%, var(--accent) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Neon border */
  .border-glow {
    border-color: var(--accent);
    box-shadow: 0 0 10px var(--accent-glow), inset 0 0 10px var(--accent-glow);
  }
}

@layer components {
  /* Card component */
  .card {
    background: var(--background-secondary);
    border: 1px solid var(--border);
    border-radius: 12px;
    padding: 1.5rem;
    transition: all 0.3s ease;
  }

  .card:hover {
    border-color: var(--border-accent);
  }

  /* Glass card variant */
  .card-glass {
    background: rgba(17, 17, 17, 0.8);
    backdrop-filter: blur(10px);
    border: 1px solid var(--border);
    border-radius: 12px;
    padding: 1.5rem;
  }

  /* Button base */
  .btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    border-radius: 8px;
    font-weight: 500;
    transition: all 0.2s ease;
    cursor: pointer;
    border: none;
  }

  .btn-primary {
    background: var(--accent);
    color: white;
    font-weight: 600;
  }

  .btn-primary:hover {
    background: var(--accent-hover);
    color: white;
    box-shadow: 0 0 25px var(--accent-glow-strong);
  }

  .btn-secondary {
    background: var(--background-tertiary);
    color: white;
    border: 1px solid var(--border);
  }

  .btn-secondary:hover {
    border-color: var(--accent);
    color: white;
    box-shadow: 0 0 15px var(--accent-glow);
  }

  .btn-outline {
    background: transparent;
    color: var(--accent);
    border: 1px solid var(--accent);
  }

  .btn-outline:hover {
    background: var(--accent);
    color: white;
    box-shadow: 0 0 20px var(--accent-glow);
  }

  /* Input styling */
  .input {
    background: var(--background-secondary);
    border: 1px solid var(--border);
    border-radius: 8px;
    padding: 0.75rem 1rem;
    color: var(--foreground);
    transition: all 0.2s ease;
  }

  .input:focus {
    outline: none;
    border-color: var(--accent);
    box-shadow: 0 0 10px var(--accent-glow);
  }

  .input::placeholder {
    color: var(--foreground-dim);
  }
}

