'use client';

import { useRouter } from 'next/navigation';
import { useGender, Gender } from '@/contexts/GenderContext';
import { Check } from 'lucide-react';

export default function GenderPage() {
  const router = useRouter();
  const { gender, setGender } = useGender();

  const handleSelect = (selectedGender: Gender) => {
    setGender(selectedGender);
  };

  const handleContinue = () => {
    if (gender) {
      router.push('/ethnicity');
    }
  };

  return (
    <main className="min-h-screen bg-white flex flex-col items-center justify-center px-4 py-12">
      {/* Back button */}
      <div className="absolute top-6 left-6">
        <button
          onClick={() => router.push('/login')}
          className="text-sm text-gray-500 hover:text-gray-900 transition-colors"
        >
          &larr; Back
        </button>
      </div>

      {/* Header */}
      <div className="text-center mb-12">
        <h1 className="text-2xl md:text-3xl font-semibold tracking-tight text-gray-900 mb-3">
          Select Your Gender
        </h1>
        <p className="text-gray-600 text-sm md:text-base">
          This helps us provide accurate facial analysis
        </p>
      </div>

      {/* Gender Cards */}
      <div className="flex flex-col sm:flex-row gap-4 sm:gap-6 mb-10">
        {/* Male Card */}
        <button
          onClick={() => handleSelect('male')}
          className={`
            relative w-48 h-56 sm:w-56 sm:h-64 rounded-xl border-2
            flex flex-col items-center justify-center gap-4
            transition-all duration-200 cursor-pointer
            ${gender === 'male'
              ? 'border-gray-900 bg-gray-50 shadow-sm'
              : 'border-gray-200 bg-white hover:border-gray-300 hover:bg-gray-50'
            }
          `}
        >
          {/* Male Icon */}
          <div className={`
            text-6xl sm:text-7xl transition-colors duration-200
            ${gender === 'male' ? 'text-gray-900' : 'text-gray-400'}
          `}>
            ♂
          </div>
          <span className={`
            text-lg font-medium transition-colors duration-200
            ${gender === 'male' ? 'text-gray-900' : 'text-gray-600'}
          `}>
            Male
          </span>

          {/* Selected indicator */}
          {gender === 'male' && (
            <div className="absolute top-3 right-3 w-6 h-6 rounded-full bg-gray-900 flex items-center justify-center">
              <Check className="w-4 h-4 text-white" strokeWidth={3} />
            </div>
          )}
        </button>

        {/* Female Card */}
        <button
          onClick={() => handleSelect('female')}
          className={`
            relative w-48 h-56 sm:w-56 sm:h-64 rounded-xl border-2
            flex flex-col items-center justify-center gap-4
            transition-all duration-200 cursor-pointer
            ${gender === 'female'
              ? 'border-gray-900 bg-gray-50 shadow-sm'
              : 'border-gray-200 bg-white hover:border-gray-300 hover:bg-gray-50'
            }
          `}
        >
          {/* Female Icon */}
          <div className={`
            text-6xl sm:text-7xl transition-colors duration-200
            ${gender === 'female' ? 'text-gray-900' : 'text-gray-400'}
          `}>
            ♀
          </div>
          <span className={`
            text-lg font-medium transition-colors duration-200
            ${gender === 'female' ? 'text-gray-900' : 'text-gray-600'}
          `}>
            Female
          </span>

          {/* Selected indicator */}
          {gender === 'female' && (
            <div className="absolute top-3 right-3 w-6 h-6 rounded-full bg-gray-900 flex items-center justify-center">
              <Check className="w-4 h-4 text-white" strokeWidth={3} />
            </div>
          )}
        </button>
      </div>

      {/* Continue Button */}
      <button
        onClick={handleContinue}
        disabled={!gender}
        className={`
          h-12 px-10 rounded-xl font-medium text-base
          transition-all duration-200
          ${gender
            ? 'bg-gray-900 text-white hover:bg-gray-800 cursor-pointer shadow-sm'
            : 'bg-gray-100 text-gray-400 cursor-not-allowed'
          }
        `}
      >
        Continue
      </button>
    </main>
  );
}

