            flex flex-col items-center justify-center gap-6
            transition-all duration-300 cursor-pointer
            ${gender === 'female'
              ? 'border-accent bg-accent/10 shadow-[0_0_40px_rgba(0,243,255,0.4),inset_0_0_40px_rgba(0,243,255,0.1)]'
              : 'border-border bg-background-secondary hover:border-border-light'
            }
          `}
        >
          {/* Female Icon */}
          <div className={`
            text-8xl transition-all duration-300
            ${gender === 'female' ? 'text-accent drop-shadow-[0_0_20px_rgba(0,243,255,0.8)]' : 'text-foreground-dim'}
          `}>
            ♀
          </div>
          <span className={`
            text-2xl font-semibold transition-all duration-300
            ${gender === 'female' ? 'text-accent text-glow' : 'text-white'}
          `}>
            Female
          </span>

          {/* Selected indicator */}
          {gender === 'female' && (
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              className="absolute top-4 right-4 w-6 h-6 rounded-full bg-accent flex items-center justify-center"
            >
              <svg className="w-4 h-4 text-black" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
              </svg>
            </motion.div>
          )}
        </motion.button>
      </div>

      {/* Continue Button */}
      <motion.button
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        onClick={handleContinue}
        disabled={!gender}
        className={`
          px-12 py-4 rounded-lg font-semibold text-lg
          transition-all duration-300
          ${gender
            ? 'bg-accent text-black hover:bg-accent-hover hover:shadow-[0_0_30px_rgba(0,243,255,0.5)] cursor-pointer'
            : 'bg-background-tertiary text-foreground-dim cursor-not-allowed'
          }
        `}
      >
        Continue
      </motion.button>
    </main>
  );
}

