        {currentStep === 'results' && (
          <motion.div
            key="results"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 20 }}
            className="w-full max-w-5xl"
          >
            <ResultsDashboard
              frontLandmarks={frontLandmarks}
              sideLandmarks={sideLandmarks}
              gender={gender || 'male'}
            />

            {/* Debug Output */}
            <details className="mt-8">
              <summary className="text-foreground-dim text-sm cursor-pointer hover:text-white">
                View Raw Landmark Data (Debug)
              </summary>
              <pre className="mt-4 p-4 rounded-lg bg-background-secondary border border-border text-xs text-foreground-dim overflow-auto max-h-64">
                {JSON.stringify(
                  {
                    front: frontLandmarks.map((l) => ({
