              {/* Social Proof - Stars and YouTube */}
              <motion.div
                variants={fadeIn}
                initial="hidden"
                animate="visible"
                transition={{ duration: 0.5 }}
                className="inline-flex flex-wrap items-center justify-center lg:justify-start gap-2 sm:gap-3 mb-4 sm:mb-6"
              >
                {/* Star Rating */}
                <div className="flex items-center gap-1.5 sm:gap-2 bg-blue-500/10 border border-blue-500/20 rounded-full px-2.5 py-1 sm:px-3 sm:py-1.5 flex-shrink-0">
                  <div className="flex">
                    {[...Array(5)].map((_, i) => (
                      <svg
                        key={i}
                        className={`w-3 h-3 sm:w-4 sm:h-4 text-yellow-400 fill-current ${i > 0 ? "-ml-0.5" : ""}`}
                        viewBox="0 0 20 20"
                      >
                        <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z" />
                      </svg>
                    ))}
                  </div>
