   336→        onLandmarksChange={setLandmarks}
   337→        onComplete={handleGuidedComplete}
   338→      />
   339→    );
   340→  }
   341→
   342→  // Auto-Detect Mode (existing implementation)
   343→  return (
   344→    <div className="w-full max-w-4xl mx-auto">
   345→      {/* Header */}
   346→      <motion.div
   347→        initial={{ opacity: 0, y: -10 }}
   348→        animate={{ opacity: 1, y: 0 }}
   349→        className="mb-6"
   350→      >
   351→        <div className="flex items-center justify-between">
   352→          <div>
   353→            <h2 className="text-xl font-semibold text-white mb-1">
   354→              Front Profile Landmarks
   355→            </h2>
   356→            <p className="text-foreground-dim text-sm">
   357→              {detectionStatus === 'success'
   358→                ? 'Landmarks auto-detected. Drag to adjust if needed.'
   359→                : 'Detecting facial landmarks...'}
   360→            </p>
   361→          </div>
   362→          <button
   363→            onClick={() => setMode('choose')}
   364→            className="px-4 py-2 rounded-lg bg-white/10 text-foreground-dim hover:bg-white/20 transition-colors text-sm"
   365→          >
   366→            Switch Mode
   367→          </button>
   368→        </div>
   369→      </motion.div>
   370→
   371→      {/* Detection Status */}
   372→      <AnimatePresence>
   373→        {(detectionStatus === 'loading' || detectionStatus === 'detecting') && (
   374→          <motion.div
   375→            initial={{ opacity: 0, y: -10 }}
   376→            animate={{ opacity: 1, y: 0 }}
   377→            exit={{ opacity: 0, y: -10 }}
   378→            className="mb-4 flex items-center justify-center gap-2 text-accent"
   379→          >
   380→            <Loader2 className="w-5 h-5 animate-spin" />
   381→            <span>
   382→              {detectionStatus === 'loading'
   383→                ? 'Loading face detection model...'
   384→                : 'Analyzing facial features...'}
   385→            </span>
   386→          </motion.div>
   387→        )}
   388→
   389→        {detectionStatus === 'error' && (
   390→          <motion.div
   391→            initial={{ opacity: 0, y: -10 }}
   392→            animate={{ opacity: 1, y: 0 }}
   393→            exit={{ opacity: 0, y: -10 }}
   394→            className="mb-4 p-4 rounded-lg bg-red-500/10 border border-red-500/30"
   395→          >
   396→            <div className="flex items-center gap-2 text-red-400 mb-2">
   397→              <AlertCircle className="w-5 h-5" />
   398→              <span className="font-medium">Detection Failed</span>
   399→            </div>
   400→            <p className="text-sm text-red-300">{errorMessage}</p>
   401→            <div className="mt-3 flex gap-3">
   402→              <button
   403→                onClick={detectLandmarks}
   404→                className="flex items-center gap-2 px-4 py-2 rounded-lg bg-red-500/20 text-red-300 hover:bg-red-500/30 transition-colors"
   405→              >
   406→                <RefreshCw className="w-4 h-4" />
   407→                Retry Detection
   408→              </button>
   409→              <button
   410→                onClick={() => setMode('guided')}
   411→                className="flex items-center gap-2 px-4 py-2 rounded-lg bg-accent/20 text-accent hover:bg-accent/30 transition-colors"
   412→              >
   413→                <Hand className="w-4 h-4" />
   414→                Try Guided Mode
   415→              </button>
   416→            </div>
   417→          </motion.div>
   418→        )}
   419→
   420→        {detectionStatus === 'success' && (
   421→          <motion.div
   422→            initial={{ opacity: 0, y: -10 }}
   423→            animate={{ opacity: 1, y: 0 }}
   424→            exit={{ opacity: 0, y: -10 }}
   425→            className="mb-4 flex items-center justify-center gap-2 text-green-400"
   426→          >
   427→            <Check className="w-5 h-5" />
   428→            <span>{landmarks.length} landmarks detected</span>
   429→            <button
   430→              onClick={detectLandmarks}
   431→              className="ml-4 flex items-center gap-1 px-3 py-1 rounded-lg bg-white/10 text-foreground-dim hover:text-white transition-colors"
   432→            >
   433→              <RefreshCw className="w-4 h-4" />
   434→              Re-detect
   435→            </button>
   436→          </motion.div>
   437→        )}
   438→      </AnimatePresence>
   439→
   440→      {/* Category Filter */}
   441→      <motion.div
   442→        initial={{ opacity: 0, y: 10 }}
   443→        animate={{ opacity: 1, y: 0 }}
   444→        className="mb-4 flex flex-wrap gap-2 justify-center"
   445→      >
   446→        <button
   447→          onClick={() => setSelectedCategory(null)}
   448→          className={`px-3 py-1.5 rounded-full text-xs font-medium transition-all ${
   449→            selectedCategory === null
   450→              ? 'bg-accent text-black'
   451→              : 'bg-white/10 text-foreground-dim hover:bg-white/20'
   452→          }`}
   453→        >
   454→          All ({landmarks.length})
   455→        </button>
   456→        {FRONT_LANDMARK_CATEGORIES.map((category) => (
   457→          <button
   458→            key={category.name}
   459→            onClick={() =>
   460→              setSelectedCategory(
   461→                selectedCategory === category.name ? null : category.name
   462→              )
   463→            }
   464→            className={`px-3 py-1.5 rounded-full text-xs font-medium transition-all flex items-center gap-1.5 ${
   465→              selectedCategory === category.name
   466→                ? 'bg-accent text-black'
   467→                : 'bg-white/10 text-foreground-dim hover:bg-white/20'
   468→            }`}
   469→          >
   470→            <span
   471→              className="w-2 h-2 rounded-full"
   472→              style={{ backgroundColor: category.color }}
   473→            />
   474→            {category.name}
   475→          </button>
   476→        ))}
   477→      </motion.div>
   478→
   479→      {/* Image Container with Landmarks */}
   480→      <motion.div
   481→        initial={{ opacity: 0, scale: 0.98 }}
   482→        animate={{ opacity: 1, scale: 1 }}
   483→        className="relative"
   484→      >
   485→        <div
   486→          ref={containerRef}
   487→          className="relative w-full aspect-[3/4] rounded-2xl overflow-hidden bg-black border border-border select-none"
   488→          onMouseMove={handleMouseMove}
   489→          onMouseUp={handleMouseUp}
   490→          onMouseLeave={handleMouseUp}
   491→          onTouchMove={handleTouchMove}
   492→          onTouchEnd={handleTouchEnd}
   493→        >
   494→          {/* User's uploaded image */}
   495→          <Image
   496→            ref={imageRef}
   497→            src={imageUrl}
   498→            alt="Front profile"
   499→            fill
   500→            className="object-contain pointer-events-none"
   501→            unoptimized
   502→            draggable={false}
   503→          />
   504→
   505→          {/* Landmark Points */}
   506→          {landmarks.map((landmark) => {
   507→            const isVisible = visibleLandmarkIds.has(landmark.id);
   508→            const color = getLandmarkColor(landmark.id, FRONT_LANDMARK_CATEGORIES);
   509→
   510→            return (
   511→              <div
   512→                key={landmark.id}
   513→                className={`
   514→                  absolute w-4 h-4 -translate-x-1/2 -translate-y-1/2 cursor-grab
   515→                  transition-opacity duration-200
   516→                  ${activeLandmark === landmark.id ? 'cursor-grabbing z-30' : 'z-10'}
   517→                  ${isVisible ? 'opacity-100' : 'opacity-20 pointer-events-none'}
   518→                `}
   519→                style={{
   520→                  left: `${landmark.x * 100}%`,
   521→                  top: `${landmark.y * 100}%`,
   522→                }}
   523→                onMouseDown={() => isVisible && handleMouseDown(landmark.id)}
   524→                onTouchStart={() => isVisible && handleTouchStart(landmark.id)}
   525→              >
   526→                {/* Outer glow */}
   527→                <div
   528→                  className={`
   529→                    absolute inset-0 rounded-full
   530→                    transition-all duration-200
   531→                    ${activeLandmark === landmark.id ? 'scale-200' : ''}
   532→                  `}
   533→                  style={{
   534→                    backgroundColor: `${color}33`,
   535→                    boxShadow:
   536→                      activeLandmark === landmark.id
   537→                        ? `0 0 20px ${color}`
   538→                        : `0 0 8px ${color}`,
   539→                  }}
   540→                />
   541→
   542→                {/* Inner dot */}
   543→                <div
   544→                  className={`
   545→                    absolute inset-1 rounded-full border-2 border-white
   546→                    transition-all duration-200
   547→                    ${activeLandmark === landmark.id ? 'scale-125' : ''}
   548→                  `}
   549→                  style={{
   550→                    backgroundColor: color,
   551→                    boxShadow: `0 0 10px ${color}`,
   552→                  }}
   553→                />
   554→
   555→                {/* Label tooltip on hover/active */}
   556→                <div
   557→                  className={`
   558→                    absolute left-5 top-1/2 -translate-y-1/2 whitespace-nowrap
   559→                    px-2 py-1 rounded bg-black/95 border
   560→                    text-xs font-medium z-50
   561→                    transition-opacity duration-200
   562→                    ${activeLandmark === landmark.id ? 'opacity-100' : 'opacity-0 pointer-events-none'}
   563→                  `}
   564→                  style={{
   565→                    borderColor: `${color}80`,
   566→                    color: color,
   567→                  }}
   568→                >
   569→                  {landmark.label}
   570→                </div>
   571→              </div>
   572→            );
   573→          })}
   574→        </div>
   575→      </motion.div>
   576→
   577→      {/* Landmark List */}
   578→      <motion.div
   579→        initial={{ opacity: 0, y: 10 }}
   580→        animate={{ opacity: 1, y: 0 }}
   581→        transition={{ delay: 0.2 }}
   582→        className="mt-6 max-h-48 overflow-y-auto rounded-xl border border-border bg-background-secondary p-3"
   583→      >
   584→        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2">
   585→          {filteredLandmarks.map((landmark) => {
   586→            const color = getLandmarkColor(landmark.id, FRONT_LANDMARK_CATEGORIES);
   587→            return (
   588→              <div
   589→                key={landmark.id}
   590→                className={`
   591→                  flex items-center gap-2 px-2 py-1.5 rounded-lg text-xs
   592→                  cursor-pointer transition-all duration-200
   593→                  ${
   594→                    activeLandmark === landmark.id
   595→                      ? 'bg-white/20'
   596→                      : 'bg-white/5 hover:bg-white/10'
   597→                  }
   598→                `}
   599→                onMouseEnter={() => setActiveLandmark(landmark.id)}
