   201→
   202→export function ResultsDashboard({
   203→  frontLandmarks,
   204→  sideLandmarks,
   205→  gender,
   206→}: ResultsDashboardProps) {
   207→  // Calculate all analyses
   208→  const frontAnalysis = useMemo(() => {
   209→    if (frontLandmarks.length === 0) return null;
   210→    return comprehensiveFrontAnalysis(frontLandmarks, gender);
   211→  }, [frontLandmarks, gender]);
   212→
   213→  const sideAnalysis = useMemo(() => {
   214→    if (sideLandmarks.length === 0) return null;
   215→    return comprehensiveSideAnalysis(sideLandmarks, gender);
   216→  }, [sideLandmarks, gender]);
   217→
   218→  // Calculate overall harmony score
   219→  const harmonyScore = useMemo(() => {
   220→    const scores: number[] = [];
   221→    if (frontAnalysis) scores.push(frontAnalysis.harmonyScore);
   222→    if (sideAnalysis) scores.push(sideAnalysis.harmonyScore);
   223→    return scores.length > 0 ? scores.reduce((a, b) => a + b, 0) / scores.length : 0;
   224→  }, [frontAnalysis, sideAnalysis]);
   225→
   226→  // Collect all individual scores
   227→  const allScores = useMemo(() => {
   228→    const scores: Record<string, number> = {};
   229→
   230→    if (frontAnalysis) {
   231→      if (frontAnalysis.fwhr) scores.fwhr = frontAnalysis.fwhr.score;
   232→      if (frontAnalysis.leftCanthalTilt) scores.canthalTilt = frontAnalysis.leftCanthalTilt.score;
   233→      if (frontAnalysis.facialThirds) scores.facialThirds = frontAnalysis.facialThirds.overall.score;
   234→      if (frontAnalysis.nasalIndex) scores.nasalIndex = frontAnalysis.nasalIndex.score;
   235→      if (frontAnalysis.mouthNoseRatio) scores.mouthNoseRatio = frontAnalysis.mouthNoseRatio.score;
   236→      if (frontAnalysis.jawRatio) scores.jawRatio = frontAnalysis.jawRatio.score;
   237→    }
   238→
   239→    if (sideAnalysis) {
   240→      if (sideAnalysis.gonialAngle) scores.gonialAngle = sideAnalysis.gonialAngle.score;
   241→      if (sideAnalysis.nasolabialAngle) scores.nasolabialAngle = sideAnalysis.nasolabialAngle.score;
   242→      if (sideAnalysis.eLine) scores.eLine = sideAnalysis.eLine.combined.score;
   243→      if (sideAnalysis.mentolabialAngle) scores.mentolabialAngle = sideAnalysis.mentolabialAngle.score;
   244→      if (sideAnalysis.nasofrontalAngle) scores.nasofrontalAngle = sideAnalysis.nasofrontalAngle.score;
   245→    }
   246→
   247→    return scores;
   248→  }, [frontAnalysis, sideAnalysis]);
   249→
   250→  // Generate bell curve data for key measurements
   251→  const bellCurveData = useMemo(() => {
   252→    const data: Array<{ key: string; title: string; data: BellCurveData; unit?: string }> = [];
   253→
   254→    if (frontAnalysis?.fwhr) {
   255→      const bellData = generateMeasurementBellCurve('fwhr', frontAnalysis.fwhr.value, gender);
   256→      if (bellData) {
   257→        data.push({ key: 'fwhr', title: 'Face Width-to-Height Ratio', data: bellData });
   258→      }
   259→    }
   260→
   261→    if (frontAnalysis?.leftCanthalTilt) {
   262→      const bellData = generateMeasurementBellCurve('canthalTilt', frontAnalysis.leftCanthalTilt.value, gender);
   263→      if (bellData) {
   264→        data.push({ key: 'canthalTilt', title: 'Canthal Tilt', data: bellData, unit: '°' });
   265→      }
   266→    }
   267→
   268→    return data;
   269→  }, [frontAnalysis, gender]);
   270→
   271→  // Determine strengths and weaknesses
   272→  const { strengths, improvements } = useMemo(() => {
   273→    const allResults: Array<{ name: string; score: number; description: string; suggestion: string }> = [];
   274→
   275→    const measurementDescriptions: Record<string, { name: string; desc: string; suggestion: string }> = {
   276→      fwhr: {
   277→        name: 'Facial Width-to-Height Ratio',
   278→        desc: 'Your face has good proportional width relative to height',
   279→        suggestion: 'Consider facial exercises to enhance jaw definition'
   280→      },
   281→      canthalTilt: {
   282→        name: 'Eye Canthal Tilt',
   283→        desc: 'Your eyes have an attractive upward tilt',
   284→        suggestion: 'Eye-lifting treatments or makeup techniques can enhance tilt'
   285→      },
   286→      facialThirds: {
   287→        name: 'Facial Thirds Balance',
   288→        desc: 'Your face has well-balanced vertical proportions',
   289→        suggestion: 'Hairstyle adjustments can help balance facial thirds'
   290→      },
   291→      nasalIndex: {
   292→        name: 'Nasal Proportions',
   293→        desc: 'Your nose width-to-height ratio is harmonious',
   294→        suggestion: 'Contouring can create the illusion of different proportions'
   295→      },
   296→      gonialAngle: {
   297→        name: 'Jaw Angle',
   298→        desc: 'Your jaw has an attractive angular definition',
   299→        suggestion: 'Jaw exercises or mewing can help define the gonial angle'
   300→      },
   301→      nasolabialAngle: {
   302→        name: 'Nose-Lip Angle',
   303→        desc: 'The angle between your nose and lip is well-balanced',
   304→        suggestion: 'Nose profile can be enhanced with non-surgical treatments'
   305→      },
   306→      eLine: {
   307→        name: 'E-Line Profile',
   308→        desc: 'Your lip position relative to nose-chin line is ideal',
   309→        suggestion: 'Lip fillers or chin augmentation can improve E-line balance'
   310→      },
   311→      mouthNoseRatio: {
   312→        name: 'Mouth-to-Nose Ratio',
   313→        desc: 'Your mouth width is proportional to your nose',
   314→        suggestion: 'Lip treatments can adjust the perceived ratio'
   315→      },
   316→      jawRatio: {
   317→        name: 'Jaw-to-Face Width',
   318→        desc: 'Your jaw width is proportional to your face',
   319→        suggestion: 'Jaw slimming or enhancement can adjust this ratio'
   320→      },
   321→    };
   322→
   323→    Object.entries(allScores).forEach(([key, score]) => {
   324→      const info = measurementDescriptions[key];
   325→      if (info) {
   326→        allResults.push({
   327→          name: info.name,
   328→          score,
   329→          description: info.desc,
   330→          suggestion: info.suggestion,
   331→        });
   332→      }
   333→    });
   334→
   335→    // Sort by score
   336→    allResults.sort((a, b) => b.score - a.score);
   337→
   338→    // Top 3 are strengths, bottom 3 are improvements
   339→    const strengths = allResults.slice(0, 3).map(r => ({
   340→      name: r.name,
   341→      score: r.score,
   342→      description: r.description,
   343→    }));
   344→
   345→    const improvements = allResults.slice(-3).reverse().map(r => ({
   346→      name: r.name,
   347→      score: r.score,
   348→      suggestion: r.suggestion,
   349→    }));
   350→
   351→    return { strengths, improvements };
   352→  }, [allScores]);
   353→
   354→  if (!frontAnalysis && !sideAnalysis) {
   355→    return (
   356→      <div className="text-center py-12">
   357→        <Activity className="w-12 h-12 text-foreground-dim mx-auto mb-4" />
   358→        <p className="text-foreground-dim">No analysis data available</p>
   359→      </div>
   360→    );
