          <h3 className="text-lg font-semibold text-white mb-4">
            Facial Thirds Breakdown
          </h3>
          <div className="grid grid-cols-3 gap-4">
            <div className="text-center">
              <div className="h-20 bg-gradient-to-b from-accent/30 to-transparent rounded-t-lg mb-2 flex items-end justify-center pb-2">
                <span className="text-2xl font-bold text-white">
                  {frontAnalysis.facialThirds.upper.value.toFixed(1)}%
                </span>
              </div>
              <p className="text-xs text-foreground-dim">Upper Third</p>
              <p className="text-xs text-accent">Hairline to Brow</p>
            </div>
            <div className="text-center">
              <div className="h-20 bg-gradient-to-b from-cyan-500/30 to-transparent rounded-t-lg mb-2 flex items-end justify-center pb-2">
                <span className="text-2xl font-bold text-white">
                  {frontAnalysis.facialThirds.middle.value.toFixed(1)}%
                </span>
              </div>
              <p className="text-xs text-foreground-dim">Middle Third</p>
              <p className="text-xs text-cyan-400">Brow to Nose Base</p>
            </div>
            <div className="text-center">
              <div className="h-20 bg-gradient-to-b from-emerald-500/30 to-transparent rounded-t-lg mb-2 flex items-end justify-center pb-2">
                <span className="text-2xl font-bold text-white">
                  {frontAnalysis.facialThirds.lower.value.toFixed(1)}%
                </span>
              </div>
              <p className="text-xs text-foreground-dim">Lower Third</p>
              <p className="text-xs text-emerald-400">Nose Base to Chin</p>
            </div>
          </div>
          <div className="mt-4 text-center">
            <p className="text-sm text-foreground-dim">
              Ideal distribution: 33.3% each • Your balance score:{' '}
              <span className="text-white font-medium">
                {frontAnalysis.facialThirds.overall.score.toFixed(0)}/100
              </span>
            </p>
          </div>
        </motion.div>
      )}
    </div>
  );
}

