   273→    }
   274→  };
   275→
   276→  return (
   277→    <div className="w-full max-w-4xl mx-auto">
   278→      {/* Header */}
   279→      <motion.div
   280→        initial={{ opacity: 0, y: -10 }}
   281→        animate={{ opacity: 1, y: 0 }}
   282→        className="mb-6 text-center"
   283→      >
   284→        <h2 className="text-xl font-semibold text-white mb-2">
   285→          Side Profile Manual Adjustment
   286→        </h2>
   287→        <p className="text-foreground-dim">
   288→          Drag each marker to match the facial landmarks on your profile photo
   289→        </p>
   290→      </motion.div>
   291→
   292→      {/* Mode Toggle and Controls */}
