   315→          {isDetecting ? 'Detecting...' : faceApiLoading ? 'Loading AI...' : 'Auto-Detect'}
   316→        </button>
   317→
   318→        <div className="w-px h-8 bg-border" />
   319→
   320→        <button
   321→          onClick={() => {
   322→            setIsGuided(true);
   323→            setSelectedCategory(null);
   324→          }}
   325→          className={`px-4 py-2 rounded-lg text-sm font-medium transition-all ${
   326→            isGuided
