   367→          </button>
   368→        </div>
   369→
   370→        <div className="w-px h-8 bg-border" />
   371→
   372→        <button
   373→          onClick={resetLandmarks}
   374→          className="px-4 py-2 rounded-lg text-sm font-medium bg-white/10 text-foreground-dim hover:bg-white/20 transition-all flex items-center gap-2"
   375→        >
   376→          <RotateCcw className="w-4 h-4" />
   377→          Reset
   378→        </button>
   379→      </motion.div>
   380→
   381→      {/* Detection Error/Status */}
   382→      <AnimatePresence>
   383→        {(detectionError || faceApiError) && (
   384→          <motion.div
   385→            initial={{ opacity: 0, y: -10 }}
   386→            animate={{ opacity: 1, y: 0 }}
   387→            exit={{ opacity: 0, y: -10 }}
   388→            className="mb-4 p-3 rounded-lg bg-red-500/10 border border-red-500/30 text-red-400 text-sm text-center"
   389→          >
   390→            {detectionError || faceApiError?.message}
   391→          </motion.div>
   392→        )}
   393→      </AnimatePresence>
   394→
   395→      {/* Guided Mode Step Indicator */}
   396→      {isGuided && currentCategory && (
   397→        <motion.div
   398→          key={currentStepIndex}
   399→          initial={{ opacity: 0, x: 20 }}
   400→          animate={{ opacity: 1, x: 0 }}
   401→          className="mb-4"
   402→        >
   403→          <div className="flex items-center justify-between mb-2">
   404→            <button
   405→              onClick={prevStep}
   406→              disabled={currentStepIndex === 0}
   407→              className={`p-2 rounded-lg transition-all ${
   408→                currentStepIndex === 0
   409→                  ? 'text-foreground-dim/30 cursor-not-allowed'
   410→                  : 'text-foreground-dim hover:text-white hover:bg-white/10'
   411→              }`}
   412→            >
   413→              <ChevronLeft className="w-5 h-5" />
   414→            </button>
   415→
   416→            <div className="text-center">
   417→              <div className="flex items-center justify-center gap-2 mb-1">
   418→                <span
   419→                  className="w-3 h-3 rounded-full"
   420→                  style={{ backgroundColor: currentCategory.color }}
   421→                />
   422→                <span className="text-white font-medium">{currentCategory.name}</span>
   423→              </div>
   424→              <span className="text-xs text-foreground-dim">
   425→                Step {currentStepIndex + 1} of {SIDE_LANDMARK_CATEGORIES.length} •{' '}
   426→                {currentCategory.landmarks.length} points
   427→              </span>
   428→            </div>
   429→
   430→            <button
   431→              onClick={nextStep}
   432→              disabled={currentStepIndex === SIDE_LANDMARK_CATEGORIES.length - 1}
   433→              className={`p-2 rounded-lg transition-all ${
   434→                currentStepIndex === SIDE_LANDMARK_CATEGORIES.length - 1
   435→                  ? 'text-foreground-dim/30 cursor-not-allowed'
   436→                  : 'text-foreground-dim hover:text-white hover:bg-white/10'
   437→              }`}
   438→            >
   439→              <ChevronRight className="w-5 h-5" />
   440→            </button>
   441→          </div>
   442→
   443→          {/* Progress bar */}
   444→          <div className="w-full h-1 bg-white/10 rounded-full overflow-hidden">
   445→            <motion.div
   446→              className="h-full rounded-full"
   447→              style={{ backgroundColor: currentCategory.color }}
   448→              initial={{ width: 0 }}
   449→              animate={{
   450→                width: `${((currentStepIndex + 1) / SIDE_LANDMARK_CATEGORIES.length) * 100}%`,
   451→              }}
   452→              transition={{ duration: 0.3 }}
   453→            />
   454→          </div>
   455→        </motion.div>
   456→      )}
   457→
   458→      {/* Category Filter (Free Edit Mode) */}
   459→      {!isGuided && (
   460→        <motion.div
   461→          initial={{ opacity: 0, y: 10 }}
   462→          animate={{ opacity: 1, y: 0 }}
   463→          className="mb-4 flex flex-wrap gap-2 justify-center"
   464→        >
   465→          <button
   466→            onClick={() => setSelectedCategory(null)}
   467→            className={`px-3 py-1.5 rounded-full text-xs font-medium transition-all ${
   468→              selectedCategory === null
   469→                ? 'bg-accent text-black'
   470→                : 'bg-white/10 text-foreground-dim hover:bg-white/20'
   471→            }`}
   472→          >
   473→            All ({landmarks.length})
   474→          </button>
   475→          {SIDE_LANDMARK_CATEGORIES.map((category) => (
   476→            <button
   477→              key={category.name}
   478→              onClick={() =>
   479→                setSelectedCategory(
   480→                  selectedCategory === category.name ? null : category.name
   481→                )
   482→              }
   483→              className={`px-3 py-1.5 rounded-full text-xs font-medium transition-all flex items-center gap-1.5 ${
   484→                selectedCategory === category.name
   485→                  ? 'bg-accent text-black'
   486→                  : 'bg-white/10 text-foreground-dim hover:bg-white/20'
   487→              }`}
   488→            >
   489→              <span
   490→                className="w-2 h-2 rounded-full"
   491→                style={{ backgroundColor: category.color }}
   492→              />
   493→              {category.name}
   494→            </button>
   495→          ))}
   496→        </motion.div>
   497→      )}
   498→
   499→      {/* Image Container with Landmarks */}
   500→      <motion.div
   501→        initial={{ opacity: 0, scale: 0.98 }}
   502→        animate={{ opacity: 1, scale: 1 }}
   503→        className="relative"
   504→      >
   505→        <div
   506→          ref={containerRef}
   507→          className="relative w-full aspect-[3/4] rounded-2xl overflow-hidden bg-black border border-border select-none"
   508→          onMouseMove={handleMouseMove}
   509→          onMouseUp={handleMouseUp}
   510→          onMouseLeave={handleMouseUp}
   511→          onTouchMove={handleTouchMove}
   512→          onTouchEnd={handleTouchEnd}
   513→        >
   514→          {/* User's uploaded image with zoom */}
   515→          <div
   516→            style={{
   517→              transform: `scale(${zoom})`,
   518→              transformOrigin: 'center center',
   519→            }}
   520→            className="absolute inset-0 transition-transform duration-200"
   521→          >
   522→            <Image
   523→              ref={imageRef as React.RefObject<HTMLImageElement>}
   524→              src={imageUrl}
   525→              alt="Side profile"
   526→              fill
   527→              className="object-contain pointer-events-none"
   528→              unoptimized
   529→              draggable={false}
   530→              onLoadingComplete={(img) => {
   531→                // Store reference for face detection
   532→                if (imageRef.current !== img) {
   533→                  (imageRef as React.MutableRefObject<HTMLImageElement>).current = img;
   534→                }
   535→              }}
   536→            />
   537→          </div>
   538→
   539→          {/* Connection lines between sequential landmarks in category */}
   540→          {isGuided && currentCategory && (
   541→            <svg
   542→              className="absolute inset-0 w-full h-full pointer-events-none"
   543→              viewBox="0 0 100 100"
   544→              preserveAspectRatio="none"
   545→            >
   546→              {currentCategory.landmarks.map((landmarkId, index) => {
   547→                if (index === currentCategory.landmarks.length - 1) return null;
   548→                const current = landmarks.find((lm) => lm.id === landmarkId);
   549→                const next = landmarks.find(
   550→                  (lm) => lm.id === currentCategory.landmarks[index + 1]
   551→                );
   552→                if (!current || !next) return null;
   553→
   554→                return (
   555→                  <line
   556→                    key={`line-${landmarkId}`}
   557→                    x1={current.x * 100}
   558→                    y1={current.y * 100}
   559→                    x2={next.x * 100}
   560→                    y2={next.y * 100}
   561→                    stroke={`${currentCategory.color}66`}
   562→                    strokeWidth="0.3"
   563→                    strokeDasharray="1,1"
   564→                  />
   565→                );
   566→              })}
   567→            </svg>
   568→          )}
   569→
   570→          {/* Landmark Points */}
   571→          {landmarks.map((landmark) => {
   572→            const isVisible = visibleLandmarkIds.has(landmark.id);
   573→            const color = getLandmarkColor(landmark.id, SIDE_LANDMARK_CATEGORIES);
   574→
   575→            return (
   576→              <div
   577→                key={landmark.id}
   578→                className={`
   579→                  absolute w-5 h-5 -translate-x-1/2 -translate-y-1/2 cursor-grab
   580→                  transition-all duration-200
   581→                  ${activeLandmark === landmark.id ? 'cursor-grabbing z-30' : 'z-10'}
   582→                  ${isVisible ? 'opacity-100 scale-100' : 'opacity-10 scale-75 pointer-events-none'}
   583→                `}
   584→                style={{
   585→                  left: `${landmark.x * 100}%`,
   586→                  top: `${landmark.y * 100}%`,
   587→                }}
   588→                onMouseDown={() => isVisible && handleMouseDown(landmark.id)}
   589→                onTouchStart={() => isVisible && handleTouchStart(landmark.id)}
   590→              >
   591→                {/* Outer glow ring */}
   592→                <div
   593→                  className={`
   594→                    absolute inset-0 rounded-full
   595→                    transition-all duration-200
   596→                    ${activeLandmark === landmark.id ? 'scale-150' : ''}
   597→                  `}
   598→                  style={{
   599→                    backgroundColor: `${color}33`,
   600→                    boxShadow:
   601→                      activeLandmark === landmark.id
   602→                        ? `0 0 25px ${color}`
   603→                        : `0 0 10px ${color}`,
   604→                  }}
   605→                />
   606→
   607→                {/* Main handle */}
   608→                <div
   609→                  className={`
   610→                    absolute inset-1 rounded-full border-2 border-white
   611→                    transition-all duration-200
   612→                    ${activeLandmark === landmark.id ? 'scale-125' : ''}
   613→                  `}
   614→                  style={{
   615→                    backgroundColor: color,
   616→                    boxShadow: `0 0 10px ${color}, 0 0 20px ${color}`,
   617→                  }}
   618→                />
   619→
   620→                {/* Label tooltip */}
   621→                <div
   622→                  className={`
   623→                    absolute left-6 top-1/2 -translate-y-1/2 whitespace-nowrap
   624→                    px-2 py-1 rounded bg-black/95 border
   625→                    text-xs font-medium z-50
   626→                    pointer-events-none transition-opacity duration-200
   627→                    ${activeLandmark === landmark.id ? 'opacity-100' : 'opacity-0'}
   628→                  `}
   629→                  style={{
   630→                    borderColor: `${color}80`,
   631→                    color: color,
   632→                  }}
   633→                >
   634→                  <div>{landmark.label}</div>
   635→                  <div className="text-[10px] opacity-70">{landmark.medicalTerm}</div>
   636→                </div>
   637→              </div>
   638→            );
   639→          })}
   640→        </div>
   641→      </motion.div>
   642→
   643→      {/* Landmark Legend for Current Category */}
   644→      <motion.div
   645→        initial={{ opacity: 0, y: 10 }}
   646→        animate={{ opacity: 1, y: 0 }}
   647→        transition={{ delay: 0.2 }}
   648→        className="mt-6 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2"
   649→      >
   650→        {filteredLandmarks.map((landmark) => {
   651→          const color = getLandmarkColor(landmark.id, SIDE_LANDMARK_CATEGORIES);
   652→          return (
   653→            <div
   654→              key={landmark.id}
   655→              className={`
   656→                flex items-center gap-2 px-3 py-2 rounded-lg
   657→                border transition-all duration-200 cursor-pointer
   658→                ${
   659→                  activeLandmark === landmark.id
   660→                    ? 'border-white/50 bg-white/20'
   661→                    : 'border-border bg-background-secondary hover:bg-white/10'
   662→                }
   663→              `}
   664→              onMouseEnter={() => setActiveLandmark(landmark.id)}
   665→              onMouseLeave={() => setActiveLandmark(null)}
   666→            >
   667→              <div
   668→                className="w-3 h-3 rounded-full flex-shrink-0"
   669→                style={{
   670→                  backgroundColor: color,
   671→                  boxShadow: `0 0 8px ${color}`,
   672→                }}
   673→              />
   674→              <div className="min-w-0">
   675→                <p className="text-sm font-medium text-white truncate">
   676→                  {landmark.label}
   677→                </p>
   678→                <p className="text-xs text-foreground-dim truncate">
   679→                  {landmark.description}
   680→                </p>
   681→              </div>
   682→            </div>
   683→          );
   684→        })}
   685→      </motion.div>
   686→
   687→      {/* Navigation/Complete Buttons */}
   688→      <motion.div
   689→        initial={{ opacity: 0, y: 10 }}
   690→        animate={{ opacity: 1, y: 0 }}
   691→        transition={{ delay: 0.3 }}
   692→        className="mt-8 flex justify-center gap-4"
   693→      >
   694→        {isGuided && currentStepIndex < SIDE_LANDMARK_CATEGORIES.length - 1 ? (
   695→          <button
   696→            onClick={nextStep}
   697→            className="
   698→              px-8 py-4 rounded-lg font-semibold text-lg
   699→              bg-accent text-black
   700→              hover:shadow-[0_0_30px_rgba(0,243,255,0.5)]
   701→              transition-all duration-300
   702→            "
   703→          >
   704→            Next: {SIDE_LANDMARK_CATEGORIES[currentStepIndex + 1]?.name}
   705→          </button>
   706→        ) : (
   707→          <button
   708→            onClick={handleComplete}
   709→            className="
   710→              px-8 py-4 rounded-lg font-semibold text-lg
   711→              bg-accent text-black
   712→              hover:shadow-[0_0_30px_rgba(0,243,255,0.5)]
   713→              transition-all duration-300
   714→            "
   715→          >
   716→            Confirm Side Landmarks
   717→          </button>
   718→        )}
   719→      </motion.div>
   720→
   721→      {/* Total Progress */}
   722→      <motion.div
   723→        initial={{ opacity: 0 }}
