'use client';

import { useMemo } from 'react';
import { motion } from 'framer-motion';
import {
  TrendingUp,
  TrendingDown,
  Minus,
  Star,
  Target,
  Activity,
  Sparkles,
} from 'lucide-react';
import { LandmarkPoint } from '@/lib/landmarks';
import {
  comprehensiveFrontAnalysis,
  comprehensiveSideAnalysis,
  generateMeasurementBellCurve,
  BellCurveData,
} from '@/lib/scoring';
import {
  BellCurveChart,
  HarmonyScoreDisplay,
} from './BellCurveChart';

interface ResultsDashboardProps {
  frontLandmarks: LandmarkPoint[];
  sideLandmarks: LandmarkPoint[];
  gender: 'male' | 'female';
}

interface MeasurementCardProps {
  title: string;
  value: number;
  unit: string;
  score: number;
  idealRange: { min: number; max: number };
  description: string;
  rating: 'excellent' | 'good' | 'average' | 'below_average';
}

function MeasurementCard({
  title,
  value,
  unit,
  score,
  idealRange,
  description,
  rating,
}: MeasurementCardProps) {
  const ratingConfig = {
    excellent: { color: '#10b981', label: 'Excellent', icon: Star },
    good: { color: '#0891b2', label: 'Good', icon: TrendingUp },
    average: { color: '#eab308', label: 'Average', icon: Minus },
    below_average: { color: '#f97316', label: 'Below Avg', icon: TrendingDown },
  };

  const config = ratingConfig[rating];
  const Icon = config.icon;

  const isInRange = value >= idealRange.min && value <= idealRange.max;

  return (
    <motion.div
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-white rounded-xl border border-gray-200 p-4 hover:border-gray-300 transition-colors"
    >
      <div className="flex items-start justify-between mb-3">
        <div>
          <h4 className="text-sm font-semibold text-gray-900">{title}</h4>
          <p className="text-xs text-gray-600 mt-0.5">{description}</p>
        </div>
        <div
          className="flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium"
          style={{
            backgroundColor: `${config.color}15`,
            color: config.color,
          }}
        >
          <Icon className="w-3 h-3" />
          {config.label}
        </div>
      </div>

      <div className="flex items-end justify-between">
        <div>
          <span className="text-2xl font-bold text-gray-900">
            {typeof value === 'number' ? value.toFixed(2) : value}
          </span>
          <span className="text-sm text-gray-600 ml-1">{unit}</span>
        </div>
        <div className="text-right">
          <div className="text-xs text-gray-600 mb-1">
            Ideal: {idealRange.min.toFixed(1)} - {idealRange.max.toFixed(1)}
          </div>
          <div
            className="text-xs font-medium"
            style={{ color: isInRange ? '#10b981' : '#f97316' }}
          >
            {isInRange ? 'In Range' : value < idealRange.min ? 'Below Ideal' : 'Above Ideal'}
          </div>
        </div>
      </div>

      {/* Score bar */}
      <div className="mt-3 h-1.5 bg-gray-100 rounded-full overflow-hidden">
        <motion.div
          className="h-full rounded-full"
          style={{ backgroundColor: config.color }}
          initial={{ width: 0 }}
          animate={{ width: `${Math.min(score, 100)}%` }}
          transition={{ duration: 0.8 }}
        />
      </div>
      <div className="mt-1 text-right">
        <span className="text-xs font-medium" style={{ color: config.color }}>
          Score: {score.toFixed(0)}/100
        </span>
      </div>
    </motion.div>
  );
}

interface StrengthWeaknessProps {
  strengths: Array<{ name: string; score: number; description: string }>;
  improvements: Array<{ name: string; score: number; suggestion: string }>;
}

function StrengthsWeaknesses({ strengths, improvements }: StrengthWeaknessProps) {
  return (
    <div className="grid md:grid-cols-2 gap-6">
      {/* Strengths */}
      <motion.div
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        className="bg-white rounded-xl border border-gray-200 p-5"
      >
        <div className="flex items-center gap-2 mb-4">
          <div className="p-2 rounded-lg bg-green-100">
            <Sparkles className="w-5 h-5 text-green-600" />
          </div>
          <h3 className="text-lg font-semibold text-gray-900">Your Strengths</h3>
        </div>
        <div className="space-y-3">
          {strengths.map((strength, index) => (
            <div
              key={index}
              className="flex items-start gap-3 p-3 rounded-lg bg-green-50 border border-green-100"
            >
              <div className="w-8 h-8 rounded-full bg-green-100 flex items-center justify-center flex-shrink-0">
                <span className="text-sm font-bold text-green-600">
                  {strength.score.toFixed(0)}
                </span>
              </div>
              <div>
                <p className="text-sm font-medium text-gray-900">{strength.name}</p>
                <p className="text-xs text-gray-600 mt-0.5">
                  {strength.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </motion.div>

      {/* Areas for Improvement */}
      <motion.div
        initial={{ opacity: 0, x: 20 }}
        animate={{ opacity: 1, x: 0 }}
        className="bg-white rounded-xl border border-gray-200 p-5"
      >
        <div className="flex items-center gap-2 mb-4">
          <div className="p-2 rounded-lg bg-amber-100">
            <Target className="w-5 h-5 text-amber-600" />
          </div>
          <h3 className="text-lg font-semibold text-gray-900">Areas for Improvement</h3>
        </div>
        <div className="space-y-3">
          {improvements.map((item, index) => (
            <div
              key={index}
              className="flex items-start gap-3 p-3 rounded-lg bg-amber-50 border border-amber-100"
            >
              <div className="w-8 h-8 rounded-full bg-amber-100 flex items-center justify-center flex-shrink-0">
                <span className="text-sm font-bold text-amber-600">
                  {item.score.toFixed(0)}
                </span>
              </div>
              <div>
                <p className="text-sm font-medium text-gray-900">{item.name}</p>
