export default function LoginPage() {
  const router = useRouter();
  const [referralCode, setReferralCode] = useState("");
  const [isApplying, setIsApplying] = useState(false);

  const handleApplyCode = () => {
    if (!referralCode.trim()) return;
    setIsApplying(true);
    setTimeout(() => {
      setIsApplying(false);
    }, 1000);
  };

  const handleGoogleSignIn = () => {
    // TODO: Implement Google OAuth
    router.push("/gender");
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-white px-4">
      <div className="w-full max-w-sm">
        {/* Logo and Header */}
        <div className="mb-10">
          <div className="flex justify-center mb-6">
            {/* Logo - 32x32 like FaceIQ */}
            <div className="h-8 w-8 rounded bg-gray-900 flex items-center justify-center">
              <span className="text-white text-sm font-bold">L</span>
            </div>
          </div>
          <h1 className="text-2xl font-semibold tracking-tight text-center text-gray-900 mb-2">
            Sign in to LOOKSMAXX
          </h1>
          <p className="text-sm text-gray-600 text-center">
            Continue with your Google account
          </p>
        </div>

        {/* Sign In Options */}
        <div className="space-y-4">
          {/* Google Sign In Button - FaceIQ style */}
          <button
            onClick={handleGoogleSignIn}
            className="w-full h-11 inline-flex items-center justify-center gap-3 rounded-lg border border-gray-300 bg-white px-4 text-sm font-medium text-gray-700 shadow-sm transition-all hover:bg-gray-50 hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-900 focus:ring-offset-2"
          >
            <GoogleIcon className="w-5 h-5" />
            Continue with Google
          </button>

          {/* Terms and Privacy */}
          <p className="text-xs text-center text-gray-500">
            By signing in, you agree to our{" "}
            <Link
              href="/terms"
              target="_blank"
              className="text-gray-700 hover:text-gray-900 underline underline-offset-2"
            >
              Terms of Service
            </Link>{" "}
            and{" "}
            <Link
              href="/privacy"
              target="_blank"
              className="text-gray-700 hover:text-gray-900 underline underline-offset-2"
            >
              Privacy Policy
            </Link>
          </p>

          {/* Divider with text */}
          <div className="relative">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-t border-gray-200"></div>
            </div>
            <div className="relative flex justify-center text-xs">
              <span className="bg-white px-2 text-gray-500">
                Have a referral code?
              </span>
            </div>
          </div>

          {/* Referral Code Input */}
          <div className="space-y-3">
            <div className="flex gap-2">
              <input
                type="text"
                placeholder="REFERRAL-CODE"
                value={referralCode}
                onChange={(e) => setReferralCode(e.target.value.toUpperCase())}
                className="flex-1 h-11 px-3.5 text-sm bg-white border border-gray-300 rounded-lg placeholder:text-gray-400 focus:outline-none focus:border-gray-400 disabled:bg-gray-50 disabled:text-gray-500 disabled:cursor-not-allowed transition-all"
              />
              <button
                onClick={handleApplyCode}
                disabled={!referralCode.trim() || isApplying}
                className="h-11 px-5 bg-gray-900 text-white rounded-lg text-sm font-medium transition-all hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-gray-900 focus:ring-offset-2 disabled:bg-gray-200 disabled:text-gray-400 disabled:cursor-not-allowed"
              >
                {isApplying ? "..." : "Apply"}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

