'use client';

import { useState, useCallback } from 'react';
import { useRouter } from 'next/navigation';
import Image from 'next/image';
import { useDropzone } from 'react-dropzone';
import { Upload, X, Image as ImageIcon, ArrowRight, ArrowLeft, User, UserCircle, Check } from 'lucide-react';
import { useUpload } from '@/contexts/UploadContext';

type UploadStep = 'front' | 'side';

export default function UploadPage() {
  const router = useRouter();
  const { frontPhoto, sidePhoto, setFrontPhoto, setSidePhoto } = useUpload();
  const [currentStep, setCurrentStep] = useState<UploadStep>('front');

  const currentPhoto = currentStep === 'front' ? frontPhoto : sidePhoto;
  const setCurrentPhoto = currentStep === 'front' ? setFrontPhoto : setSidePhoto;

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const selectedFile = acceptedFiles[0];
    if (selectedFile) {
      const objectUrl = URL.createObjectURL(selectedFile);
      setCurrentPhoto({ file: selectedFile, preview: objectUrl });
    }
  }, [setCurrentPhoto]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.webp']
    },
    maxFiles: 1,
    multiple: false
  });

  const removeImage = () => {
    if (currentPhoto?.preview) {
      URL.revokeObjectURL(currentPhoto.preview);
    }
    setCurrentPhoto(null);
  };

  const handleNext = () => {
    if (currentStep === 'front' && frontPhoto) {
      setCurrentStep('side');
    } else if (currentStep === 'side' && sidePhoto) {
      router.push('/analysis');
    }
  };

  const handleBack = () => {
    if (currentStep === 'side') {
      setCurrentStep('front');
    } else {
      router.push('/ethnicity');
    }
  };

  const stepConfig = {
    front: {
      title: 'Upload Front-Facing Photo',
      subtitle: 'Upload a clear, front-facing photo looking straight at the camera',
      icon: User,
    },
    side: {
      title: 'Upload Side Profile Photo',
      subtitle: 'Upload a clear side profile photo showing your facial structure',
      icon: UserCircle,
    },
  };

  const config = stepConfig[currentStep];
  const StepIcon = config.icon;

  return (
    <main className="min-h-screen bg-white flex flex-col items-center justify-center px-4 py-12">
      {/* Back button */}
      <div className="absolute top-6 left-6">
        <button
          onClick={handleBack}
          className="text-sm text-gray-500 hover:text-gray-900 transition-colors flex items-center gap-1"
        >
          <ArrowLeft className="w-4 h-4" />
          Back
        </button>
      </div>

      {/* Progress Steps */}
      <div className="flex items-center gap-4 mb-8">
        {/* Step 1 */}
        <div className="flex items-center gap-2">
          <div className={`
            w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
            transition-all duration-200
            ${currentStep === 'front' || frontPhoto
              ? 'bg-gray-900 text-white'
              : 'bg-gray-100 text-gray-400'
            }
          `}>
            {frontPhoto ? <Check className="w-4 h-4" /> : '1'}
          </div>
          <span className={`text-sm hidden sm:block ${currentStep === 'front' ? 'text-gray-900 font-medium' : 'text-gray-500'}`}>
            Front Photo
          </span>
        </div>

        {/* Connector */}
        <div className={`w-12 h-0.5 ${frontPhoto ? 'bg-gray-900' : 'bg-gray-200'} transition-colors duration-200`} />

        {/* Step 2 */}
        <div className="flex items-center gap-2">
          <div className={`
            w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
            transition-all duration-200
            ${currentStep === 'side'
              ? 'bg-gray-900 text-white'
              : sidePhoto
                ? 'bg-gray-900 text-white'
                : 'bg-gray-100 text-gray-400'
            }
          `}>
            {sidePhoto ? <Check className="w-4 h-4" /> : '2'}
          </div>
          <span className={`text-sm hidden sm:block ${currentStep === 'side' ? 'text-gray-900 font-medium' : 'text-gray-500'}`}>
            Side Profile
          </span>
        </div>
      </div>

      {/* Header */}
      <div className="text-center mb-8">
        <div className="flex items-center justify-center gap-3 mb-3">
          <StepIcon className="w-6 h-6 text-gray-900" />
          <h1 className="text-2xl md:text-3xl font-semibold tracking-tight text-gray-900">
            {config.title}
          </h1>
        </div>
        <p className="text-gray-600 text-sm md:text-base max-w-md mx-auto">
          {config.subtitle}
        </p>
      </div>

      {/* Upload Container */}
      <div className="w-full max-w-xl">
        {!currentPhoto ? (
          /* Dropzone */}
          <div
            {...getRootProps()}
            className={`
              relative w-full aspect-[4/3] rounded-xl border-2 border-dashed
              flex flex-col items-center justify-center gap-4
              cursor-pointer transition-all duration-200
              ${isDragActive
                ? 'border-gray-900 bg-gray-50'
                : 'border-gray-300 bg-gray-50 hover:border-gray-400 hover:bg-gray-100'
              }
            `}
          >
            <input {...getInputProps()} />

            {/* Upload Icon */}
            <div className={`
              p-5 rounded-full transition-colors duration-200
              ${isDragActive ? 'bg-gray-200' : 'bg-gray-100'}
            `}>
              <Upload className={`
                w-10 h-10 transition-colors duration-200
                ${isDragActive ? 'text-gray-900' : 'text-gray-400'}
              `} />
            </div>

            {/* Text */}
            <div className="text-center px-4">
              <p className={`
                text-base font-medium transition-colors duration-200
                ${isDragActive ? 'text-gray-900' : 'text-gray-700'}
              `}>
                {isDragActive ? 'Drop your image here' : 'Drag & drop your image here'}
              </p>
              <p className="text-gray-500 mt-1.5 text-sm">
                or <span className="text-gray-700 underline underline-offset-2">browse</span> to upload
              </p>
            </div>

            {/* Supported formats */}
            <div className="flex items-center gap-2 text-xs text-gray-400 mt-2">
              <ImageIcon className="w-4 h-4" />
              <span>JPG, PNG, WEBP supported</span>
            </div>

            {/* Photo guide hint */}
            <div className="absolute bottom-4 left-4 right-4 text-center">
              <p className="text-xs text-gray-400">
                {currentStep === 'front'
                  ? 'Tip: Face the camera directly with neutral expression'
                  : 'Tip: Turn 90° to show your side profile clearly'
                }
              </p>
            </div>
          </div>
        ) : (
          /* Preview */
          <div className="relative w-full aspect-[4/3] rounded-xl overflow-hidden bg-gray-100 border border-gray-200">
            {/* Image */}
            <Image
              src={currentPhoto.preview}
              alt={`${currentStep} preview`}
              fill
              className="object-contain"
              unoptimized
            />

            {/* Photo type badge */}
            <div className="absolute top-4 left-4 px-3 py-1.5 rounded-full bg-white/90 border border-gray-200 z-10">
              <span className="text-gray-700 text-sm font-medium flex items-center gap-2">
                <StepIcon className="w-4 h-4" />
                {currentStep === 'front' ? 'Front Photo' : 'Side Profile'}
              </span>
            </div>

            {/* Remove button */}
            <button
              onClick={removeImage}
              className="
                absolute top-4 right-4 p-2 rounded-full
                bg-white/90 border border-gray-200
                text-gray-600 hover:text-gray-900 hover:border-gray-300
                transition-all duration-200 z-10
              "
            >
              <X className="w-5 h-5" />
            </button>

            {/* File info */}
            <div className="absolute bottom-4 left-4 right-4 px-4 py-3 rounded-lg bg-white/90 border border-gray-200 z-10">
              <p className="text-gray-900 font-medium truncate text-sm">{currentPhoto.file.name}</p>
              <p className="text-gray-500 text-xs mt-0.5">
                {(currentPhoto.file.size / 1024 / 1024).toFixed(2)} MB
              </p>
            </div>
          </div>
        )}
      </div>

      {/* Navigation Buttons */}
      <div className="flex items-center gap-4 mt-8">
        {/* Back Button */}
        <button
          onClick={handleBack}
          className="h-12 px-6 rounded-xl font-medium flex items-center gap-2 border border-gray-300 bg-white text-gray-700 hover:bg-gray-50 hover:border-gray-400 transition-all duration-200"
        >
          <ArrowLeft className="w-4 h-4" />
          Back
        </button>

        {/* Next Button */}
        <button
          onClick={handleNext}
          disabled={!currentPhoto}
          className={`
            h-12 px-8 rounded-xl font-medium text-base
            flex items-center gap-2
            transition-all duration-200
            ${currentPhoto
              ? 'bg-gray-900 text-white hover:bg-gray-800 cursor-pointer shadow-sm'
              : 'bg-gray-100 text-gray-400 cursor-not-allowed'
            }
          `}
        >
          {currentStep === 'front' ? 'Next: Side Profile' : 'Start Analysis'}
          <ArrowRight className="w-4 h-4" />
        </button>
      </div>

      {/* Thumbnails for both photos when on step 2 */}
      {currentStep === 'side' && frontPhoto && (
        <div className="mt-8 flex items-center gap-4">
          <span className="text-gray-500 text-sm">Uploaded:</span>
          <button
            className="relative w-16 h-16 rounded-lg overflow-hidden border-2 border-gray-900 cursor-pointer hover:opacity-90 transition-opacity"
            onClick={() => setCurrentStep('front')}
          >
            <Image
              src={frontPhoto.preview}
              alt="Front photo thumbnail"
              fill
              className="object-cover"
              unoptimized
            />
            <div className="absolute inset-0 bg-black/30 flex items-center justify-center">
              <User className="w-5 h-5 text-white" />
            </div>
          </button>
          {sidePhoto && (
            <div className="relative w-16 h-16 rounded-lg overflow-hidden border-2 border-gray-900">
              <Image
                src={sidePhoto.preview}
                alt="Side photo thumbnail"
                fill
                className="object-cover"
                unoptimized
              />
              <div className="absolute inset-0 bg-black/30 flex items-center justify-center">
                <UserCircle className="w-5 h-5 text-white" />
              </div>
            </div>
          )}
        </div>
      )}
    </main>
  );
}

