    55→    try {
    56→      // Dynamically import MediaPipe
    57→      const vision = await import('@mediapipe/tasks-vision');
    58→      const { FaceLandmarker, FilesetResolver } = vision;
    59→
    60→      setDetectionStatus('detecting');
    61→
    62→      // Initialize MediaPipe Face Landmarker
