     7→import {
     8→  LandmarkPoint,
     9→  SIDE_PROFILE_LANDMARKS,
    10→  SIDE_LANDMARK_CATEGORIES,
    11→  getLandmarkColor,
    12→} from '@/lib/landmarks';
    13→import { useFaceApi, FACE_API_LANDMARK_MAPPING } from '@/hooks/useFaceApi';
    14→
    15→// Re-export types for backwards compatibility
    16→export type { LandmarkPoint } from '@/lib/landmarks';
    17→
    18→interface SideProfileManualToolProps {
    19→  imageUrl: string;
    20→  onLandmarksChange?: (landmarks: LandmarkPoint[]) => void;
    21→  onComplete?: (landmarks: LandmarkPoint[]) => void;
    22→}
    23→
    24→export function SideProfileManualTool({
    25→  imageUrl,
    26→  onLandmarksChange,
    27→  onComplete,
    28→}: SideProfileManualToolProps) {
    29→  const containerRef = useRef<HTMLDivElement>(null);
    30→  const imageRef = useRef<HTMLImageElement>(null);
    31→  const [landmarks, setLandmarks] = useState<LandmarkPoint[]>(SIDE_PROFILE_LANDMARKS);
    32→  const [activeLandmark, setActiveLandmark] = useState<string | null>(null);
    33→  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
    34→  const [currentStepIndex, setCurrentStepIndex] = useState(0);
    35→  const [isGuided, setIsGuided] = useState(true);
    36→  const [zoom, setZoom] = useState(1);
    37→  const [isDetecting, setIsDetecting] = useState(false);
    38→  const [detectionError, setDetectionError] = useState<string | null>(null);
    39→
    40→  // Face API hook for auto-detection
    41→  const { isLoading: faceApiLoading, isReady: faceApiReady, error: faceApiError, detect } = useFaceApi();
    42→
    43→  useEffect(() => {
    44→    onLandmarksChange?.(landmarks);
    45→  }, [landmarks, onLandmarksChange]);
    46→
    47→  // Auto-detect landmarks using face-api.js
    48→  const handleAutoDetect = useCallback(async () => {
    49→    if (!faceApiReady || !imageRef.current) {
    50→      setDetectionError('Face detection not ready. Please wait...');
    51→      return;
    52→    }
    53→
    54→    setIsDetecting(true);
    55→    setDetectionError(null);
    56→
    57→    try {
    58→      const result = await detect(imageRef.current);
    59→
    60→      if (!result) {
    61→        setDetectionError('No face detected. Please ensure your side profile is clearly visible.');
    62→        setIsDetecting(false);
    63→        return;
    64→      }
    65→
    66→      // Get image dimensions
    67→      const imgWidth = imageRef.current.naturalWidth;
    68→      const imgHeight = imageRef.current.naturalHeight;
    69→
    70→      // Map face-api.js 68-point landmarks to our side profile landmarks
    71→      const detectedLandmarks = result.landmarks;
    72→
    73→      setLandmarks((prev) =>
    74→        prev.map((lm) => {
    75→          // Map our landmark IDs to face-api indices
    76→          let detectedPoint = null;
    77→
    78→          switch (lm.id) {
    79→            // Chin/Jaw landmarks
    80→            case 'menton_side':
    81→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.MENTON];
    82→              break;
    83→            case 'pogonion':
    84→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.MENTON];
    85→              break;
    86→            case 'gonion_inferior_side':
    87→              detectedPoint = detectedLandmarks[4]; // Jaw contour point
    88→              break;
    89→            case 'gonion_superior_side':
    90→              detectedPoint = detectedLandmarks[3]; // Higher jaw point
    91→              break;
    92→
    93→            // Nose landmarks
    94→            case 'nasion':
    95→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.NASION];
    96→              break;
    97→            case 'pronasale':
    98→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.PRONASALE];
    99→              break;
   100→            case 'rhinion':
   101→              detectedPoint = detectedLandmarks[28]; // Mid nose bridge
   102→              break;
   103→            case 'subnasale_side':
   104→              detectedPoint = detectedLandmarks[33]; // Nose bottom center
   105→              break;
   106→            case 'columella_nasi':
   107→              detectedPoint = detectedLandmarks[33];
   108→              break;
   109→
   110→            // Lip landmarks
   111→            case 'labrale_superius_side':
   112→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.UPPER_LIP_TOP];
   113→              break;
   114→            case 'labrale_inferius_side':
   115→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.LOWER_LIP_BOTTOM];
   116→              break;
   117→            case 'cheilion_side':
   118→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.LEFT_MOUTH_CORNER];
   119→              break;
   120→            case 'sublabiale':
   121→              // Approximate from lower lip and chin
   122→              const lowerLip = detectedLandmarks[57];
   123→              const chin = detectedLandmarks[8];
   124→              if (lowerLip && chin) {
   125→                detectedPoint = {
   126→                  x: (lowerLip.x + chin.x) / 2,
   127→                  y: lowerLip.y + (chin.y - lowerLip.y) * 0.3,
   128→                };
   129→              }
   130→              break;
   131→
   132→            // Eye region
   133→            case 'corneal_apex':
   134→              detectedPoint = detectedLandmarks[FACE_API_LANDMARK_MAPPING.LEFT_EYE_OUTER];
   135→              break;
   136→            case 'lateral_eyelid':
   137→              detectedPoint = detectedLandmarks[36]; // Outer eye corner
   138→              break;
   139→            case 'palpebra_inferior_side':
   140→              detectedPoint = detectedLandmarks[40]; // Lower eyelid
   141→              break;
   142→            case 'orbitale':
   143→              detectedPoint = detectedLandmarks[41]; // Lower eye socket
   144→              break;
   145→
   146→            // Forehead
   147→            case 'glabella':
   148→              // Approximate from brow points
   149→              const leftBrow = detectedLandmarks[21];
   150→              const rightBrow = detectedLandmarks[22];
   151→              if (leftBrow && rightBrow) {
   152→                detectedPoint = {
   153→                  x: (leftBrow.x + rightBrow.x) / 2,
   154→                  y: (leftBrow.y + rightBrow.y) / 2,
   155→                };
   156→              }
   157→              break;
   158→
   159→            // Ear landmarks (use jaw contour as proxy)
   160→            case 'tragion':
   161→              detectedPoint = detectedLandmarks[1]; // Upper jaw near ear
   162→              break;
   163→            case 'porion':
   164→              detectedPoint = detectedLandmarks[0]; // Near ear
   165→              break;
   166→
   167→            default:
   168→              break;
   169→          }
   170→
   171→          if (detectedPoint) {
   172→            return {
   173→              ...lm,
   174→              x: detectedPoint.x / imgWidth,
   175→              y: detectedPoint.y / imgHeight,
   176→            };
   177→          }
   178→
   179→          return lm;
   180→        })
   181→      );
   182→
   183→      setIsDetecting(false);
   184→    } catch (err) {
   185→      setDetectionError(err instanceof Error ? err.message : 'Detection failed');
   186→      setIsDetecting(false);
   187→    }
   188→  }, [faceApiReady, detect]);
   189→
   190→  // Zoom controls
   191→  const handleZoomIn = () => setZoom((prev) => Math.min(prev + 0.25, 3));
   192→  const handleZoomOut = () => setZoom((prev) => Math.max(prev - 0.25, 0.5));
   193→
   194→  const handleMouseDown = useCallback((id: string) => {
   195→    setActiveLandmark(id);
   196→  }, []);
   197→
   198→  const handleMouseMove = useCallback(
   199→    (e: React.MouseEvent<HTMLDivElement>) => {
   200→      if (!activeLandmark || !containerRef.current) return;
   201→
   202→      const rect = containerRef.current.getBoundingClientRect();
   203→      const x = Math.max(0, Math.min(1, (e.clientX - rect.left) / rect.width));
   204→      const y = Math.max(0, Math.min(1, (e.clientY - rect.top) / rect.height));
   205→
   206→      setLandmarks((prev) =>
   207→        prev.map((lm) => (lm.id === activeLandmark ? { ...lm, x, y } : lm))
   208→      );
   209→    },
   210→    [activeLandmark]
   211→  );
   212→
   213→  const handleMouseUp = useCallback(() => {
