   276→  return (
   277→    <div className="w-full max-w-4xl mx-auto">
   278→      {/* Header */}
   279→      <motion.div
   280→        initial={{ opacity: 0, y: -10 }}
   281→        animate={{ opacity: 1, y: 0 }}
   282→        className="mb-6 text-center"
   283→      >
