   550→                  (lm) => lm.id === currentCategory.landmarks[index + 1]
   551→                );
   552→                if (!current || !next) return null;
   553→
   554→                return (
   555→                  <line
   556→                    key={`line-${landmarkId}`}
   557→                    x1={current.x * 100}
   558→                    y1={current.y * 100}
   559→                    x2={next.x * 100}
   560→                    y2={next.y * 100}
   561→                    stroke={`${currentCategory.color}66`}
   562→                    strokeWidth="0.3"
   563→                    strokeDasharray="1,1"
   564→                  />
   565→                );
   566→              })}
   567→            </svg>
   568→          )}
   569→
   570→          {/* Landmark Points */}
   571→          {landmarks.map((landmark) => {
   572→            const isVisible = visibleLandmarkIds.has(landmark.id);
   573→            const color = getLandmarkColor(landmark.id, SIDE_LANDMARK_CATEGORIES);
   574→
   575→            return (
   576→              <div
   577→                key={landmark.id}
   578→                className={`
   579→                  absolute w-5 h-5 -translate-x-1/2 -translate-y-1/2 cursor-grab
   580→                  transition-all duration-200
   581→                  ${activeLandmark === landmark.id ? 'cursor-grabbing z-30' : 'z-10'}
   582→                  ${isVisible ? 'opacity-100 scale-100' : 'opacity-10 scale-75 pointer-events-none'}
   583→                `}
   584→                style={{
   585→                  left: `${landmark.x * 100}%`,
   586→                  top: `${landmark.y * 100}%`,
   587→                }}
   588→                onMouseDown={() => isVisible && handleMouseDown(landmark.id)}
   589→                onTouchStart={() => isVisible && handleTouchStart(landmark.id)}
   590→              >
   591→                {/* Outer glow ring */}
   592→                <div
   593→                  className={`
   594→                    absolute inset-0 rounded-full
   595→                    transition-all duration-200
   596→                    ${activeLandmark === landmark.id ? 'scale-150' : ''}
   597→                  `}
   598→                  style={{
   599→                    backgroundColor: `${color}33`,
   600→                    boxShadow:
   601→                      activeLandmark === landmark.id
   602→                        ? `0 0 25px ${color}`
   603→                        : `0 0 10px ${color}`,
   604→                  }}
   605→                />
   606→
   607→                {/* Main handle */}
   608→                <div
   609→                  className={`
   610→                    absolute inset-1 rounded-full border-2 border-white
   611→                    transition-all duration-200
   612→                    ${activeLandmark === landmark.id ? 'scale-125' : ''}
   613→                  `}
   614→                  style={{
   615→                    backgroundColor: color,
   616→                    boxShadow: `0 0 10px ${color}, 0 0 20px ${color}`,
   617→                  }}
   618→                />
   619→
   620→                {/* Label tooltip */}
   621→                <div
   622→                  className={`
   623→                    absolute left-6 top-1/2 -translate-y-1/2 whitespace-nowrap
   624→                    px-2 py-1 rounded bg-black/95 border
   625→                    text-xs font-medium z-50
   626→                    pointer-events-none transition-opacity duration-200
   627→                    ${activeLandmark === landmark.id ? 'opacity-100' : 'opacity-0'}
   628→                  `}
   629→                  style={{
   630→                    borderColor: `${color}80`,
   631→                    color: color,
   632→                  }}
   633→                >
   634→                  <div>{landmark.label}</div>
   635→                  <div className="text-[10px] opacity-70">{landmark.medicalTerm}</div>
   636→                </div>
   637→              </div>
   638→            );
   639→          })}
   640→        </div>
   641→      </motion.div>
   642→
   643→      {/* Landmark Legend for Current Category */}
   644→      <motion.div
   645→        initial={{ opacity: 0, y: 10 }}
   646→        animate={{ opacity: 1, y: 0 }}
   647→        transition={{ delay: 0.2 }}
   648→        className="mt-6 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2"
   649→      >
   650→        {filteredLandmarks.map((landmark) => {
   651→          const color = getLandmarkColor(landmark.id, SIDE_LANDMARK_CATEGORIES);
   652→          return (
   653→            <div
   654→              key={landmark.id}
   655→              className={`
   656→                flex items-center gap-2 px-3 py-2 rounded-lg
   657→                border transition-all duration-200 cursor-pointer
   658→                ${
   659→                  activeLandmark === landmark.id
   660→                    ? 'border-gray-400 bg-gray-100'
   661→                    : 'border-gray-200 bg-white hover:bg-gray-50'
   662→                }
   663→              `}
   664→              onMouseEnter={() => setActiveLandmark(landmark.id)}
   665→              onMouseLeave={() => setActiveLandmark(null)}
   666→            >
   667→              <div
   668→                className="w-3 h-3 rounded-full flex-shrink-0"
   669→                style={{
   670→                  backgroundColor: color,
   671→                }}
   672→              />
   673→              <div className="min-w-0">
   674→                <p className="text-sm font-medium text-gray-900 truncate">
   675→                  {landmark.label}
   676→                </p>
   677→                <p className="text-xs text-gray-600 truncate">
   678→                  {landmark.description}
   679→                </p>
   680→              </div>
   681→            </div>
   682→          );
   683→        })}
   684→      </motion.div>
   685→
   686→      {/* Navigation/Complete Buttons */}
   687→      <motion.div
   688→        initial={{ opacity: 0, y: 10 }}
   689→        animate={{ opacity: 1, y: 0 }}
   690→        transition={{ delay: 0.3 }}
   691→        className="mt-8 flex justify-center gap-4"
   692→      >
   693→        {isGuided && currentStepIndex < SIDE_LANDMARK_CATEGORIES.length - 1 ? (
   694→          <button
   695→            onClick={nextStep}
   696→            className="
   697→              px-8 py-4 rounded-xl font-semibold text-lg
   698→              bg-gray-900 text-white
   699→              hover:bg-gray-800
   700→              transition-all duration-300
   701→            "
   702→          >
   703→            Next: {SIDE_LANDMARK_CATEGORIES[currentStepIndex + 1]?.name}
   704→          </button>
   705→        ) : (
   706→          <button
   707→            onClick={handleComplete}
   708→            className="
   709→              px-8 py-4 rounded-xl font-semibold text-lg
   710→              bg-gray-900 text-white
   711→              hover:bg-gray-800
   712→              transition-all duration-300
   713→            "
   714→          >
   715→            Confirm Side Landmarks
   716→          </button>
   717→        )}
   718→      </motion.div>
   719→
   720→      {/* Total Progress */}
   721→      <motion.div
   722→        initial={{ opacity: 0 }}
   723→        animate={{ opacity: 1 }}
   724→        transition={{ delay: 0.4 }}
   725→        className="mt-4 text-center text-sm text-gray-600"
   726→      >
   727→        {landmarks.length} landmarks • Drag markers to adjust positions
   728→      </motion.div>
   729→    </div>
   730→  );
   731→}
