'use client';

import { useRouter } from 'next/navigation';
import { useGender, Gender } from '@/contexts/GenderContext';
import { Check } from 'lucide-react';

export default function GenderPage() {
  const router = useRouter();
  const { gender, setGender } = useGender();

  const handleSelect = (selectedGender: Gender) => {
    setGender(selectedGender);
  };

  const handleContinue = () => {
    if (gender) {
      router.push('/ethnicity');
    }
  };

  return (
    <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4 py-12">
      {/* Back button */}
      <div className="absolute top-6 left-6">
        <button
          onClick={() => router.push('/login')}
          className="text-sm text-neutral-400 hover:text-white transition-colors"
        >
          &larr; Back
        </button>
      </div>

      {/* Header */}
      <div className="text-center mb-12">
        <h1 className="text-2xl md:text-3xl font-semibold tracking-tight text-white mb-3">
          Select Your Gender
        </h1>
        <p className="text-neutral-400 text-sm md:text-base">
          This helps us provide accurate facial analysis
        </p>
      </div>

      {/* Gender Cards */}
      <div className="flex flex-col sm:flex-row gap-4 sm:gap-6 mb-10">
        {/* Male Card */}
        <button
          onClick={() => handleSelect('male')}
          className={`
            relative w-48 h-56 sm:w-56 sm:h-64 rounded-xl border-2
            flex flex-col items-center justify-center gap-4
            transition-all duration-200 cursor-pointer
            ${gender === 'male'
              ? 'border-[#00f3ff] bg-[#00f3ff]/5 shadow-[0_0_20px_rgba(0,243,255,0.15)]'
              : 'border-neutral-800 bg-black hover:border-neutral-700 hover:bg-white/5'
            }
          `}
        >
          {/* Male Icon */}
          <div className={`
            text-6xl sm:text-7xl transition-colors duration-200
            ${gender === 'male' ? 'text-[#00f3ff]' : 'text-neutral-500'}
          `}>
            ♂
          </div>
          <span className={`
            text-lg font-medium transition-colors duration-200
            ${gender === 'male' ? 'text-white' : 'text-neutral-400'}
          `}>
            Male
          </span>

          {/* Selected indicator */}
          {gender === 'male' && (
            <div className="absolute top-3 right-3 w-6 h-6 rounded-full bg-[#00f3ff] flex items-center justify-center">
              <Check className="w-4 h-4 text-black" strokeWidth={3} />
            </div>
          )}
        </button>

        {/* Female Card */}
        <button
          onClick={() => handleSelect('female')}
          className={`
            relative w-48 h-56 sm:w-56 sm:h-64 rounded-xl border-2
            flex flex-col items-center justify-center gap-4
            transition-all duration-200 cursor-pointer
            ${gender === 'female'
              ? 'border-[#00f3ff] bg-[#00f3ff]/5 shadow-[0_0_20px_rgba(0,243,255,0.15)]'
              : 'border-neutral-800 bg-black hover:border-neutral-700 hover:bg-white/5'
            }
          `}
        >
          {/* Female Icon */}
          <div className={`
            text-6xl sm:text-7xl transition-colors duration-200
            ${gender === 'female' ? 'text-[#00f3ff]' : 'text-neutral-500'}
          `}>
            ♀
          </div>
          <span className={`
            text-lg font-medium transition-colors duration-200
            ${gender === 'female' ? 'text-white' : 'text-neutral-400'}
          `}>
            Female
          </span>

          {/* Selected indicator */}
          {gender === 'female' && (
            <div className="absolute top-3 right-3 w-6 h-6 rounded-full bg-[#00f3ff] flex items-center justify-center">
              <Check className="w-4 h-4 text-black" strokeWidth={3} />
            </div>
          )}
        </button>
      </div>

      {/* Continue Button */}
      <button
        onClick={handleContinue}
        disabled={!gender}
        className={`
          h-12 px-10 rounded-xl font-medium text-base
          transition-all duration-200
          ${gender
            ? 'bg-[#00f3ff] text-black hover:shadow-[0_0_20px_rgba(0,243,255,0.3)] cursor-pointer'
            : 'bg-neutral-800 text-neutral-500 cursor-not-allowed'
          }
        `}
      >
        Continue
      </button>
    </main>
  );
}

'use client';

import { useRouter } from 'next/navigation';
import { ArrowLeft, ArrowRight, Check } from 'lucide-react';
import { useEthnicity, EthnicityOption, ethnicityLabels } from '@/contexts/EthnicityContext';

const ethnicityOptions: EthnicityOption[] = [
  'white',
  'black',
  'asian',
  'south-asian',
  'hispanic',
  'middle-eastern',
  'pacific-islander',
  'native-american',
  'mixed',
];

export default function EthnicityPage() {
  const router = useRouter();
  const { ethnicities, toggleEthnicity } = useEthnicity();

  const handleToggle = (option: EthnicityOption) => {
    toggleEthnicity(option);
  };

  const handleContinue = () => {
    if (ethnicities.length > 0) {
      router.push('/upload');
    }
  };

  const handleBack = () => {
    router.push('/gender');
  };

  return (
    <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4 py-12">
      {/* Back button */}
      <div className="absolute top-6 left-6">
        <button
          onClick={handleBack}
          className="text-sm text-neutral-400 hover:text-white transition-colors flex items-center gap-1"
        >
          <ArrowLeft className="w-4 h-4" />
          Back
        </button>
      </div>

      {/* Header */}
      <div className="text-center mb-10">
        <h1 className="text-2xl md:text-3xl font-semibold tracking-tight text-white mb-3">
          Select Your Ethnicity
        </h1>
        <p className="text-neutral-400 text-sm md:text-base max-w-md mx-auto">
          Select all that apply - this helps us provide more accurate analysis
        </p>
      </div>

      {/* Ethnicity Options */}
      <div className="w-full max-w-md space-y-2 mb-10">
        {ethnicityOptions.map((option) => {
          const isSelected = ethnicities.includes(option);
          return (
            <button
              key={option}
              onClick={() => handleToggle(option)}
              className={`
                w-full h-14 rounded-xl border-2
                flex items-center justify-between px-5
                transition-all duration-200 cursor-pointer
                ${isSelected
                  ? 'border-[#00f3ff] bg-[#00f3ff]/5 shadow-[0_0_15px_rgba(0,243,255,0.1)]'
                  : 'border-neutral-800 bg-black hover:border-neutral-700 hover:bg-white/5'
                }
              `}
            >
              <span className={`
                font-medium transition-colors duration-200
                ${isSelected ? 'text-white' : 'text-neutral-300'}
              `}>
                {ethnicityLabels[option]}
              </span>

              {/* Checkbox indicator */}
              <div className={`
                w-6 h-6 rounded-md border-2 flex items-center justify-center
                transition-all duration-200
                ${isSelected
                  ? 'bg-[#00f3ff] border-[#00f3ff]'
                  : 'bg-transparent border-neutral-700'
                }
              `}>
                {isSelected && (
                  <Check className="w-4 h-4 text-black" strokeWidth={3} />
                )}
              </div>
            </button>
          );
        })}
      </div>

      {/* Navigation Buttons */}
      <div className="flex items-center gap-4">
        {/* Back Button */}
        <button
          onClick={handleBack}
          className="h-12 px-6 rounded-xl font-medium flex items-center gap-2 border border-neutral-700 bg-black text-white hover:bg-white/5 hover:border-neutral-600 transition-all duration-200"
        >
          <ArrowLeft className="w-4 h-4" />
          Back
        </button>

        {/* Continue Button */}
        <button
          onClick={handleContinue}
          disabled={ethnicities.length === 0}
          className={`
            h-12 px-8 rounded-xl font-medium text-base
            flex items-center gap-2
            transition-all duration-200
            ${ethnicities.length > 0
              ? 'bg-[#00f3ff] text-black hover:shadow-[0_0_20px_rgba(0,243,255,0.3)] cursor-pointer'
              : 'bg-neutral-800 text-neutral-500 cursor-not-allowed'
            }
          `}
        >
          Continue
          <ArrowRight className="w-4 h-4" />
        </button>
      </div>
    </main>
  );
}

'use client';

import { useState, useCallback } from 'react';
import { useRouter } from 'next/navigation';
import Image from 'next/image';
import { useDropzone } from 'react-dropzone';
import { Upload, X, Image as ImageIcon, ArrowRight, ArrowLeft, User, UserCircle, Check } from 'lucide-react';
import { useUpload } from '@/contexts/UploadContext';

type UploadStep = 'front' | 'side';

export default function UploadPage() {
  const router = useRouter();
  const { frontPhoto, sidePhoto, setFrontPhoto, setSidePhoto } = useUpload();
  const [currentStep, setCurrentStep] = useState<UploadStep>('front');

  const currentPhoto = currentStep === 'front' ? frontPhoto : sidePhoto;
  const setCurrentPhoto = currentStep === 'front' ? setFrontPhoto : setSidePhoto;

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const selectedFile = acceptedFiles[0];
    if (selectedFile) {
      const objectUrl = URL.createObjectURL(selectedFile);
      setCurrentPhoto({ file: selectedFile, preview: objectUrl });
    }
  }, [setCurrentPhoto]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.webp']
    },
    maxFiles: 1,
    multiple: false
  });

  const removeImage = () => {
    if (currentPhoto?.preview) {
      URL.revokeObjectURL(currentPhoto.preview);
    }
    setCurrentPhoto(null);
  };

  const handleNext = () => {
    if (currentStep === 'front' && frontPhoto) {
      setCurrentStep('side');
    } else if (currentStep === 'side' && sidePhoto) {
      router.push('/analysis');
    }
  };

  const handleBack = () => {
    if (currentStep === 'side') {
      setCurrentStep('front');
    } else {
      router.push('/ethnicity');
    }
  };

  const stepConfig = {
    front: {
      title: 'Upload Front-Facing Photo',
      subtitle: 'Upload a clear, front-facing photo looking straight at the camera',
      icon: User,
    },
    side: {
      title: 'Upload Side Profile Photo',
      subtitle: 'Upload a clear side profile photo showing your facial structure',
      icon: UserCircle,
    },
  };

  const config = stepConfig[currentStep];
  const StepIcon = config.icon;

  return (
    <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4 py-12">
      {/* Back button */}
      <div className="absolute top-6 left-6">
        <button
          onClick={handleBack}
          className="text-sm text-neutral-400 hover:text-white transition-colors flex items-center gap-1"
        >
          <ArrowLeft className="w-4 h-4" />
          Back
        </button>
      </div>

      {/* Progress Steps */}
      <div className="flex items-center gap-4 mb-8">
        {/* Step 1 */}
        <div className="flex items-center gap-2">
          <div className={`
            w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
            transition-all duration-200
            ${currentStep === 'front' || frontPhoto
              ? 'bg-[#00f3ff] text-black'
              : 'bg-neutral-800 text-neutral-500'
            }
          `}>
            {frontPhoto ? <Check className="w-4 h-4" /> : '1'}
          </div>
          <span className={`text-sm hidden sm:block ${currentStep === 'front' ? 'text-white font-medium' : 'text-neutral-500'}`}>
            Front Photo
          </span>
        </div>

        {/* Connector */}
        <div className={`w-12 h-0.5 ${frontPhoto ? 'bg-[#00f3ff]' : 'bg-neutral-800'} transition-colors duration-200`} />

        {/* Step 2 */}
        <div className="flex items-center gap-2">
          <div className={`
            w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
            transition-all duration-200
            ${currentStep === 'side'
              ? 'bg-[#00f3ff] text-black'
              : sidePhoto
                ? 'bg-[#00f3ff] text-black'
                : 'bg-neutral-800 text-neutral-500'
            }
          `}>
            {sidePhoto ? <Check className="w-4 h-4" /> : '2'}
          </div>
          <span className={`text-sm hidden sm:block ${currentStep === 'side' ? 'text-white font-medium' : 'text-neutral-500'}`}>
            Side Profile
          </span>
        </div>
      </div>

      {/* Header */}
      <div className="text-center mb-8">
        <div className="flex items-center justify-center gap-3 mb-3">
          <StepIcon className="w-6 h-6 text-[#00f3ff]" />
          <h1 className="text-2xl md:text-3xl font-semibold tracking-tight text-white">
            {config.title}
          </h1>
        </div>
        <p className="text-neutral-400 text-sm md:text-base max-w-md mx-auto">
          {config.subtitle}
        </p>
      </div>

      {/* Upload Container */}
      <div className="w-full max-w-xl">
        {!currentPhoto ? (
          <div
            {...getRootProps()}
            className={`
              relative w-full aspect-[4/3] rounded-xl border-2 border-dashed
              flex flex-col items-center justify-center gap-4
              cursor-pointer transition-all duration-200
              ${isDragActive
                ? 'border-[#00f3ff] bg-[#00f3ff]/5'
                : 'border-neutral-700 bg-neutral-900/50 hover:border-neutral-600 hover:bg-neutral-900'
              }
            `}
          >
            <input {...getInputProps()} />

            {/* Upload Icon */}
            <div className={`
              p-5 rounded-full transition-colors duration-200
              ${isDragActive ? 'bg-[#00f3ff]/20' : 'bg-neutral-800'}
            `}>
              <Upload className={`
                w-10 h-10 transition-colors duration-200
                ${isDragActive ? 'text-[#00f3ff]' : 'text-neutral-500'}
              `} />
            </div>

            {/* Text */}
            <div className="text-center px-4">
              <p className={`
                text-base font-medium transition-colors duration-200
                ${isDragActive ? 'text-[#00f3ff]' : 'text-neutral-300'}
              `}>
                {isDragActive ? 'Drop your image here' : 'Drag & drop your image here'}
              </p>
              <p className="text-neutral-500 mt-1.5 text-sm">
                or <span className="text-neutral-300 underline underline-offset-2">browse</span> to upload
              </p>
            </div>

            {/* Supported formats */}
            <div className="flex items-center gap-2 text-xs text-neutral-500 mt-2">
              <ImageIcon className="w-4 h-4" />
              <span>JPG, PNG, WEBP supported</span>
            </div>

            {/* Photo guide hint */}
            <div className="absolute bottom-4 left-4 right-4 text-center">
              <p className="text-xs text-neutral-500">
                {currentStep === 'front'
                  ? 'Tip: Face the camera directly with neutral expression'
                  : 'Tip: Turn 90° to show your side profile clearly'
                }
              </p>
            </div>
          </div>
        ) : (
          /* Preview */
          <div className="relative w-full aspect-[4/3] rounded-xl overflow-hidden bg-neutral-900 border border-neutral-800">
            {/* Image */}
            <Image
              src={currentPhoto.preview}
              alt={`${currentStep} preview`}
              fill
              className="object-contain"
              unoptimized
            />

            {/* Photo type badge */}
            <div className="absolute top-4 left-4 px-3 py-1.5 rounded-full bg-black/80 border border-neutral-700 z-10">
              <span className="text-white text-sm font-medium flex items-center gap-2">
                <StepIcon className="w-4 h-4 text-[#00f3ff]" />
                {currentStep === 'front' ? 'Front Photo' : 'Side Profile'}
              </span>
            </div>

            {/* Remove button */}
            <button
              onClick={removeImage}
              className="
                absolute top-4 right-4 p-2 rounded-full
                bg-black/80 border border-neutral-700
                text-neutral-400 hover:text-white hover:border-neutral-600
                transition-all duration-200 z-10
              "
            >
              <X className="w-5 h-5" />
            </button>

            {/* File info */}
            <div className="absolute bottom-4 left-4 right-4 px-4 py-3 rounded-lg bg-black/80 border border-neutral-700 z-10">
              <p className="text-white font-medium truncate text-sm">{currentPhoto.file.name}</p>
              <p className="text-neutral-500 text-xs mt-0.5">
                {(currentPhoto.file.size / 1024 / 1024).toFixed(2)} MB
              </p>
            </div>
          </div>
        )}
      </div>

      {/* Navigation Buttons */}
      <div className="flex items-center gap-4 mt-8">
        {/* Back Button */}
        <button
          onClick={handleBack}
          className="h-12 px-6 rounded-xl font-medium flex items-center gap-2 border border-neutral-700 bg-black text-white hover:bg-white/5 hover:border-neutral-600 transition-all duration-200"
        >
          <ArrowLeft className="w-4 h-4" />
          Back
        </button>

        {/* Next Button */}
        <button
          onClick={handleNext}
          disabled={!currentPhoto}
          className={`
            h-12 px-8 rounded-xl font-medium text-base
            flex items-center gap-2
            transition-all duration-200
            ${currentPhoto
              ? 'bg-[#00f3ff] text-black hover:shadow-[0_0_20px_rgba(0,243,255,0.3)] cursor-pointer'
              : 'bg-neutral-800 text-neutral-500 cursor-not-allowed'
            }
          `}
        >
          {currentStep === 'front' ? 'Next: Side Profile' : 'Start Analysis'}
          <ArrowRight className="w-4 h-4" />
        </button>
      </div>

      {/* Thumbnails for both photos when on step 2 */}
      {currentStep === 'side' && frontPhoto && (
        <div className="mt-8 flex items-center gap-4">
          <span className="text-neutral-500 text-sm">Uploaded:</span>
          <button
            className="relative w-16 h-16 rounded-lg overflow-hidden border-2 border-[#00f3ff] cursor-pointer hover:opacity-90 transition-opacity"
            onClick={() => setCurrentStep('front')}
          >
            <Image
              src={frontPhoto.preview}
              alt="Front photo thumbnail"
              fill
              className="object-cover"
              unoptimized
            />
            <div className="absolute inset-0 bg-black/30 flex items-center justify-center">
              <User className="w-5 h-5 text-white" />
            </div>
          </button>
          {sidePhoto && (
            <div className="relative w-16 h-16 rounded-lg overflow-hidden border-2 border-[#00f3ff]">
              <Image
                src={sidePhoto.preview}
                alt="Side photo thumbnail"
                fill
                className="object-cover"
                unoptimized
              />
              <div className="absolute inset-0 bg-black/30 flex items-center justify-center">
                <UserCircle className="w-5 h-5 text-white" />
              </div>
            </div>
          )}
        </div>
      )}
    </main>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { ArrowLeft, User, UserCircle, Check, BarChart3 } from 'lucide-react';
import { useUpload } from '@/contexts/UploadContext';
import { useGender } from '@/contexts/GenderContext';
import { FrontProfileLandmarks } from '@/components/FrontProfileLandmarks';
import { SideProfileManualTool } from '@/components/SideProfileManualTool';
import { ResultsDashboard } from '@/components/ResultsDashboard';
import { LandmarkPoint } from '@/lib/landmarks';

type AnalysisStep = 'front' | 'side' | 'results';

export default function AnalysisPage() {
  const router = useRouter();
  const { frontPhoto, sidePhoto } = useUpload();
  const { gender } = useGender();
  const [currentStep, setCurrentStep] = useState<AnalysisStep>('front');
  const [frontLandmarks, setFrontLandmarks] = useState<LandmarkPoint[]>([]);
  const [sideLandmarks, setSideLandmarks] = useState<LandmarkPoint[]>([]);

  // Redirect if no photos uploaded
  if (!frontPhoto && !sidePhoto) {
    return (
      <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4">
        <div className="text-center">
          <h1 className="text-2xl font-semibold text-white mb-4">No Photos Found</h1>
          <p className="text-neutral-400 mb-8">
            Please upload your photos first to begin the analysis.
          </p>
          <button
            onClick={() => router.push('/upload')}
            className="h-12 px-8 rounded-xl bg-[#00f3ff] text-black font-medium hover:shadow-[0_0_20px_rgba(0,243,255,0.3)] transition-all"
          >
            Go to Upload
          </button>
        </div>
      </main>
    );
  }

  const handleFrontComplete = (landmarks: LandmarkPoint[]) => {
    setFrontLandmarks(landmarks);
    if (sidePhoto) {
      setCurrentStep('side');
    } else {
      setCurrentStep('results');
    }
  };

  const handleSideComplete = (landmarks: LandmarkPoint[]) => {
    setSideLandmarks(landmarks);
    setCurrentStep('results');
  };

  const handleBack = () => {
    if (currentStep === 'side') {
      setCurrentStep('front');
    } else if (currentStep === 'results') {
      setCurrentStep(sidePhoto ? 'side' : 'front');
    } else {
      router.push('/upload');
    }
  };

  const stepConfig = {
    front: {
      title: 'Front Profile',
      icon: User,
    },
    side: {
      title: 'Side Profile',
      icon: UserCircle,
    },
    results: {
      title: 'Results',
      icon: BarChart3,
    },
  };

  const config = stepConfig[currentStep];

  return (
    <main className="min-h-screen bg-black flex flex-col items-center px-4 py-8">
      {/* Header */}
      <div className="w-full max-w-5xl mb-6">
        <div className="flex items-center justify-between">
          <button
            onClick={handleBack}
            className="flex items-center gap-2 text-neutral-400 hover:text-white transition-colors text-sm"
          >
            <ArrowLeft className="w-4 h-4" />
            Back
          </button>

          {/* Step Indicators */}
          <div className="flex items-center gap-3">
            {/* Front Step */}
            <div className="flex items-center gap-2">
              <div
                className={`
                  w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                  transition-all duration-200
                  ${
                    currentStep === 'front'
                      ? 'bg-[#00f3ff] text-black'
                      : frontLandmarks.length > 0
                        ? 'bg-green-500 text-white'
                        : 'bg-neutral-800 text-neutral-500'
                  }
                `}
              >
                {frontLandmarks.length > 0 && currentStep !== 'front' ? (
                  <Check className="w-4 h-4" />
                ) : (
                  '1'
                )}
              </div>
              <span
                className={`text-sm hidden sm:block ${
                  currentStep === 'front' ? 'text-white font-medium' : 'text-neutral-500'
                }`}
              >
                Front
              </span>
            </div>

            {/* Connector */}
            <div
              className={`w-8 h-0.5 ${
                frontLandmarks.length > 0 ? 'bg-[#00f3ff]' : 'bg-neutral-800'
              } transition-colors duration-200`}
            />

            {/* Side Step */}
            {sidePhoto && (
              <>
                <div className="flex items-center gap-2">
                  <div
                    className={`
                      w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                      transition-all duration-200
                      ${
                        currentStep === 'side'
                          ? 'bg-[#00f3ff] text-black'
                          : sideLandmarks.length > 0
                            ? 'bg-green-500 text-white'
                            : 'bg-neutral-800 text-neutral-500'
                      }
                    `}
                  >
                    {sideLandmarks.length > 0 && currentStep !== 'side' ? (
                      <Check className="w-4 h-4" />
                    ) : (
                      '2'
                    )}
                  </div>
                  <span
                    className={`text-sm hidden sm:block ${
                      currentStep === 'side' ? 'text-white font-medium' : 'text-neutral-500'
                    }`}
                  >
                    Side
                  </span>
                </div>

                {/* Connector */}
                <div
                  className={`w-8 h-0.5 ${
                    sideLandmarks.length > 0 ? 'bg-[#00f3ff]' : 'bg-neutral-800'
                  } transition-colors duration-200`}
                />
              </>
            )}

            {/* Results Step */}
            <div className="flex items-center gap-2">
              <div
                className={`
                  w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                  transition-all duration-200
                  ${
                    currentStep === 'results'
                      ? 'bg-[#00f3ff] text-black'
                      : 'bg-neutral-800 text-neutral-500'
                  }
                `}
              >
                {sidePhoto ? '3' : '2'}
              </div>
              <span
                className={`text-sm hidden sm:block ${
                  currentStep === 'results' ? 'text-white font-medium' : 'text-neutral-500'
                }`}
              >
                Results
              </span>
            </div>
          </div>

          {/* Current step badge */}
          <div className="px-3 py-1.5 rounded-full bg-neutral-900 border border-neutral-800">
            <span className="text-white text-sm font-medium flex items-center gap-2">
              <config.icon className="w-4 h-4 text-[#00f3ff]" />
              {config.title}
            </span>
          </div>
        </div>
      </div>

      {/* Main Content */}
      {currentStep === 'front' && frontPhoto && (
        <div className="w-full">
          <FrontProfileLandmarks
            imageUrl={frontPhoto.preview}
            onLandmarksChange={setFrontLandmarks}
            onComplete={handleFrontComplete}
          />
        </div>
      )}

      {currentStep === 'side' && sidePhoto && (
        <div className="w-full">
          <SideProfileManualTool
            imageUrl={sidePhoto.preview}
            onLandmarksChange={setSideLandmarks}
            onComplete={handleSideComplete}
          />
        </div>
      )}

      {currentStep === 'results' && (
        <div className="w-full max-w-5xl">
          <ResultsDashboard
            frontLandmarks={frontLandmarks}
            sideLandmarks={sideLandmarks}
            gender={gender || 'male'}
          />
        </div>
      )}
    </main>
  );
}

