'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { ArrowLeft, User, UserCircle, Check, BarChart3 } from 'lucide-react';
import { useUpload } from '@/contexts/UploadContext';
import { useGender } from '@/contexts/GenderContext';
import { FrontProfileLandmarks } from '@/components/FrontProfileLandmarks';
import { SideProfileManualTool } from '@/components/SideProfileManualTool';
import { ResultsDashboard } from '@/components/ResultsDashboard';
import { LandmarkPoint } from '@/lib/landmarks';

type AnalysisStep = 'front' | 'side' | 'results';

export default function AnalysisPage() {
  const router = useRouter();
  const { frontPhoto, sidePhoto } = useUpload();
  const { gender } = useGender();
  const [currentStep, setCurrentStep] = useState<AnalysisStep>('front');
  const [frontLandmarks, setFrontLandmarks] = useState<LandmarkPoint[]>([]);
  const [sideLandmarks, setSideLandmarks] = useState<LandmarkPoint[]>([]);

  // Redirect if no photos uploaded
  if (!frontPhoto && !sidePhoto) {
    return (
      <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4">
        <div className="text-center">
          <h1 className="text-2xl font-semibold text-white mb-4">No Photos Found</h1>
          <p className="text-neutral-400 mb-8">
            Please upload your photos first to begin the analysis.
          </p>
          <button
            onClick={() => router.push('/upload')}
            className="h-12 px-8 rounded-xl bg-[#00f3ff] text-black font-medium hover:shadow-[0_0_20px_rgba(0,243,255,0.3)] transition-all"
          >
            Go to Upload
          </button>
        </div>
      </main>
    );
  }

  const handleFrontComplete = (landmarks: LandmarkPoint[]) => {
    setFrontLandmarks(landmarks);
    if (sidePhoto) {
      setCurrentStep('side');
    } else {
      setCurrentStep('results');
    }
  };

  const handleSideComplete = (landmarks: LandmarkPoint[]) => {
    setSideLandmarks(landmarks);
    setCurrentStep('results');
  };

  const handleBack = () => {
    if (currentStep === 'side') {
      setCurrentStep('front');
    } else if (currentStep === 'results') {
      setCurrentStep(sidePhoto ? 'side' : 'front');
    } else {
      router.push('/upload');
    }
  };

  const stepConfig = {
    front: {
      title: 'Front Profile',
      icon: User,
    },
    side: {
      title: 'Side Profile',
      icon: UserCircle,
    },
    results: {
      title: 'Results',
      icon: BarChart3,
    },
  };

  const config = stepConfig[currentStep];

  return (
    <main className="min-h-screen bg-black flex flex-col items-center px-4 py-8">
      {/* Header */}
      <div className="w-full max-w-5xl mb-6">
        <div className="flex items-center justify-between">
          <button
            onClick={handleBack}
            className="flex items-center gap-2 text-neutral-400 hover:text-white transition-colors text-sm"
          >
            <ArrowLeft className="w-4 h-4" />
            Back
          </button>

          {/* Step Indicators */}
          <div className="flex items-center gap-3">
            {/* Front Step */}
            <div className="flex items-center gap-2">
              <div
                className={`
                  w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                  transition-all duration-200
                  ${
                    currentStep === 'front'
                      ? 'bg-[#00f3ff] text-black'
                      : frontLandmarks.length > 0
                        ? 'bg-green-500 text-white'
                        : 'bg-neutral-800 text-neutral-500'
                  }
                `}
              >
                {frontLandmarks.length > 0 && currentStep !== 'front' ? (
                  <Check className="w-4 h-4" />
                ) : (
                  '1'
                )}
              </div>
              <span
                className={`text-sm hidden sm:block ${
                  currentStep === 'front' ? 'text-white font-medium' : 'text-neutral-500'
                }`}
              >
                Front
              </span>
            </div>

            {/* Connector */}
            <div
              className={`w-8 h-0.5 ${
                frontLandmarks.length > 0 ? 'bg-[#00f3ff]' : 'bg-neutral-800'
              } transition-colors duration-200`}
            />

            {/* Side Step */}
            {sidePhoto && (
              <>
                <div className="flex items-center gap-2">
                  <div
                    className={`
                      w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                      transition-all duration-200
                      ${
                        currentStep === 'side'
                          ? 'bg-[#00f3ff] text-black'
                          : sideLandmarks.length > 0
                            ? 'bg-green-500 text-white'
                            : 'bg-neutral-800 text-neutral-500'
                      }
                    `}
                  >
                    {sideLandmarks.length > 0 && currentStep !== 'side' ? (
                      <Check className="w-4 h-4" />
                    ) : (
                      '2'
                    )}
                  </div>
                  <span
                    className={`text-sm hidden sm:block ${
                      currentStep === 'side' ? 'text-white font-medium' : 'text-neutral-500'
                    }`}
                  >
                    Side
                  </span>
                </div>

                {/* Connector */}
                <div
                  className={`w-8 h-0.5 ${
                    sideLandmarks.length > 0 ? 'bg-[#00f3ff]' : 'bg-neutral-800'
                  } transition-colors duration-200`}
                />
              </>
            )}

            {/* Results Step */}
            <div className="flex items-center gap-2">
              <div
                className={`
                  w-8 h-8 rounded-full flex items-center justify-center text-sm font-semibold
                  transition-all duration-200
                  ${
                    currentStep === 'results'
                      ? 'bg-[#00f3ff] text-black'
                      : 'bg-neutral-800 text-neutral-500'
                  }
                `}
              >
                {sidePhoto ? '3' : '2'}
              </div>
              <span
                className={`text-sm hidden sm:block ${
                  currentStep === 'results' ? 'text-white font-medium' : 'text-neutral-500'
                }`}
              >
                Results
              </span>
            </div>
          </div>

          {/* Current step badge */}
          <div className="px-3 py-1.5 rounded-full bg-neutral-900 border border-neutral-800">
            <span className="text-white text-sm font-medium flex items-center gap-2">
              <config.icon className="w-4 h-4 text-[#00f3ff]" />
              {config.title}
            </span>
          </div>
        </div>
      </div>

      {/* Main Content */}
      {currentStep === 'front' && frontPhoto && (
        <div className="w-full">
          <FrontProfileLandmarks
            imageUrl={frontPhoto.preview}
            onLandmarksChange={setFrontLandmarks}
            onComplete={handleFrontComplete}
          />
        </div>
      )}

      {currentStep === 'side' && sidePhoto && (
        <div className="w-full">
          <SideProfileManualTool
            imageUrl={sidePhoto.preview}
            onLandmarksChange={setSideLandmarks}
            onComplete={handleSideComplete}
          />
        </div>
      )}

      {currentStep === 'results' && (
        <div className="w-full max-w-5xl">
          <ResultsDashboard
            frontLandmarks={frontLandmarks}
            sideLandmarks={sideLandmarks}
            gender={gender || 'male'}
          />
        </div>
      )}
    </main>
  );
}

