                if (!current || !next) return null;

                return (
                  <line
                    key={`line-${landmarkId}`}
                    x1={current.x * 100}
                    y1={current.y * 100}
                    x2={next.x * 100}
                    y2={next.y * 100}
                    stroke={`${currentCategory.color}66`}
