   375→        >
   376→          <RotateCcw className="w-4 h-4" />
   377→          Reset
   378→        </button>
   379→      </motion.div>
   380→
   381→      {/* Detection Error/Status */}
   382→      <AnimatePresence>
   383→        {(detectionError || faceApiError) && (
   384→          <motion.div
   385→            initial={{ opacity: 0, y: -10 }}
   386→            animate={{ opacity: 1, y: 0 }}
   387→            exit={{ opacity: 0, y: -10 }}
   388→            className="mb-4 p-3 rounded-lg bg-red-500/10 border border-red-500/30 text-red-400 text-sm text-center"
   389→          >
   390→            {detectionError || faceApiError?.message}
   391→          </motion.div>
   392→        )}
   393→      </AnimatePresence>
   394→
   395→      {/* Guided Mode Step Indicator */}
   396→      {isGuided && currentCategory && (
   397→        <motion.div
   398→          key={currentStepIndex}
   399→          initial={{ opacity: 0, x: 20 }}
   400→          animate={{ opacity: 1, x: 0 }}
   401→          className="mb-4"
   402→        >
   403→          <div className="flex items-center justify-between mb-2">
   404→            <button
   405→              onClick={prevStep}
   406→              disabled={currentStepIndex === 0}
   407→              className={`p-2 rounded-lg transition-all ${
   408→                currentStepIndex === 0
   409→                  ? 'text-neutral-700 cursor-not-allowed'
   410→                  : 'text-neutral-400 hover:text-white hover:bg-neutral-800'
   411→              }`}
   412→            >
   413→              <ChevronLeft className="w-5 h-5" />
   414→            </button>
   415→
   416→            <div className="text-center">
   417→              <div className="flex items-center justify-center gap-2 mb-1">
   418→                <span
   419→                  className="w-3 h-3 rounded-full"
   420→                  style={{ backgroundColor: currentCategory.color }}
   421→                />
   422→                <span className="text-white font-medium">{currentCategory.name}</span>
   423→              </div>
   424→              <span className="text-xs text-neutral-400">
   425→                Step {currentStepIndex + 1} of {SIDE_LANDMARK_CATEGORIES.length} •{' '}
   426→                {currentCategory.landmarks.length} points
   427→              </span>
   428→            </div>
   429→
   430→            <button
   431→              onClick={nextStep}
   432→              disabled={currentStepIndex === SIDE_LANDMARK_CATEGORIES.length - 1}
   433→              className={`p-2 rounded-lg transition-all ${
   434→                currentStepIndex === SIDE_LANDMARK_CATEGORIES.length - 1
   435→                  ? 'text-neutral-700 cursor-not-allowed'
   436→                  : 'text-neutral-400 hover:text-white hover:bg-neutral-800'
   437→              }`}
   438→            >
   439→              <ChevronRight className="w-5 h-5" />
   440→            </button>
   441→          </div>
   442→
   443→          {/* Progress bar */}
   444→          <div className="w-full h-1 bg-neutral-800 rounded-full overflow-hidden">
   445→            <motion.div
   446→              className="h-full rounded-full"
   447→              style={{ backgroundColor: currentCategory.color }}
   448→              initial={{ width: 0 }}
   449→              animate={{
   450→                width: `${((currentStepIndex + 1) / SIDE_LANDMARK_CATEGORIES.length) * 100}%`,
   451→              }}
   452→              transition={{ duration: 0.3 }}
   453→            />
   454→          </div>
   455→        </motion.div>
   456→      )}
   457→
   458→      {/* Category Filter (Free Edit Mode) */}
   459→      {!isGuided && (
   460→        <motion.div
   461→          initial={{ opacity: 0, y: 10 }}
   462→          animate={{ opacity: 1, y: 0 }}
   463→          className="mb-4 flex flex-wrap gap-2 justify-center"
   464→        >
   465→          <button
   466→            onClick={() => setSelectedCategory(null)}
