   569→
   570→          {/* Landmark Points */}
   571→          {landmarks.map((landmark) => {
   572→            const isVisible = visibleLandmarkIds.has(landmark.id);
   573→            const color = getLandmarkColor(landmark.id, SIDE_LANDMARK_CATEGORIES);
   574→
   575→            return (
