   587→                }}
   588→                onMouseDown={() => isVisible && handleMouseDown(landmark.id)}
   589→                onTouchStart={() => isVisible && handleTouchStart(landmark.id)}
   590→              >
   591→                {/* Outer glow ring */}
   592→                <div
   593→                  className={`
   594→                    absolute inset-0 rounded-full
   595→                    transition-all duration-200
   596→                    ${activeLandmark === landmark.id ? 'scale-150' : ''}
   597→                  `}
   598→                  style={{
   599→                    backgroundColor: `${color}33`,
   600→                    boxShadow:
   601→                      activeLandmark === landmark.id
   602→                        ? `0 0 25px ${color}`
   603→                        : `0 0 10px ${color}`,
   604→                  }}
   605→                />
   606→
   607→                {/* Main handle */}
   608→                <div
   609→                  className={`
   610→                    absolute inset-1 rounded-full border-2 border-white
   611→                    transition-all duration-200
   612→                    ${activeLandmark === landmark.id ? 'scale-125' : ''}
   613→                  `}
   614→                  style={{
   615→                    backgroundColor: color,
   616→                    boxShadow: `0 0 10px ${color}, 0 0 20px ${color}`,
   617→                  }}
   618→                />
   619→
   620→                {/* Label tooltip */}
   621→                <div
   622→                  className={`
   623→                    absolute left-6 top-1/2 -translate-y-1/2 whitespace-nowrap
   624→                    px-2 py-1 rounded bg-black/95 border
   625→                    text-xs font-medium z-50
   626→                    pointer-events-none transition-opacity duration-200
   627→                    ${activeLandmark === landmark.id ? 'opacity-100' : 'opacity-0'}
   628→                  `}
   629→                  style={{
   630→                    borderColor: `${color}80`,
   631→                    color: color,
   632→                  }}
   633→                >
   634→                  <div>{landmark.label}</div>
   635→                  <div className="text-[10px] opacity-70">{landmark.medicalTerm}</div>
   636→                </div>
   637→              </div>
   638→            );
   639→          })}
   640→        </div>
   641→      </motion.div>
   642→
   643→      {/* Landmark Legend for Current Category */}
   644→      <motion.div
   645→        initial={{ opacity: 0, y: 10 }}
   646→        animate={{ opacity: 1, y: 0 }}
   647→        transition={{ delay: 0.2 }}
   648→        className="mt-6 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2"
   649→      >
   650→        {filteredLandmarks.map((landmark) => {
   651→          const color = getLandmarkColor(landmark.id, SIDE_LANDMARK_CATEGORIES);
   652→          return (
   653→            <div
   654→              key={landmark.id}
   655→              className={`
   656→                flex items-center gap-2 px-3 py-2 rounded-lg
   657→                border transition-all duration-200 cursor-pointer
   658→                ${
   659→                  activeLandmark === landmark.id
   660→                    ? 'border-neutral-600 bg-neutral-800'
   661→                    : 'border-neutral-800 bg-black hover:bg-neutral-900'
   662→                }
   663→              `}
   664→              onMouseEnter={() => setActiveLandmark(landmark.id)}
   665→              onMouseLeave={() => setActiveLandmark(null)}
   666→            >
   667→              <div
   668→                className="w-3 h-3 rounded-full flex-shrink-0"
   669→                style={{
   670→                  backgroundColor: color,
   671→                }}
   672→              />
   673→              <div className="min-w-0">
   674→                <p className="text-sm font-medium text-white truncate">
   675→                  {landmark.label}
