     7â†’ * - Metric-specific advice generation
     8â†’ * - Gaussian scoring with exponential decay
     9â†’ */
    10â†’
    11â†’import type { FaceIQScoreResult, QualityTier, SeverityLevel } from './faceiq-scoring';
    12â†’
    13â†’// =============================================================================
    14â†’// HARMONY SCORE WEIGHTS
    15â†’// =============================================================================
    16â†’
    17â†’/**
    18â†’ * High Impact Metrics (3.0x weight)
    19â†’ * Core facial harmony indicators that have the greatest effect on overall attractiveness
    20â†’ */
    21â†’export const HIGH_IMPACT_METRICS = new Set([
    22â†’  'faceWidthToHeight',
    23â†’  'midfaceRatio',
    24â†’  'lateralCanthalTilt',
    25â†’  'gonialAngle',
    26â†’  'mandibularPlaneAngle',
    27â†’  'chinToPhiltrumRatio',
    28â†’]);
    29â†’
    30â†’/**
    31â†’ * Medium Impact Metrics (2.0x weight)
    32â†’ * Secondary aesthetic factors
    33â†’ */
    34â†’export const MEDIUM_IMPACT_METRICS = new Set([
    35â†’  'noseBridgeToNoseWidth',
    36â†’  'nasolabialAngle',
    37â†’  'cheekboneHeight',
    38â†’  'ramusToMandibleRatio',
    39â†’]);
    40â†’
    41â†’/**
    42â†’ * Get the weight multiplier for a metric
    43â†’ */
    44â†’export function getMetricWeight(metricId: string): number {
    45â†’  if (HIGH_IMPACT_METRICS.has(metricId)) return 3.0;
    46â†’  if (MEDIUM_IMPACT_METRICS.has(metricId)) return 2.0;
    47â†’  return 1.0;
    48â†’}
    49â†’
    50â†’/**
    51â†’ * Get weight tier name for display
    52â†’ */
    53â†’export function getWeightTier(metricId: string): 'high' | 'medium' | 'standard' {
    54â†’  if (HIGH_IMPACT_METRICS.has(metricId)) return 'high';
    55â†’  if (MEDIUM_IMPACT_METRICS.has(metricId)) return 'medium';
    56â†’  return 'standard';
    57â†’}
