    59â†’// =============================================================================
    60â†’// WEIGHTED HARMONY SCORE CALCULATION
    61â†’// =============================================================================
    62â†’
    63â†’export interface HarmonyScoreResult {
    64â†’  /** Weighted harmony percentage (0-100%) */
    65â†’  harmonyPercentage: number;
    66â†’  /** Weighted average score (0-10 scale) */
    67â†’  weightedAverage: number;
    68â†’  /** Unweighted average for comparison */
    69â†’  unweightedAverage: number;
    70â†’  /** Breakdown by weight tier */
    71â†’  weightDistribution: {
    72â†’    highImpact: { count: number; avgScore: number };
    73â†’    mediumImpact: { count: number; avgScore: number };
    74â†’    standard: { count: number; avgScore: number };
    75â†’  };
    76â†’}
    77â†’
    78â†’/**
    79â†’ * Calculate the weighted Harmony Score from scored measurements.
    80â†’ * Uses 3-tier weighting: High (3x), Medium (2x), Standard (1x)
    81â†’ *
    82â†’ * Formula: Î£(Score Ã— Weight) / Î£(Weights) â†’ percentage
    83â†’ */
    84â†’export function calculateHarmonyScore(
    85â†’  measurements: Array<{ metricId: string; standardizedScore: number }>
    86â†’): HarmonyScoreResult {
    87â†’  let totalWeightedScore = 0;
    88â†’  let totalWeight = 0;
    89â†’  let unweightedTotal = 0;
    90â†’
    91â†’  const tiers = {
    92â†’    high: { total: 0, count: 0 },
    93â†’    medium: { total: 0, count: 0 },
    94â†’    standard: { total: 0, count: 0 },
    95â†’  };
    96â†’
    97â†’  for (const m of measurements) {
    98â†’    const weight = getMetricWeight(m.metricId);
    99â†’    const tier = getWeightTier(m.metricId);
   100â†’
   101â†’    totalWeightedScore += m.standardizedScore * weight;
   102â†’    totalWeight += weight;
   103â†’    unweightedTotal += m.standardizedScore;
   104â†’
   105â†’    tiers[tier].total += m.standardizedScore;
   106â†’    tiers[tier].count++;
   107â†’  }
   108â†’
   109â†’  const weightedAverage = totalWeight > 0 ? totalWeightedScore / totalWeight : 0;
   110â†’  const unweightedAverage = measurements.length > 0 ? unweightedTotal / measurements.length : 0;
   111â†’  const harmonyPercentage = (weightedAverage / 10) * 100;
   112â†’
   113â†’  return {
   114â†’    harmonyPercentage,
   115â†’    weightedAverage,
   116â†’    unweightedAverage,
   117â†’    weightDistribution: {
   118â†’      highImpact: {
   119â†’        count: tiers.high.count,
   120â†’        avgScore: tiers.high.count > 0 ? tiers.high.total / tiers.high.count : 0,
   121â†’      },
   122â†’      mediumImpact: {
   123â†’        count: tiers.medium.count,
   124â†’        avgScore: tiers.medium.count > 0 ? tiers.medium.total / tiers.medium.count : 0,
   125â†’      },
   126â†’      standard: {
   127â†’        count: tiers.standard.count,
   128â†’        avgScore: tiers.standard.count > 0 ? tiers.standard.total / tiers.standard.count : 0,
   129â†’      },
   130â†’    },
   131â†’  };
   132â†’}
