   353â†’/**
   354â†’ * Generate personalized advice based on metric score and value position.
   355â†’ *
   356â†’ * @param metricId - The metric identifier
   357â†’ * @param score - Score from 0-10
   358â†’ * @param value - Actual measured value
   359â†’ * @param idealMin - Minimum of ideal range
   360â†’ * @param idealMax - Maximum of ideal range
   361â†’ * @returns Advice string for the metric
   362â†’ */
   363â†’export function generateAdvice(
   364â†’  metricId: string,
   365â†’  score: number,
   366â†’  value: number,
   367â†’  idealMin: number,
   368â†’  idealMax: number
   369â†’): string {
   370â†’  // Perfect score - no action needed
   371â†’  if (score >= 9.0) {
   372â†’    return "Ideal proportions. No action needed.";
   373â†’  }
   374â†’
   375â†’  // Get advice from database
   376â†’  const advice = ADVICE_DATABASE[metricId];
   377â†’  if (advice) {
   378â†’    if (value > idealMax) {
   379â†’      return advice.tooHigh;
   380â†’    } else if (value < idealMin) {
   381â†’      return advice.tooLow;
   382â†’    }
   383â†’  }
   384â†’
   385â†’  // Default fallback
   386â†’  if (value > idealMax) {
   387â†’    return `Value (${value.toFixed(2)}) is above the ideal range of ${idealMin.toFixed(1)}-${idealMax.toFixed(1)}.`;
   388â†’  } else if (value < idealMin) {
   389â†’    return `Value (${value.toFixed(2)}) is below the ideal range of ${idealMin.toFixed(1)}-${idealMax.toFixed(1)}.`;
   390â†’  } else {
   391â†’    return "Value is within acceptable range but not optimal.";
   392â†’  }
   393â†’}
   394â†’
   395â†’/**
