   408â†’// =============================================================================
   409â†’// GAUSSIAN SCORING (matches Python implementation)
   410â†’// =============================================================================
   411â†’
   412â†’/**
   413â†’ * Calculate a score from 0-10 using Gaussian decay.
   414â†’ * Matches the Python looksmax_engine.py implementation.
   415â†’ *
   416â†’ * @param value - The measured value
   417â†’ * @param minRange - Minimum of ideal range
   418â†’ * @param maxRange - Maximum of ideal range
   419â†’ * @returns Score from 0-10
   420â†’ */
   421â†’export function calculateGaussianScore(
   422â†’  value: number,
   423â†’  minRange: number,
   424â†’  maxRange: number
   425â†’): number {
   426â†’  // Within ideal range = perfect score
   427â†’  if (value >= minRange && value <= maxRange) {
   428â†’    return 10.0;
   429â†’  }
   430â†’
   431â†’  let deviation: number;
   432â†’  let target: number;
   433â†’
   434â†’  if (value < minRange) {
   435â†’    deviation = minRange - value;
   436â†’    target = minRange;
   437â†’  } else {
   438â†’    deviation = value - maxRange;
   439â†’    target = maxRange;
   440â†’  }
   441â†’
   442â†’  // Prevent division by zero
   443â†’  if (target === 0) target = 1.0;
   444â†’
   445â†’  // Sigma is 10% of target value
   446â†’  let sigma = Math.abs(target) * 0.1;
   447â†’  if (sigma === 0) sigma = 1.0;
   448â†’
   449â†’  // Gaussian decay
   450â†’  const score = 10.0 * Math.exp(-0.5 * Math.pow(deviation / sigma, 2));
   451â†’  return Math.max(0.0, Math.min(10.0, score));
   452â†’}
