   475â†’  count: number = 3
   476â†’): RankedMetric[] {
   477â†’  return [...measurements]
   478â†’    .sort((a, b) => b.standardizedScore - a.standardizedScore)
   479â†’    .slice(0, count)
   480â†’    .map((m) => ({
   481â†’      metricId: m.metricId,
   482â†’      name: m.name,
   483â†’      value: m.value,
   484â†’      score: m.standardizedScore,
   485â†’      idealMin: m.idealMin,
   486â†’      idealMax: m.idealMax,
   487â†’      advice: generateAdviceFromResult(m),
   488â†’      weightTier: getWeightTier(m.metricId),
   489â†’      category: m.category,
   490â†’    }));
   491â†’}
