export function getQualityTier(score: number): QualityTier {
  const normalized = (score / 10) * 100;
  if (normalized >= 90) return 'ideal';
  if (normalized >= 70) return 'excellent';
  if (normalized >= 50) return 'good';
  return 'below_average';
