   285→  }
   286→
   287→  /**
   288→   * Check if a single threshold condition is met
   289→   */
   290→  private checkThreshold(userValue: number, operator: '<' | '>', thresholdValue: number): boolean {
   291→    if (operator === '<') {
   292→      return userValue < thresholdValue;
   293→    }
   294→    return userValue > thresholdValue;
   295→  }
   296→
   297→  /**
   298→   * Evaluate trigger rules against user metrics
   299→   */
   300→  private evaluateTriggerRules(
   301→    triggerRules: TriggerRules,
   302→    metricsDict: Record<string, number>
   303→  ): { isTriggered: boolean; triggeredMetrics: TriggerResult[] } {
   304→    const { metrics, thresholds, condition } = triggerRules;
   305→    const triggeredMetrics: TriggerResult[] = [];
   306→    const results: boolean[] = [];
   307→
   308→    for (const metricName of metrics) {
   309→      if (!(metricName in metricsDict)) continue;
   310→      if (!(metricName in thresholds)) continue;
   311→
   312→      const userValue = metricsDict[metricName];
   313→      const { operator, value: thresholdValue } = thresholds[metricName];
   314→      const isMet = this.checkThreshold(userValue, operator, thresholdValue);
   315→
   316→      if (isMet) {
   317→        triggeredMetrics.push({
   318→          metric: metricName,
   319→          value: userValue,
   320→          threshold: thresholdValue,
   321→          operator
   322→        });
   323→      }
   324→
   325→      results.push(isMet);
   326→    }
   327→
   328→    const isTriggered = condition === 'AND'
   329→      ? results.length > 0 && results.every(r => r)
   330→      : results.some(r => r);
   331→
   332→    return { isTriggered, triggeredMetrics };
   333→  }
   334→
   335→  /**
   336→   * Get all recommendations that match the user's metrics
   337→   *
   338→   * @param metricsDict - Raw metric values
   339→   * @param severityDict - Severity classifications (ideal/good/moderate/severe) for each metric
   340→   *                       If a metric is 'ideal', it will NOT trigger remediation plans
   341→   */
   342→  getRecommendations(
   343→    metricsDict: Record<string, number>,
   344→    severityDict?: Record<string, string>
   345→  ): Plan[] {
   346→    const recommendations: Plan[] = [];
   347→
   348→    for (const rawPlan of this.plans) {
   349→      const { isTriggered, triggeredMetrics } = this.evaluateTriggerRules(
   350→        rawPlan.trigger_rules,
   351→        metricsDict
   352→      );
   353→
   354→      // If severity data is provided, filter out metrics that are already ideal
   355→      if (isTriggered && severityDict) {
   356→        // Check if ALL triggered metrics are outside ideal range
   357→        const hasActualFlaw = triggeredMetrics.some(tm => {
   358→          const severity = severityDict[tm.metric];
   359→          // Only trigger if severity is NOT ideal (i.e., it's good/moderate/severe)
   360→          return severity && severity !== 'ideal';
   361→        });
   362→
   363→        // Skip this plan if all triggered metrics are already ideal
   364→        if (!hasActualFlaw) {
   365→          continue;
   366→        }
   367→      }
   368→
   369→      if (isTriggered) {
   370→        recommendations.push({
   371→          id: rawPlan.id,
   372→          title: rawPlan.title,
   373→          content: {
   374→            description: rawPlan.content.description,
   375→            cost_min: rawPlan.content.cost_min,
   376→            cost_max: rawPlan.content.cost_max,
   377→            time_min: rawPlan.content.time_min || 'Unknown',
   378→            time_max: rawPlan.content.time_max || 'Unknown',
   379→            risks: rawPlan.content.risks || 'Consult a professional.',
   380→            citations: rawPlan.content.citations || [],
   381→            tags: rawPlan.content.tags
   382→          },
   383→          trigger_reason: triggeredMetrics
   384→        });
   385→      }
   386→    }
   387→
   388→    return recommendations;
   389→  }
   390→
   391→  /**
   392→   * Get recommendations sorted by cost (cheapest first)
   393→   */
   394→  getRecommendationsByCost(
   395→    metricsDict: Record<string, number>,
   396→    severityDict?: Record<string, string>
   397→  ): Plan[] {
   398→    return this.getRecommendations(metricsDict, severityDict).sort(
   399→      (a, b) => a.content.cost_min - b.content.cost_min
   400→    );
   401→  }
   402→
   403→  /**
   404→   * Get recommendations grouped by phase/tag
   405→   */
   406→  getRecommendationsByPhase(
   407→    metricsDict: Record<string, number>,
   408→    severityDict?: Record<string, string>
   409→  ): Record<string, Plan[]> {
   410→    const recommendations = this.getRecommendations(metricsDict, severityDict);
   411→    const grouped: Record<string, Plan[]> = {
   412→      'Foundational': [],
   413→      'Minimally Invasive': [],
   414→      'Surgical': []
   415→    };
   416→
   417→    for (const rec of recommendations) {
   418→      const tags = rec.content.tags || [];
   419→      if (tags.includes('Foundational')) {
   420→        grouped['Foundational'].push(rec);
   421→      } else if (tags.includes('Surgical')) {
   422→        grouped['Surgical'].push(rec);
   423→      } else if (tags.includes('Minimally Invasive')) {
   424→        grouped['Minimally Invasive'].push(rec);
   425→      } else {
   426→        grouped['Minimally Invasive'].push(rec); // Default
   427→      }
   428→    }
   429→
   430→    return grouped;
   431→  }
   432→}
   433→
   434→// ============================================
   435→// SINGLETON INSTANCE
   436→// ============================================
   437→
   438→export const adviceEngine = new AdviceEngine();
   439→

